// Cookie Collective rulz


#define ITER 64.
#define time (iTime*0.3)
#define PI 3.141592


vec2 moda (vec2 p, float per)
{
    float a = atan(p.y, p.x);
    float l = length(p);
    a = mod(a-per/2., per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

mat2 rot (float a)
{return mat2(cos(a), sin(a), -sin(a), cos(a));}

float random (vec2 st)
{return fract(sin(dot(st.xy, vec2(12.2544, 35.1571)))*5418.548416);}

float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5 *(u+a+abs(mod(u-a+st, 2.*st)-st)));
}

float sdTriPrism( vec3 p, vec2 h )
{
    p.xz *= rot(-iTime);
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.866025+p.y*0.5,-p.y)-h.x*0.5);
}

float sdHexPrism( vec3 p, vec2 h )
{
    const vec3 k = vec3(-0.8660254, 0.5, 0.57735);
    p = abs(p);
    p.xy -= 2.0*min(dot(k.xy, p.xy), 0.0)*k.xy;
    vec2 d = vec2(
       length(p.xy-vec2(clamp(p.x,-k.z*h.x,k.z*h.x), h.x))*sign(p.y-h.x),
       p.z-h.y );
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float column (vec3 p)
{
    float width = 5.;
    float c1 = length(p.xz)-width;

    p.xz *= rot(p.y*0.2);
    p.xz *= rot(iTime);
    p.xz = moda(p.xz, PI);
    p.x -= width;
    float c2 = length(p.xz)-2.;

    return min(c1, c2);
}

float g2 = 0.;
float columns (vec3 p)
{
	p.xz = moda(p.xz, PI/3.);
    p.x -= 60.;
    float d = column(p);
    g2 += 0.1/(0.1+d*d);
    return d;
}

float room (vec3 p)
{
    float g = abs(p.y+5.)-0.5;
    float h = sdHexPrism(p.xzy, vec2(60.));
    return min (g,-h);
}

float pedestal (vec3 p)
{
    vec3 new_p = vec3(p.x, p.z, p.y+4.);
    float width = 15.;
    float height = 0.2;
    float st = 2.;

    float hexs = sdHexPrism(new_p, vec2(width, height));

    for (float i = 0.; i<2.; i++)
    {
        new_p.z -= 0.7;
        width -= 4.+i;
        height += 0.1+i;
        hexs = stmin(hexs, sdHexPrism(new_p, vec2(width, height)), 1., st);
        st++;
    } 
    return hexs;
}

float g1 = 0.;
float triforce (vec3 p)
{
    p.y -= 7.;
    float tri1 = sdTriPrism(p, vec2(3,0.3));
    float tri2 = sdTriPrism(vec3(p.x-2.5, p.y+4.5, p.z), vec2(3,0.3));
    float tri3 = sdTriPrism(vec3(p.x+2.5, p.y+4.5, p.z), vec2(3,0.3));
    float d = min(tri3,min(tri1, tri2));
    g1 += 0.1/(0.1+d*d);
    return d;
}

float SDF (vec3 p)
{
    float r = stmin(room(p), columns(p), 10., 3.);
    float hs = pedestal(p);
    return min(triforce(p),stmin(hs, r, 0.5, 2.));
}

vec3 get_normals (vec3 p)
{
    vec2 eps = vec2(0.01, 0.);
    return normalize(vec3 (SDF(p+eps.xyy)-SDF(p-eps.xyy),
                          SDF(p+eps.yxy)-SDF(p-eps.yxy),
                          SDF(p+eps.yyx)-SDF(p-eps.yyx)
                          )
                    );
}

float lighting (vec3 n, vec3 l)
{return dot(n, normalize(l))*0.5+0.5;}

vec3 point_light(vec3 p, vec3 n)
{
    vec3 lpos = vec3(0, 8, 0);
    vec3 ldir = normalize(lpos - p);
    float ldist = length(lpos - p);
    float dotNL = dot(n, ldir)*0.5+0.5;
	return (vec3(.8, .8, 0) * dotNL) / (.007 * ldist * ldist);
}

vec3 get_cam (vec3 ro, vec3 target, vec2 uv, float fov)
{
    vec3 forward = normalize(target - ro);
    vec3 left = normalize(cross(vec3(0.,1.,0.), forward));
    vec3 up = normalize(cross (forward, left));
    return normalize(forward*fov+ left*uv.x + up*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    //pixellate
    uv = floor(uv * 100.)/100.;

    vec3 ro = vec3(-18.*cos(time),5., -18.*sin(time)); vec3 p = ro; 
    vec3 target = vec3(0.);
    vec3 rd = get_cam(ro, target, uv, 1.);
    vec3 col = vec3(0.);

    float shad = 0.;
    float dither = random(uv);

    for (float i = 0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.01)
        {
            vec3 n = get_normals(p);

            // hemi light
            col = mix(vec3(0.5, 0.1, 0.5), vec3(0.3, 0.8, 0.9),lighting(n, vec3(1., 10.,-2.)));

            // point light
            col += point_light(p, n);

            // ao
            shad = i/ITER;
            col *= (1.- shad);
            break;
        }
        d *= 0.9 + dither*0.2;
        p += d*rd;
    }

    float t = length(p-ro);
	col += g1*vec3(1.,1.,0.)*0.8;
    col += g2*0.1;
    col = mix(col, vec3(0.5, 0.8, 0.9), (1.-exp(-0.002*t*t))*0.8);

    // posterize
    col = floor(col*10.)/10.;

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265359

#define SEGMENTS 8.0

#define RINGS 5.0

#define SPEED -0.3

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 delta = (fragCoord -.5 * iResolution.xy) / iResolution.y;
    float dist = length(delta),
    angle = atan(delta.x, delta.y);
    float x = dist * RINGS;
    float y = angle * (SEGMENTS / 8.0);
    float t = iTime * SPEED;
    fragColor = vec4(
          mod(x + mod(y + t, PI / 4.0) + y + mod(x + t, PI / 4.0), PI) / PI,
          0,
          mod(x + mod(y + t, PI / 2.0) + y + mod(x + t, PI / 2.0), PI) / PI,
        1.0
    );
}

#define t mod(iTime, 100.0)

struct Ray { vec3 o, d; };

float rectify(float f, float b) { return b+f*b*.5; }
mat2 rot(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float vmax(vec3 v) { return max(max(v.x,v.y),v.z); }
float sphere(vec3 p, float r) {return length(p)-r;}
float cube(vec3 p, vec3 s, float sm) {
  vec3 b=abs(p)-s;
  return length(max(b,0.0) + min(vmax(b), 0.0)) - sm;
}
vec3 rep(vec3 p, float n) { return mod(p-vec3(n*.5), n) - .5*n; }
float scene(vec3 p) {
  p = rep(p,3.0+sin(t*.42));
  p.xy = abs(p.xy);
  p.xz *= rot(t*.6+p.y);
  p.xy *= rot(t*.3);
  float s=sphere(p,.2);
  float c=cube(p, vec3(.5), .5);
  return max(-s,c);
}

Ray camera(vec2 uv, vec3 o, vec3 tg, float z) {
  Ray r;
  r.o = o;
  vec3 f = normalize(tg-o);
  vec3 s = normalize(cross(vec3(0,1,0), f));
  vec3 u = cross(f,s);
  r.d = normalize(f*z+uv.x*s+uv.y*u);
  return r;
}

vec3 normal(vec3 p) {
  vec2 e=vec2(-0.001, 0.001);
  return normalize(e.xyy*scene(p+e.xyy) + e.yxy*scene(p+e.yxy) + e.yyx*scene(p+e.yyx) + e.xxx*scene(p+e.xxx));
}

vec3 shade(vec3 p) {
  vec3 l=normalize(vec3(sin(t*.9), 2, cos(t)));
  vec3 n=normal(p);
  return vec3(max(0.0,dot(l, n)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  uv /= 1.0-length(uv)*1.5;
  //uv.x = abs(uv.x);
  vec3 eye = vec3(1,3,-2);
  eye.xz += 4.0*vec2(sin(t*.2),cos(t*.2));
  vec3 target = vec3(0);
  target.xy += 2.0*vec2(cos(t*.2),sin(t*.2));

  Ray r = camera(uv, eye, target, .2+.1*sin(t*2.0));
  vec3 col = vec3(0);

  // lets try nusan's transparency trick
  vec3 p = r.o;
  float d=0.0;
  float acc=.27;
  int bbb=0;
  float side=sign(scene(p));
  for(int i=0; i<200; i++) {
    float h=scene(p)*side*.92;
    if(h<0.00001) {
      bbb++;
      vec3 n=normal(p)*side;
      col += 1.*shade(p)+acc*vec3(-.1,0.1,.2+.1*cos(t));
      side=-side;
      acc *= .84;
      h = 0.001;
      r.d = refract(r.d, n, 1.0+.025*side);
    }
    p+=r.d*h;
    d+=h;
  }
  col /= float(bbb);
  fragColor = vec4(pow(col, vec3(1.1/2.2)), 1);
}

#define PI acos(-1.)
#define TWO_PI PI*2.

mat2 r2(float a){
    float s =sin(a);
    float c = cos(a);
    return mat2(s,c,-c,s);
}

vec2 rnd(vec2 p){
    vec3 a = fract(p.xyx*vec3(123.12, 256.45, 359.88));
    a += dot(a, a+47.88);
    return fract(vec2(a.x*a.y, a.z*a.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x/iResolution.y;
    float t =iTime;
	uv *= 1.+dot(uv,uv)*.0911275;

    uv *= 4.+sin(t/2.);
    uv+=vec2(sin(t),cos(t));
    uv*=r2(sin(t/3.)*.5)*1.12;

    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);
    float minDist = 100.;
    vec2 cid = vec2(0);
    for(int y = -1; y<1; y++){
        for(int x =-1; x<1; x++){
            vec2 offset = vec2(x, y);
            vec2 rand = rnd(id + offset);

            vec2 point = offset + sin(rand*iTime)*.5+.5;

            point-=gv;
            point*=r2(cid.x-t);
            float d = length(point);
            float md = abs(point.x)+abs(point.y);

            float dst = mix(d,md,sin(rnd(cid).x+rnd(cid).y));

            if(d<minDist){
                minDist = d;
                cid = id+offset;
            }
        }
    }

    vec3 col = vec3(0); 

    float c = length(minDist)-.3;

    float grd = step(.46,gv.x)+step(.46,gv.y);   
    fragColor =  vec4(smoothstep(0.05,0.03, c*sin(3.*t-c*22.+sin(t-rnd(cid).x*12.))));   // vec4(minDist);
    if(iMouse.z>0.0){
        fragColor +=vec4(cos(rnd(cid)*4.).xyxy);
        fragColor.r += grd;
    }
}

#define pi acos(-1.)
#define tau (2.*pi)

vec2 rotate(vec2 a, float b)
{
    float c = cos(b);
    float s = sin(b);
    return vec2(
        a.x*c - a.y*s,
        a.x*s + a.y*c
    );
}

float sdbox2d(vec2 p, float r)
{
    p=abs(p);
    return max(p.x,p.y)-r;
}

vec2 boffset(vec2 p, float t)
{
    t*=pi*2.;
    return rotate(p+vec2(
        cos(t)*2.,
        -sin(t*3.)
    )*.15, sin(t)*(pi*2./3.));
}

float tick(float t)
{
    t = smoothstep(0.,1.,t);
    t = smoothstep(0.,1.,t);
    t = smoothstep(0.,1.,t);
    t = smoothstep(0.,1.,t);
    return t;
}

float pattern(float t)
{
    t=fract(t);
    return clamp(abs(t-.5)*-16.+7.5,-1.,1.)+1.;
    return tick(abs(t-.5)*2.);
}

float scene2(vec2 p, float angle)
{
    float time = ((angle/tau)/5.)*4.+iTime*.25;

    float q = iTime+angle;

    float r = .02 + pattern((angle/tau)*30.)*.02;

    float a = sdbox2d(boffset(p,time),r);
    float b = sdbox2d(boffset(p,time+1./5.),r);
    float c = sdbox2d(boffset(p,time+2./5.),r);
    float d = sdbox2d(boffset(p,time+3./5.),r);
    float e = sdbox2d(boffset(p,time+4./5.),r);
    return min(min(min(a,b),min(c,d)),e);
}

float scene(vec3 p)
{
    p.xz = mod(p.xz+2.,4.)-2.;

    float angle = atan(p.x,p.z);

    float q = .75;

    p.y += (angle/tau)*(q+q);

    p.y = mod(p.y+q,(q+q))-q;

    vec2 a = vec2(length(p.xz)-1., p.y);

    return scene2(a, angle);
}

void mainImage(out vec4 out_color, vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x *= iResolution.x/iResolution.y;

    uv *= 1.+length(uv)*.3;

    uv = abs(uv);
    uv=vec2(max(uv.x,uv.y),min(uv.x,uv.y)).yx;
    uv *= 2.5;

    vec3 cam = vec3(0,0,-5);
    vec3 dir = normalize(vec3(uv, 2.5));

    cam.yz = rotate(cam.yz, pi/5.);
    dir.yz = rotate(dir.yz, pi/5.);

    cam.xz = rotate(cam.xz, pi/4.);
    dir.xz = rotate(dir.xz, pi/4.);

    cam.y += iTime;

    float t =0.;
    float k = 0.;
    int iter=0;
    for(int i=0;i<100;++i)
    {
        k = scene(cam+dir*t)*.7;
        t+=k;
        iter=i;
        if (k < .001)break;
    }
    vec3 h = cam+dir*t;
    vec2 o = vec2(.002,0);
    vec3 n = normalize(vec3(
        scene(h+o.xyy)-scene(h-o.xyy),
        scene(h+o.yxy)-scene(h-o.yxy),
        scene(h+o.yyx)-scene(h-o.yyx)
    ));

    if (k < .001)
    {
        float iterFog = 1.-float(iter)/100.;
        iterFog = pow(iterFog, 3.);
        float light = max(n.y,0.);
        out_color.rgb += mix(vec3(.01,.01,.1), vec3(.1,.5,.5), iterFog);
        out_color.rgb += mix(vec3(0.), vec3(sin(iTime),sin(iTime+2.),sin(iTime+4.))+1., light*iterFog);
    }
    else
    {
        out_color *= 0.;
    }

    out_color.rgb = vec3(pow(length(out_color.rgb)/sqrt(3.),2.));
}

// Based on this tutorial: https://www.youtube.com/watch?v=PGtv-dBi2wE
// Soft shadow implementation based on: http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
// 3D simplex noise from: https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83 (by Ian McEwan)
// Arbitraty axis rotation from: http://www.neilmendoza.com/glsl-rotation-about-an-arbitrary-axis/ (blarg)


/////// -- simplex noise start


vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
    const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
    const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

    vec3 i  = floor(v + dot(v, C.yyy) );
    vec3 x0 =   v - i + dot(i, C.xxx) ;

    vec3 g = step(x0.yzx, x0.xyz);
    vec3 l = 1.0 - g;
    vec3 i1 = min( g.xyz, l.zxy );
    vec3 i2 = max( g.xyz, l.zxy );

    vec3 x1 = x0 - i1 + 1.0 * C.xxx;
    vec3 x2 = x0 - i2 + 2.0 * C.xxx;
    vec3 x3 = x0 - 1. + 3.0 * C.xxx;

    i = mod(i, 289.0 ); 
    vec4 p = permute( permute( permute( 
               i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
             + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
             + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

    float n_ = 1.0/7.0; // N=7
    vec3  ns = n_ * D.wyz - D.xzx;

    vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

    vec4 x_ = floor(j * ns.z);
    vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

    vec4 x = x_ *ns.x + ns.yyyy;
    vec4 y = y_ *ns.x + ns.yyyy;
    vec4 h = 1.0 - abs(x) - abs(y);

    vec4 b0 = vec4( x.xy, y.xy );
    vec4 b1 = vec4( x.zw, y.zw );

    vec4 s0 = floor(b0)*2.0 + 1.0;
    vec4 s1 = floor(b1)*2.0 + 1.0;
    vec4 sh = -step(h, vec4(0.0));

    vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
    vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

    vec3 p0 = vec3(a0.xy,h.x);
    vec3 p1 = vec3(a0.zw,h.y);
    vec3 p2 = vec3(a1.xy,h.z);
    vec3 p3 = vec3(a1.zw,h.w);

    vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
    p0 *= norm.x;
    p1 *= norm.y;
    p2 *= norm.z;
    p3 *= norm.w;

    vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
    m = m * m;
    return (42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                   dot(p2,x2), dot(p3,x3) ) )) * 0.5 + 0.5;
}


////// -- simplex noise end / abritrary rotation axis start


mat3 rotAxis(vec3 axis, float a) {
    float s=sin(a);
    float c=cos(a);
    float oc=1.0-c;
    vec3 as=axis*s;
    mat3 p=mat3(axis.x*axis,axis.y*axis,axis.z*axis);
    mat3 q=mat3(c,-as.z,as.y,as.z,c,-as.x,-as.y,as.x,c);
    return p*oc+q;
}


////// -- abritrary rotation axis end / ray<->sphere intersect start


vec2 raySphere(vec3 r0, vec3 rd, vec3 s0, float sr) {
    float a = dot(rd, rd);
    vec3 s0_r0 = r0 - s0;
    float b = 2.0 * dot(rd, s0_r0);
    float c = dot(s0_r0, s0_r0) - (sr * sr);
    float test = b*b - 4.0*a*c;
    if (test < 0.0) {
        return vec2(-1.0, -1.0);
    }
    test = sqrt(test);
    float X = (-b - test)/(2.0*a);
    float Y = (-b + test)/(2.0*a);
    return vec2(
        min(X, Y),
        max(X, Y)
    );
}


////// -- ray<->sphere intersect end / main start



// Planetoid terrain functions
float planetHeightHD(vec3 n, vec3 pseed) {
	return 
        snoise(n*2.5 + pseed * 1234.1) * 0.5 +
        snoise(n*2.5*2. + pseed * 1234.1) * 0.25 +
        snoise(n*2.5*4. + pseed * 1234.1) * 0.125 +
        snoise(n*2.5*8. + pseed * 1234.1) * 0.125*0.5 +
        snoise(n*2.5*16. + pseed * 1234.1) * 0.125*0.25;
}

float planetHeightHDS(vec3 n, vec3 pseed) {
	return 
        snoise(n*1.5 + pseed * 1234.1) * 0.85,
        snoise(n*2.5 + pseed * 1234.1) * 0.15;
}

vec3 clrPlanetHD(vec3 p, vec3 pc, vec3 pseed, mat3 rot) {
    vec3 n = normalize(rot * (p - pc));
    float ph = planetHeightHD(n, pseed);
    float t = pow(ph, 1.5);
    float t2 = pow((1. - ph) * 1.5, 2.0);
    return mix(
        mix(
        	vec3(0.3, 0.3, 0.3),
            vec3(1., 1., 1.),
            t2
        ),
    	vec3(1., 1., .7) * (0.25 + pow(snoise(n*2.5*1. + pseed * 11234.1), 0.2) * 0.75),
        t
   	);
}

float distPlanetHD(vec3 p, vec3 pc, float pr, vec3 pseed, float hd, mat3 rot) {

    // Start with sphere
    float d0 = length(p - pc);
    // Offset by layered simplex noise
    float ph = planetHeightHD(normalize(rot * (p - pc)), pseed);
    float phs = planetHeightHDS(normalize(rot * (p - pc)), pseed);
    float r = pr - pr * hd * mix(ph, phs * 3.5, pow(ph, 1.5));

    return d0 - r;

}

// Satellite terrain functions
float planetHeight(vec3 n, vec3 pseed) {
	return 
        snoise(n*0.5 + pseed * 1234.1) +
        snoise(n*0.5*4. + pseed * 1234.1) * 0.25 +
        snoise(n*0.5*16. + pseed * 1234.1) * 0.125 * 0.5;
}

float distPlanet(vec3 p, vec3 pc, float pr, vec3 pseed, float hd, mat3 rot) {

    float d0 = length(p - pc);
    float r = pr + pr * hd * planetHeight(normalize(rot * (p - pc)), pseed);

    return d0 - r;    

}

// Scene function
float getDist(vec3 p) {

    // Planetoid self rotation
    mat3 p1r = rotAxis(normalize(vec3(.5, -.2, 1.)), iTime * 0.1);
    // Orbital rotation
    mat3 p1r2 = rotAxis(normalize(vec3(-1, -.1, 0.05)), -iTime * 0.2);
    // Satellite self rotation
    mat3 p2r = rotAxis(normalize(vec3(.1, .5, -1.)), -iTime * 0.5);

    return min(
	    distPlanetHD(p, vec3(0., 0., 0.), 5., vec3(.4315, .3415, .141561), 0.1, p1r),
        distPlanet(p, p1r2 * vec3(1., 3., 7.) * 1., 0.5, vec3(.1315, .7615, .5341561), 1., p2r)
    );

}

vec3 getClr(vec3 p) {
    vec3 clr = vec3(0.75, 0.75, 0.75);
    mat3 p1r = rotAxis(normalize(vec3(.5, -.2, 1.)), iTime * 0.1);
    float dp = distPlanetHD(p, vec3(0., 0., 0.), 5., vec3(.4315, .3415, .141561), 0.1, p1r);
    if (dp < (1e-3)) {
        return clrPlanetHD(p, vec3(0., 0., 0.), vec3(.4315, .3415, .141561), p1r);
    }
    return clr;
}

// Raymarching implementation
#define MAX_ITERATIONS 256
#define FAR_CLIP 60.
#define MIN_DIST (1e-3)

float rayMarch(vec3 r0, vec3 rd) {
    float ds = 0.;
    for (int i=0; i<MAX_ITERATIONS; i++) {
        vec3 p = r0 + rd * ds;
        float dist = getDist(p);
        ds += dist;
        if (dist < MIN_DIST) {
            return ds;
        }
        if (ds >= FAR_CLIP) {
            break;
        }
    }
    return FAR_CLIP;
}

// Lighting functions
#define LIGHT vec3(0., 15., -1.)
#define SHADOW_STRENGTH 0.8
#define AMBIENT_LIGHT 0.01
#define SURFACE_DIST 0.02
#define NORMAL_SAMPLE_DIST (1e-3)
#define SHADOW_ITERATIONS 32
#define SHADOW_MIN_DIST (1e-4)
#define SHADOW_SHARPNESS 2.25

float shadowRay(vec3 r0, vec3 rd, float maxDist) {
    float ds = 0.;
    float ret = 1.;
    for (int i=0; i<SHADOW_ITERATIONS && ds < maxDist; i++) {
        vec3 p = r0 + rd * ds;
        float dist = getDist(p);
        ds += dist;
        if (dist < SHADOW_MIN_DIST) {
            return 0.0;
        }
        ret = min(ret, SHADOW_STRENGTH * dist/ds);
    }
    return pow(ret, 1.0 / SHADOW_SHARPNESS);
}

float shadowRayLD(vec3 r0, vec3 rd, float maxDist) {
    float ds = 0.;
    float ret = 1.;
    for (int i=0; i<16 && ds < maxDist; i++) {
        vec3 p = r0 + rd * ds;
        float dist = getDist(p);
        ds += dist;
        if (dist < (0.5 * 1e-1)) {
            return 0.0;
        }
        ret = min(ret, SHADOW_STRENGTH * dist/ds);
    }
    return pow(ret, 1.0 / 2.);
}

vec3 getNormal(vec3 p) {
    vec2 eps = vec2(NORMAL_SAMPLE_DIST, 0.);
    return normalize(
        getDist(p) - vec3(
            getDist(p - eps.xyy),
            getDist(p - eps.yxy),
            getDist(p - eps.yyx)
        )
    );
}

float getLight(vec3 n, vec3 p) {

    vec3 ld = normalize(LIGHT - p);

    // Apply defuse lighting
    float l = clamp(dot(n, ld), 0., 1.) * (1. - AMBIENT_LIGHT) + AMBIENT_LIGHT;

    return l;
}

float getShading(vec3 n, vec3 p) {

    vec3 ld = normalize(LIGHT - p);
    return shadowRay(p + n*SURFACE_DIST, ld, length(LIGHT - p)-SURFACE_DIST);

}

float getShadingLD(vec3 n, vec3 p) {

    vec3 ld = normalize(LIGHT - p);
    return shadowRayLD(p + n, ld, length(LIGHT - p)-1.0);

}


// Main
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Translate viewport
    vec2 uv = (fragCoord/iResolution.xy - vec2(0.5, 0.5)) * vec2(1., iResolution.y / iResolution.x);
    vec2 uvx1 = ((fragCoord-vec2(2., 0.))/iResolution.xy - vec2(0.5, 0.5)) * vec2(1., iResolution.y / iResolution.x);
    vec2 uvx2 = ((fragCoord+vec2(2., 0.))/iResolution.xy - vec2(0.5, 0.5)) * vec2(1., iResolution.y / iResolution.x);
    vec2 uvy1 = ((fragCoord-vec2(0., 2.))/iResolution.xy - vec2(0.5, 0.5)) * vec2(1., iResolution.y / iResolution.x);
    vec2 uvy2 = ((fragCoord+vec2(0., 2.))/iResolution.xy - vec2(0.5, 0.5)) * vec2(1., iResolution.y / iResolution.x);

    // Animate camera and compute camera ray
    mat3 crot = rotAxis(vec3(0., 1., 0.), iTime * 0.15);
    vec3 r0 = vec3(0., 0., -40. + (sin(iTime * 0.25) * 0.5 + 0.5) * 15.);
    vec3 rd = normalize(vec3(uv, 1.));
    r0 = crot * r0;
    rd = normalize(crot * rd);

    vec3 rdx1 = normalize(crot * normalize(vec3(uvx1, 1.)));
    vec3 rdx2 = normalize(crot * normalize(vec3(uvx2, 1.)));
    vec3 rdy1 = normalize(crot * normalize(vec3(uvy1, 1.)));
    vec3 rdy2 = normalize(crot * normalize(vec3(uvy2, 1.)));

    // Render fragment
    float id = rayMarch(r0, rd);
    vec3 rp = r0 + rd * id;
    vec3 n = getNormal(rp);
    float shadow = getShading(n, rp);
    float light = getLight(n, rp) * shadow;
    if (id < (FAR_CLIP-(1e-6))) {
    	fragColor = vec4(getClr(rp) * light, 1.0);
    }
    else {
        fragColor = vec4(0., 0., 0., 1.);
    }

    // Atmosphere
    vec2 rsi = raySphere(r0, rd, vec3(0., 0., 0.), 5.75);
    if (rsi.x > 0.) {
        rsi.y = min(rsi.y, id);
        vec3 rp2 = r0 + rd * (rsi.x+rsi.y) * 0.5;
        float ashadow = 0.35 + 0.65 * getShadingLD(n, rp2);
        float astr = max(rsi.y - rsi.x, 0.) / 11.5 * max(dot(normalize(LIGHT), normalize(rp2)), 0.0);
        fragColor.rgb += vec3(1., 1., 0.8) * ashadow * pow(astr, 2.0);
    }

}

float sphere(vec3 p) {
    p += vec3(sin(iTime), 0., cos(iTime) - 1.0);
    return length(p) - 1.;
}

float ground(vec3 p) {
    return p.y + 1.5;
}

float ceiling(vec3 p) {
    return -p.y + 1.5;
}

float walls(vec3 p) {
    return min(-p.z + 3.0, 
               min(p.x + 3.0, -p.x + 3.0));
}

float creature (vec3 p) {
    p += vec3(2.*sin(iTime/3.), 0.5*sin(iTime/3.01), cos(iTime/3.) - 1.0);
    p.x += 0.1*sin(p.y*2.0+iTime);
    p.z += 0.1*cos(p.y*2.0+iTime);
    return length(p) - 1.0;
}

float f(vec3 p) {
    return 
        min(creature(p), 
            min(ground(p),
               min(ceiling(p),
                  walls(p))));
}

float raymarch(vec3 o, vec3 d) {
    vec3 p = o;
    int count = 0;
    while (abs(f(p)) > 0.001 && count < 160) {
        p += f(p) * d;
        count++;
    }
    return length(o - p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = vec2(fragCoord.x - 0.5 * iResolution.x, fragCoord.y - 0.5 * iResolution.y);
    uv /= iResolution.y;

    mat3 maty = mat3(
        cos(iTime), 0, -sin(iTime),
        0, 1, 0,
        sin(iTime), 0, cos(iTime));

    mat3 matx = mat3(
        1, 0, 0, 
        0, cos(iTime), sin(iTime),
        0, -sin(iTime), cos(iTime));

    mat3 matz = mat3(
        cos(iTime), sin(iTime), 0,
        -sin(iTime), cos(iTime), 0,
        0, 0, 1);

    vec3 start = vec3(uv.x/10., uv.y/10., 0.);
    if (mod(iTime+3.14, 4.*3.14) < 6.28) {
        start.z -= 3.;
    } else {
        start.z -= 3.;
    }
    vec3 dir = normalize(vec3(uv.x/1., uv.y/1., 1.));

    float dist = raymarch(start, dir);
    vec3 hit = start + dist * dir;

    // Time varying pixel color
    vec3 col = vec3(1. - raymarch(start, dir)/10.);

    if (abs(hit.y) < 1.49 && abs(hit.z) < 2.99 && abs(hit.x) < 2.99) {
        col = vec3(float(
            (int(abs(hit.z*10.)) % 2)));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

// Plane:
float sdPlane( vec3 p )
{
	return p.y;
}



// Torus:
float sdTorus( vec3 p, vec2 t )
{
    return length( vec2(length(p.xz)-t.x,p.y) )-t.y;
}

// Ellipsoid:
float sdEllipsoid( in vec3 p, in vec3 r ) // approximated
{
    float k0 = length(p/r);
    float k1 = length(p/(r*r));
    return k0*(k0-1.0)/k1;

}


// Capsule
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
	vec3 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

// Round Cone:
float sdRoundCone( in vec3 p, in float r1, float r2, float h )
{
    vec2 q = vec2( length(p.xz), p.y );

    float b = (r1-r2)/h;
    float a = sqrt(1.0-b*b);
    float k = dot(q,vec2(-b,a));

    if( k < 0.0 ) return length(q) - r1;
    if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

    return dot(q, vec2(a,b) ) - r1;
}

///////////////////////////////////////////////////////////////////////
// Union, Subtraction, Intersection:
//Union:
float opUnion( float d1, float d2 )
{
    return min(d1,d2);
}
// Subtraction:
float opSubtraction( float d1, float d2 )
{
    return max(-d1,d2);
}

// Intersection:
float opIntersection( float d1, float d2 )
{
    return max(d1,d2);
}
// Round box:
float sdRoundBox( in vec3 p, in vec3 b, in float r ) 
{
    vec3 q = abs(p) - b;
    return min(max(q.x,max(q.y,q.z)),0.0) + length(max(q,0.0)) - r;
}

// Sphere:
float sdSphere( vec3 p, float s )
{
    return length(p)-s;
}

float map(in vec3 pos)
{
    float d = 1e10;


    float an = sin(iTime);

    // opUnion
    {
    vec3 q = pos - vec3(-2.0,0.0,-1.3);
    float d1 = sdSphere( q-vec3(0.0,0.5+0.3*an,0.0), 0.55 );
    float d2 = sdRoundBox(q, vec3(0.6,0.2,0.7), 0.1 ); 
    float dt = opUnion(d1,d2);
    d = min( d, dt );
  	}

    // opSubtraction
    {
    vec3 q = pos - vec3(0.0,0.0,-1.3);
    float d1 = sdSphere( q-vec3(0.0,0.5+0.3*an,0.0), 0.55 );
    float d2 = sdRoundBox(q, vec3(0.6,0.2,0.7), 0.1 ); 
    float dt = opSubtraction(d1,d2);
    d = min( d, dt );
    }

    // opIntersection
    {
    vec3 q = pos - vec3(2.0,0.0,-1.3);
    float d1 = sdSphere( q-vec3(0.0,0.5+0.3*an,0.0), 0.55 );
    float d2 = sdRoundBox(q, vec3(0.6,0.2,0.7), 0.1 ); 
    float dt = opIntersection(d1,d2);
    d = min( d, dt );
    }



    return d;
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    const float ep = 0.0001;
    vec2 e = vec2(1.0,-1.0)*0.5773;
    return normalize( e.xyy*map( pos + e.xyy*ep ) + 
					  e.yyx*map( pos + e.yyx*ep ) + 
					  e.yxy*map( pos + e.yxy*ep ) + 
					  e.xxx*map( pos + e.xxx*ep ) );
}

// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, float tmin, float tmax, const float k )
{
	float res = 1.0;
    float t = tmin;
    for( int i=0; i<50; i++ )
    {
		float h = map( ro + rd*t );
        res = min( res, k*h/t );
        t += clamp( h, 0.02, 0.20 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

#define AA 2

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = (2.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

    //vec3 sphere = vec3(uv, 0.3);
    //float d1 = sdSphere(sphere, 0.4);
    //float r = length( uv )*1.9;

    //vec3 col = mix( vec3(0.6, 0.0, 0.5), vec3(0.9-0.4*pow(r,4.0)), 1.0-smoothstep(0.94,0.95,r) );

    // Output to screen
    //fragColor = vec4(col,1.0);

    vec3 tot = vec3(0.0);

    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;

        vec3 ro = vec3(0.0,4.0,8.0);
        vec3 rd = normalize(vec3(p-vec2(0.0,1.8),-3.5));

        float t = 7.0;
        for( int i=0; i<64; i++ )
        {
            vec3 p = ro + t*rd;
            float h = map(p);
            if( abs(h)<0.001 || t>11.0 ) break;
            t += h;
        }

        vec3 col = vec3(0.0);

        if( t<11.0 )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            vec3  lig = normalize(vec3(1.0,0.8,-0.2));
            float dif = clamp(dot(nor,lig),0.0,1.0);
            float sha = calcSoftshadow( pos, lig, 0.001, 1.0, 16.0 );
            float amb = 0.5 + 0.5*nor.y;
            col = vec3(0.05,0.1,0.15)*amb + vec3(1.00,0.9,0.80)*dif*sha;
        }

        col = sqrt( col );
	    tot += col;

    }
    tot /= float(AA*AA);


	fragColor = vec4( tot, 1.0 );
}

// Hash functions by Dave Hoskins: https://www.shadertoy.com/view/4djSRW

float hash12(vec2 p) {
    float hashScale = 0.1031;
	vec3 p3  = fract(vec3(p.xyx) * hashScale);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

const vec2 add = vec2(1.0,0.0);
float noise( in vec2 x ) {
    vec2 p = floor(x);
    vec2 f = fract(x);
    f = f*f*(1.5-f)*2.0;

    float res = mix(mix( hash12(p), hash12(p + add.xy),f.x),
                    mix( hash12(p + add.yx), hash12(p + add.xx),f.x),f.y);
    return res;
}

// Noise functions and warping methods inspired and modified from https://www.shadertoy.com/view/lsl3RH

const float angle = 0.2;
const float scale = 3.24;
const mat2 m = mat2(cos(angle), sin(angle), -sin(angle), cos(angle)) * scale;

float fbm4( vec2 p ) {
    float f = 0.0;
    f += 0.5000*noise( p ); p = m*p;
    f += 0.2500*noise( p ); p = m*p;
    f += 0.1250*noise( p ); p = m*p;
    f += 0.0625*noise( p );
    return f/0.9375;
}

float fbm6( vec2 p ) {
    float f = 0.0;
    f += 0.500000*(0.5+0.5*noise( p )); p = m*p;
    f += 0.250000*(0.5+0.5*noise( p )); p = m*p;
    f += 0.125000*(0.5+0.5*noise( p )); p = m*p;
    f += 0.062500*(0.5+0.5*noise( p )); p = m*p;
    f += 0.031250*(0.5+0.5*noise( p )); p = m*p;
    f += 0.015625*(0.5+0.5*noise( p ));
    return f/0.96875;
}

vec3 warpedNoise(in vec2 q) {

	vec2 o = vec2(0.0);
    o.x = 0.5*fbm6(vec2(2.0*q));
    o.y = 0.5*fbm6(vec2(2.0*q));

    vec2 n = vec2(0.0);
    n.x = fbm6(vec2(7.0*o+vec2(19.2)));
    n.y = fbm6(vec2(7.0*o+vec2(15.7)));

    vec2 p = 4.0*q + 4.0*n;

    float f = 0.5 + 0.5 * fbm4(p);

    // Rendering the value in different ways
    float time = mod(iTime - 13.0, 4.0 * 4.0);

    // Option 1: Grayscale rendering
    if (time < 4.0) {
    	return 0.8 * vec3(f);
    }

    // Option 2: Rendering with steps
    if (time < 8.0) {
    	float steps = 10.0;
    	return 0.8 * vec3(floor(f * steps) / steps);
    }

    // Option 3: Dirt-like Colours
    if (time < 12.0) {
    	float steps = 10.0;
    	float val = floor(f * steps) / steps;
        val = min(max(val, 0.6), 0.8);
    	return 0.45 + 0.35*sin(vec3(0.05,0.08,0.10)*(val*1583343.0));
    }

    // Option 4: Camo-like Colours
    else {
    	float steps = 10.0;
    	float val = floor(f * steps) / steps;
        val = min(max(val, 0.6), 0.8);
    	return 0.25 + 0.15*sin(vec3(0.05,0.08,0.10)*(val*1234.1));
    }

    // Option 5: Time-based Colours (unused)
    //float steps = 10.0;
    //float val = floor(f * steps) / steps;
    // Color function from iq: https://www.shadertoy.com/view/Xds3zN
    //return 0.45 + 0.35*sin(vec3(0.05,0.08,0.10)*(val*iTime*10.0));

}

