}

const float iter = 256.0;
const float cx = -1.0, cy = 0.0;

float atan2(in float y, in float x){
    return x == 0.0 ? sign(y)*3.14159265358979/2.0 : atan(y, x);
}

float f( float x ) {
	return x*x;    
}

vec3 hsv(float h, float s, float v){
    vec4 t = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(vec3(h) + t.xyz) * 6.0 - vec3(t.w));
    return v * mix(vec3(t.x), clamp(p - vec3(t.x), 0.0, 1.0), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;

    float x = ((uv.x-0.5)*3.0-cx)*(1.0/f(iTime))+cx, y = (uv.y*3.0-cy)*(1.0/f(iTime))+cy;
    float r = sqrt(x*x+y*y), theta = atan2(y,x);
    float itrX = x, itrY = y;
    float a = 3.0;

    float i;
    for( i = 0.0; i < iter; ++i )
    {
        float tmpX = itrX, tmpY = itrY;

        itrX = pow(r,tmpX)*exp(-tmpY*theta)*cos(tmpY+tmpX*theta);
        itrY = pow(r,tmpX)*exp(-tmpY*theta)*sin(tmpY+tmpX*theta);

        if( length(vec2(itrX-x,itrY-y)) > 30.0 )
        	break;
    }

    // Output to screen
    fragColor = vec4(vec3((10.0*i)/iter),1.0);
    //fragColor = vec4(vec3(1.0-length(vec2(itrX-x,itrY-y))/10000.0),1.0);
}

#define time iTime

const int NRow = 10;
const int NCol = 10;
const float period = 100.;

const float X_MAX = 0.9;
const float X_MIN = -0.9;
const float Y_MAX = 0.9;
const float Y_MIN = -0.9;
const int NOrb = 3;
const float speed = 0.5;

/*
const vec3 color1 = vec3(0.05, 1.00, 0.80);
const vec3 color2 = vec3(0.80, 1.00, 0.05);
const vec3 color3 = vec3(0.05, 1.00, 0.05);
*/
const vec3 color1 = vec3(1.00, 0.01, 0.01);
const vec3 color2 = vec3(1.00, 0.01, 0.01);
const vec3 color3 = vec3(1.00, 0.01, 0.01);

mat2 rot(float t){
    return mat2(cos(t),-sin(t),sin(t),cos(t));
}
float rand(vec2 co){                                             
  return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}
float loopNoise(float s, float o, float period){
    float i = floor(s);
    float f = fract(s);
    float u = f * f * (3. - 2. * f);

    float s1 = rand(vec2(i/100., i*o/100.));
    float s2;
    if(s < period - 1.){
        s2 = rand(vec2((i+1.)/100., (i+1.)*o/100.));
    } else {
        s2 = rand(vec2(0.,0.));
    }

    return mix(s1, s2, u);
}

float circle(vec2 p) {
    return 0.05/pow(length(p), 1.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2. - iResolution.xy) / min(iResolution.x, iResolution.y);
    p *= 0.7;

    vec3 draw = vec3(0.);

    float m_dist = 3.;
    for (int i=0; i<NRow; i++) {
        for (int j=0; j<NCol; j++) {
            float tx = mod(time*speed+rand(vec2(float(i),float(j))*period), period);
            float ty = mod(time*speed+rand(vec2(float(j),float(i))*period), period);
            float dx = loopNoise(tx, float(i*NRow+j), period)*0.2-0.1;
            float dy = loopNoise(ty, float(j*NRow+i), period)*0.2-0.1;
            float x = 2./float(NRow-1)*float(i)-1. + dx;
            float y = 2./float(NCol-1)*float(j)-1. + dy;
        	float dist = distance(p, vec2(x, y));
        	m_dist = min(m_dist, dist);
        }
    }

    vec3 orbs = vec3(0.);
    for (int i=0; i<NOrb; i++) {
        vec3 color;
        if (i == 0) {
            color = color1;
        } else if (i == 1) {
            color = color2;
        } else {
            color = color3;
        }
        orbs += circle(p-vec2(
            loopNoise(mod((time*speed+rand(vec2(float(i),1.)*period)),period),float(i)+0.1,period)*(X_MAX-X_MIN)+X_MIN,
            loopNoise(mod((time*speed+rand(vec2(float(i),2.)*period)),period),float(i)+0.2,period)*(Y_MAX-Y_MIN)+Y_MIN
        )) * color;
    }
    float f = textureLod( iChannel0, p/256.0, 0.0 ).x;
    draw += pow(m_dist*(6.+f),3.) * orbs;

    fragColor = vec4(draw,1.0);
}

#define MAX_DIST 4.0


 mat3 mat_a = mat3(0.8, 0.6, 0.0,
                        -0.6, 0.8, 0.0,
                        0.0, 0.0, 1.0) * mat3(0.96, 0.0, 0.28, 0.0, 1.0, 0.0, -0.28, 0.0, 0.96);

 mat3 mat_b = mat3(0.96, 0.0, -0.28, 0.0, 1.0, 0.0, 0.28, 0.0, 0.96) *
    mat3(0.6, -0.8, 0.0,
         0.8, 0.6, 0.0,
         0.0, 0.0, 1.0);

 mat3 mat_c = mat3(0.8, 0.0, -0.6, 0.0, 1.0, 0.0, 0.6, 0.0, 0.8) *
    mat3(1.0, 0.0, 0.0,
         0.0, 0.96, -0.28,
         0.0, 0.28, 0.96);

const float cutoff = 0.81; // ad-hoc, should be much larger

float pill(in vec3 pt, in float l, in float r) {
    vec3 to_core = abs(pt - vec3(0.0, 0.5 * l, 0.0));
    to_core.y -= 0.5 * l;
    to_core = max(vec3(0.0), to_core);
    return length(to_core) - 2.0 * r;
}

float s_min(in float x, in float y, in float s) {

    float bridge =
        clamp(abs(x-y)/s, 0.0, 1.0);
    return min(x,y) - 0.25 * s * (bridge - 1.0) * (bridge - 1.0);
}


float sdf_3(in vec3 pt) {
    vec3 off = vec3(0.0, 0.25, 0.0);
    if (dot(pt-off, pt-off) > cutoff) {
        return length(pt-off);
    }
    float d = pill(pt, 0.25, 0.025);
    return d;
}


float sdf_2(in vec3 pt) {
    vec3 off = vec3(0.0, 0.25, 0.0);
    if (dot(pt-off, pt-off) > cutoff) {
        return length(pt-off);
    }
    float d = pill(pt, 0.25, 0.025);
    // d = s_min(d, 0.8 * sdf_3(1.25 * mat_a * (pt - off)), 0.025);
    d = s_min(d, 0.8 * sdf_3(1.25 * mat_b * (pt - off)), 0.025);
    d = s_min(d, 0.8 * sdf_3(1.25 * mat_c * (pt - off)), 0.025);
    return d;
}

float sdf_1(in vec3 pt) {
    vec3 off = vec3(0.0, 0.25, 0.0);
    if (dot(pt-off, pt-off) > cutoff) {
        return length(pt-off);
    }
    float d = pill(pt, 0.25, 0.025);
    d = s_min(d, 0.8 * sdf_2(1.25 * mat_a * (pt - off)), 0.025);
    // d = s_min(d, 0.8 * sdf_2(1.25 * mat_b * (pt - off)), 0.025);
    d = s_min(d, 0.8 * sdf_2(1.25 * mat_c * (pt - off)), 0.025);
    return d;
}

float sdf(in vec3 pt) {
    vec3 off = vec3(0.0, 0.25, 0.0);
    if (dot(pt-off, pt-off) > cutoff) {
        return length(pt-off);
    }
    float d = pill(pt, 0.25, 0.025); // temporary sdf
    d = s_min(d, 0.8 * sdf_1(1.25 * mat_a * (pt - off)), 0.025);
    d = s_min(d, 0.8 * sdf_1(1.25 * mat_b * (pt - off)), 0.025);
    // d = s_min(d, 0.8 * sdf_1(1.25 * mat_c * (pt - off)), 0.025);
    return d;
}

vec3 sdf_grad(in vec3 pt) {
    float f = sdf(pt);
    const float h = 0.001;
    const float h_inv = 1000.0;

    return h_inv *
        vec3(sdf(pt + vec3(h, 0.0, 0.0)) - f,
             sdf(pt + vec3(0.0, h, 0.0)) - f,
             sdf(pt + vec3(0.0, 0.0, h)) - f);
}

float raymarch(in vec3 pt, in vec3 dir) {
    vec3 d = normalize(dir);
    vec3 p = pt;
    float accum = 0.0;
    float s = sdf(pt);
    for(int i = 0; i < 128; ++i) {
        if (accum > MAX_DIST || s < 1.0e-3) {
            return accum;
        }
        accum += 0.75 * s;
        p = pt + accum * d;
        s = sdf(p);
    }
    if (s > 1.0e-3) {
        return MAX_DIST + 1.0;
    }
    return accum;
}

float raymarch_out(in vec3 pt, in vec3 dir) {
    vec3 d = normalize(dir);
    vec3 p = pt;
    float accum = 0.0;
    float s = sdf(pt);
    for(int i = 0; i < 2; ++i) {
        if (accum > 1.0e-3 && s > -1.0e-3) {
            return accum;
        }
        accum += 0.75 * max(abs(s), 1.0e-3);
        p = pt + accum * d;
        s = sdf(p);
    }
    if (s < -1.0e-3) {
        return 1000.0;
    }
    return accum;
}

float ramp(in float a, in float b, in float x) {
    float p = (x-a)/(b-a);
    return clamp(p, 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 dir = normalize(vec3(uv, 6.0));

    vec3 orig = vec3(0.0, 0.25, -2.5);

    float theta = 0.1 * iTime;
    float ct = cos(theta);
    float st = sin(theta);

    mat3 spin = mat3(ct, 0.0, st,
                     0.0, 1.0, 0.0,
                     -st, 0.0, ct);

    theta = 0.25 * sin(iTime);
    vec2 trigs = cos(vec2(theta, theta + 0.5 * 3.141592654));
    ct = 0.8 * trigs.x - 0.6 * trigs.y;
    st = 0.6 * trigs.x + 0.8 * trigs.y;
    mat_a =
	    mat3(ct, st, 0.0,
                        -st, ct, 0.0,
                        0.0, 0.0, 1.0) * mat3(0.96, 0.0, 0.28, 0.0, 1.0, 0.0, -0.28, 0.0, 0.96);
	theta = 0.25 * sin(1.21 * iTime);
    trigs = cos(vec2(theta, theta + 0.5 * 3.141592654));
    ct = 0.6 * trigs.x - 0.8 * trigs.y;
    st = 0.8 * trigs.x + 0.6 * trigs.y;

    mat_b = 
        mat3(0.96, 0.0, -0.28, 0.0, 1.0, 0.0, 0.28, 0.0, 0.96) *
    mat3(ct, -st, 0.0,
         st, ct, 0.0,
         0.0, 0.0, 1.0);

    theta = 0.25 * sin(0.93 * iTime);
    trigs = cos(vec2(theta, theta + 0.5 * 3.141592654));
    ct = 0.8 * trigs.x - 0.6 * trigs.y;
    st = 0.6 * trigs.x + 0.8 * trigs.y;

    mat_c = mat3(ct, 0.0, -st, 0.0, 1.0, 0.0, st, 0.0, ct) *
    mat3(1.0, 0.0, 0.0,
         0.0, 0.96, -0.28,
         0.0, 0.28, 0.96);
    orig = spin * orig;
    dir = spin * dir;

    float dist = raymarch(orig, dir);

    float bright = 0.0;

    vec3 refl_color = vec3(1.0);
    vec3 trans_color = vec3(1.0, 0.5, 0.4);
    const vec3 light_dir = vec3(1.0, 0.0, 0.0);
    float thru_dist = 1000.0;
    vec3 n = dir;
    if (dist < MAX_DIST) {
        n = normalize(sdf_grad(orig + dir * dist));
//        float inc 
        // bright = smoothstep(0.0, 0.2, abs(dot(dir, n)));
        float dist_out = raymarch_out(orig + dir * (dist + 1.0e-2), dir);
        bright = ramp(0.0, 1.0 / iResolution.y, dist_out);
        dir = mix(dir, normalize(reflect(dir, n)), bright);
        thru_dist = raymarch_out(orig + dir * (dist + 1.0e-2), light_dir);
        refl_color = vec3(0.8, 0.7, 0.6);
    }

    vec3 col = (0.5 + 0.5 * bright) *
        (0.2 * smoothstep(0.7, 1.0, dot(dir, light_dir)) + 0.7 * smoothstep(0.1, 1.0,
                                                                      dot(n, light_dir)))*
        refl_color;

    col += bright * (1.0 / max(abs(1.0 * thru_dist), 0.5)) * trans_color;

    // Output to screen
    fragColor =  vec4(col,1.0);
}

float mainBubbleSize = 70.0;
float bubbleOutline = 5.0;
float curlHeight = 9.0;
float curlFrequency = 10.0;
float curlNoisePower = 25.0;

vec2 leadBubble = vec2(120.0, 20.0);
float startBabyBubbleSize = 7.0;
float babyBubbleWidth = 2.0;

const int babyBubbles = 4;

float horizontalStretch = 0.6;
float swimPower = 40.0;
float babySwimPower = 10.0;

// Uses Inigo Quilez Simpex Noise
// Simplex  Noise 2D             : https://www.shadertoy.com/view/Msf3WH
vec2 hash( vec2 p ) // replace this by something better
{
	p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)) );

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

	vec2 i = floor( p + (p.x+p.y)*K1 );

    vec2 a = p - i + (i.x+i.y)*K2;
    vec2 o = step(a.yx,a.xy);    
    vec2 b = a - o + K2;
	vec2 c = a - 1.0 + 2.0*K2;

    vec3 h = max( 0.5-vec3(dot(a,a), dot(b,b), dot(c,c) ), 0.0 );

	vec3 n = h*h*h*h*vec3( dot(a,hash(i+0.0)), dot(b,hash(i+o)), dot(c,hash(i+1.0)));

    return dot( n, vec3(70.0) );
}

float n_noise(in vec2 p)
{
    return 0.5 + 0.5 * noise(p);
}


// https://www.shadertoy.com/view/XsfGzn
vec4 videoColor(vec2 center, vec2 coord, float size) {
    vec4 bg = vec4(1.0, 1.0, 1.0, 1.0);
    vec2 q = (coord - center) / size;
    q.x += 0.5;
    q.y += 0.5;

    if(q.x <= 0.0)
        return bg;
    if(q.x >= 1.0)
        return bg;
    if(q.y <= 0.0)
        return bg;
    if(q.y >= 1.0)
        return bg;

	vec3 fg = texture( iChannel0, q ).xyz;


    float maxrb = max( fg.r, fg.b );
    float k = clamp( (fg.g-maxrb)*5.0, 0.0, 1.0 );

    float uvDist = distance(vec2(0.5, 0.5), q);
    uvDist = pow(uvDist, 1.0/1.6) / 0.7071;

    float dg = fg.g; 
    fg.g = min( fg.g, maxrb*0.8 ); 
    fg += dg - fg.g;
    fg = mix(fg, bg.xyz, uvDist);



    return vec4( mix(fg, bg.xyz, k), uvDist );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float outsideBubble= mainBubbleSize + bubbleOutline;
    vec2 center = vec2(320, 190);
    float bubbleSwimX = noise(vec2(iTime * 0.1, 0.0)) * swimPower;
    float bubbleSwimY = noise(vec2(iTime * 0.06, 0.0)) * swimPower;
    center.x += bubbleSwimX;
    center.y += bubbleSwimY;


    vec2 pt = fragCoord - center;
    pt.x *= horizontalStretch;

    float angle = atan(pt.y / pt.x);
    float d = length(pt);

    d -= abs(cos(angle * curlFrequency)) * curlHeight;
    d -= n_noise(fragCoord * 0.01) * curlNoisePower;


    vec4 c = vec4(1.0, 1.0, 1.0, 1.0);


    if(d < mainBubbleSize) {        
        c = videoColor(center, fragCoord, mainBubbleSize * 2.0);
    }
    else if(d < outsideBubble) {
        c = vec4(0.0, 0.0, 0.0, 1.0);
    }
    else {
        for(int i = 0; i < babyBubbles; i++) {
            float fi = float(i);
            vec2 babyBubble = leadBubble;
     		float babySwimX = noise(vec2(iTime * 0.1 * fi, 0.0)) * babySwimPower;
    		float babySwimY = noise(vec2(iTime * 0.06 * fi, 0.0)) * babySwimPower;           
            babyBubble.x += babySwimX;
            babyBubble.y += babySwimY;
            babyBubble.x += fi * 35.0;
            babyBubble.y += pow(fi * 2.5, 2.0);

            float babyBubbleSize = startBabyBubbleSize * (fi*0.4 + 1.0);
            pt = fragCoord - babyBubble;
            pt.x *= horizontalStretch;
            d = length(pt);
            if(d > babyBubbleSize && d < babyBubbleSize + babyBubbleWidth) {
                c = vec4(0.0, 0.0, 0.0, 1.0);
            }
        }
    }

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = c;
}

// Loopless version of toocanzs's https://shadertoy.com/view/3sfGDj


#define DYNAMIC

#define CS(a)  vec2(cos(a),sin(a))

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = ( 2.*u - R )/ R.y;
    O = vec4(0);

    float PI = 3.14159,
#ifdef DYNAMIC
        phi = iTime*0.001 + 0.1,
#else
        phi = (1. + sqrt(5.))/2.,
#endif
          a = phi * 2.*PI,
         i0 = ( length(U) - .01 ) /.0015,
         i1 = ( mod( PI-atan(U.y,U.x) ,2.*PI) )/ a, // + k.2PI/a
          k = floor( (i0-i1) / (2.*PI/a) ), 
          i, d = 1e9;

    for (float n = 0.; n < 2.; n++) {
        i = round(i1 + k++ * 2.*PI/a );
        vec2 p = ( .01+ 0.0015*i) *CS(PI-i*a);
    	d = min( d, length(U - p) - .001);
        //O[int(n)%3] += max(0.,1.-length(U - p)/.1);    
    }

    O += vec4(smoothstep(3./iResolution.y, 0., d - .01));
}      






/**    // toocanzs's shader https://shadertoy.com/view/3sfGDj

