}

vec3 grad( vec3 pos )
{
	vec2 h = vec2(0.00001, 0.);

    return vec3(
    	func(pos+h.xyy)-func(pos-h.xyy),
        func(pos+h.yxy)-func(pos-h.yxy),
        func(pos+h.yyx)-func(pos-h.yyx)
    )/(2.0*h.xxx);
}

vec3 gradAna( vec3 pos )
{
  float x = pos.x, y = pos.y, z = pos.z;

  return vec3(
    -2749.231165*3.*x*x*z*y*y-1832.820776*y*z*z*2.*x+648.*z*2.*x*y*y-1620.*z*z*2.*x*y*y+1832.820776*y*z*z*z*2.*x-4123.846747*y*y*y*y*z+916.4103882*z*z*z*y*y-729.*6.*x*x*x*x*x-144.*z*z*2.*x+324.*z*z*z*z*2.*x+324.*z*4.*x*x*x-810.*z*z*4.*x*x*x-2187.*4.*x*x*x*y*y-2187.*2.*x*y*y*y*y+1374.615582*5.*x*x*x*x*z-305.4701294*z*z*z*3.*x*x,
    -2749.231165*x*x*x*z*2.*y-1832.820776*z*z*x*x+648.*z*x*x*2.*y-1620.*z*z*x*x*2.*y+1832.820776*z*z*z*x*x-4123.846747*4.*y*y*y*x*z+916.4103882*z*z*z*x*2.*y-729.*6.*y*y*y*y*y-144.*z*z*2.*y+324.*z*z*z*z*2.*y+324.*z*4.*y*y*y+610.9402588*3.*y*y*z*z-810.*z*z*4.*y*y*y-610.9402588*3.*y*y*z*z*z-2187.*x*x*x*x*2.*y-2187.*x*x*4.*y*y*y,
    -2749.231165*x*x*x*y*y-1832.820776*y*2.*z*x*x+648.*x*x*y*y-1620.*2.*z*x*x*y*y+1832.820776*y*3.*z*z*x*x-4123.846747*y*y*y*y*x+916.4103882*3.*z*z*x*y*y-288.*4.*z*z*z+64.*3.*z*z+432.*5.*z*z*z*z-216.*6.*z*z*z*z*z-144.*2.*z*x*x-144.*2.*z*y*y+324.*4.*z*z*z*x*x+324.*4.*z*z*z*y*y+324.*x*x*x*x+324.*y*y*y*y+610.9402588*y*y*y*2.*z-810.*2.*z*x*x*x*x-810.*2.*z*y*y*y*y-610.9402588*y*y*y*3.*z*z+1374.615582*x*x*x*x*x-305.4701294*3.*z*z*x*x*x
  );
}

vec2 map( in vec3 pos )
{
    vec2 res = vec2( sdPlane(     pos), 1.0 );

    pos -= vec3( -0.5,0.25, 0.0);
    pos *= 2.;

    res = opU( res, vec2( abs(func(pos))/length(gradAna(pos)), 46.9) );

    res.x /= 2.0;

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd )
{
    float tmin = 1.0;
    float tmax = 20.0;

#if 1
    // bounding volume
    float tp1 = (0.0-ro.y)/rd.y; if( tp1>0.0 ) tmax = min( tmax, tp1 );
    float tp2 = (1.6-ro.y)/rd.y; if( tp2>0.0 ) { if( ro.y>1.6 ) tmin = max( tmin, tp2 );
                                                 else           tmax = min( tmax, tp2 ); }
#endif

    float t = tmin;
    float m = -1.0;
    for( int i=0; i<128; i++ )
    {
	    float precis = 0.0004*t;
	    vec2 res = map( ro+rd*t );
        if( res.x<precis || t>tmax ) break;
        t += res.x;
	    m = res.y;
    }

    if( t>tmax ) m=-1.0;
    return vec2( t, m );
}


// http://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax )
{
	float res = 1.0;
    float t = mint;
    for( int i=ZERO; i<16; i++ )
    {
		float h = map( ro + rd*t ).x;
        res = min( res, 8.0*h/t );
        t += clamp( h, 0.02, 0.10 );
        if( res<0.005 || t>tmax ) break;
    }
    return clamp( res, 0.0, 1.0 );
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
#if 0
    vec2 e = vec2(1.0,-1.0)*0.5773*0.0005;
