#define NUM_POINT 256
#define HARMONIK 8.0f
#define KERNEL 4 + 1
#define TIME (5000.0f + iTime) * 0.07f
vec2 points[KERNEL];

float hash11(float p)
{
	vec3 p3  = fract(vec3(p) * .1f);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash21(float p)
{

return vec2(fract(sin(dot(vec2(p, p * vec2(20.0f)), vec2(12.9898, 78.233))) * 43758.5453), fract(sin(dot(vec2(p,p), vec2(8.9898, 78.233))) * 41758.5453));
}

vec2 getPoint(int graine) {
   // return vec2(0.5f + 0.1f * float(graine),float(graine)*0.0f + .5f);
    vec2 p = hash21(float(graine));
    for (int i = 0; i < int(HARMONIK); ++i) {
    float c = hash11(float(graine + i*NUM_POINT));
     float h = hash11(float(graine + i*NUM_POINT));
    p += 16.0f*(vec2(cos(float(i) / float(HARMONIK) + c * TIME * HARMONIK), sin(h * TIME * HARMONIK)) / (1.0f + 18.0f)) / float(HARMONIK);
    }
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x/iResolution.y;
    // Time varying pixel color
    vec3 col = 0.0 + 0.005*cos(iTime+uv.xyx+vec3(0,2,4)) * vec3(1.0f, 0.5f, 0.5f);

    for (int i = 0; i < KERNEL; ++i)
        points[i] = vec2(1000.0f);
    for (int i = 0; i < NUM_POINT; ++i) {
		vec2 p = getPoint(i);
		float m = 0.0f;
        int mi = 0;
        for (int j = 0; j < KERNEL - 1; ++j) {
            if (length(uv - points[j]) > m) {
                m = length(uv - points[j]) ;
                    mi = j;
                    }
        }
        if (m > length(uv - p))
            points[mi] = p;

    }
    //points[2] = vec2(0.5f + 0.1f * 0.0f,0.0f + .5f);
 //   points[1] = vec2(0.5f + 0.1f * 1.0f,0.0f + .5f);
 //   points[0] = vec2(0.5f + 0.1f * 2.0f,0.0f + .5f);

    float t = (.0f);
    for (int i = 0; i < KERNEL - 1; ++i) {
        t += length(points[i] - uv);
    }
    t /= float(KERNEL);
    if (t < 0.1f * float(KERNEL)){
        col +=1200.5f * t*t*t*t * vec3(0.2f, 0.7f, 0.1f);
        col += 0.00000002f *1.0f / (t*t *t*t) * vec3(.5f,0.f, .5f);
    }
    for (int i = 0; i < KERNEL-1; ++i) {
          if (length(uv - points[i]) < 0.01f)
           col += .0001f + (1.0f / (t*t)) * .000000004f * 1.0f / length(uv - points[i]) * (.8f + .2f * sin(0.0f * 40.0f));   
    }
col *= 8.0f;
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define pi acos(-1.0)

float oldpizza( vec2 p, vec2 c, float rad1, float rad2)
{
    p.x = abs(p.x);

    float len2 = dot(vec2(c.y,-c.x),p.xy);
    float len3 = dot(c,p);

    float len;
    if (len2 > 0.0) {
        len = len3;
    } else {
        len = length(p);
        if (len3 < 0.0) {
            len2 = -len;
        }
    }

    vec2 lens = vec2(abs(len-(rad1+rad2)*0.5)-(rad2-rad1)*0.5,len2);

    len = min(max(lens.x,lens.y),0.0)+length(max(lens,0.0));

    return len;
}

float pizza( vec2 p, vec2 c, float rad1, float rad2)
{
    p.x = abs(p.x);

    vec2 c2 = vec2(c.y,-c.x);

    float l = -dot(c,p.xy);
    float q = p.x+c.x*l;
    float l2;

    if (max(q,-sign(p.y*c.y)) < 0.0) {
        l = -length(p.xy);
    }

    if (l < 0.0) {
        l2 = length(p.xy);
    } else {
        l2 = dot(c2,p.xy)*sign(c.y);
    }



    vec2 a = vec2(max(l2-rad2,rad1-l2),l);

    return length(max(a,0.0))+min(0.0,max(a.x,a.y));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 muv = iMouse.xy/iResolution.xy;

    if (iMouse.x <= 0.0) 
        muv = vec2(0.4+sin(iTime)*0.1,0.85+cos(iTime)*0.1);

    float t = muv.y*pi*2.0;
    float r = muv.x*0.5;

    float len = pizza(uv,vec2(-cos(t),sin(t)), r,0.5);

    //distance field coloring by iq https://www.shadertoy.com/view/4lcBWn
    vec3 col = vec3(1.0) - sign(len)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-2.0*abs(len));
	col *= 0.8 + 0.2*cos(120.0*abs(len));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.02,abs(len)) );

	fragColor = vec4(col, 1.0);
}

