float opUnion(float d1, float d2) { return min(d1, d2); }
float opIntersection(float d1, float d2) { return max(d1, d2); }
float opSubtraction(float d1, float d2) { return max(d1, -d2); }
float opOnion(float d, float thikness) { return abs(d) - thikness; }

mat2 rotMat(float theta)
{
    float c = cos(theta);
    float s = sin(theta);

    return mat2(c, s, -s, c);
}

float sdCircle(vec2 p, float r)
{
    return length(p) - r;
}

float sdFish(vec2 p, float r)
{
    float upperCircle = sdCircle(p - vec2(0, 0.6) * r, r);
    float lowerCircle = sdCircle(p - vec2(0, -0.6) * r, r);

    float upperTail = sdCircle(p - vec2(1.4, 0.5) * r, r);
    float lowerTail = sdCircle(p - vec2(1.4, -0.5) * r, r);

    float body = opIntersection(upperCircle, lowerCircle);
    float tail = opSubtraction(opIntersection(upperTail, lowerTail), sdCircle(p - vec2(2, 0) * r, r));
    float eye = sdCircle(p - vec2(-0.4, 0.1) * r, 0.07 * r);
    return opSubtraction(opUnion(body, tail), eye);
}

float sdMovingFish(vec2 p, float r, float t, float speed, float period)
{
    float x = mod(p.x + 0.5 + speed * t, period) - 0.5;
    return sdFish(vec2(x, p.y), r);
}

float sdRect(vec2 p, vec2 r)
{
    vec2 d = abs(p) - r;
    return length(max(d, 0.0))
        + min(max(d.x, d.y), 0.0);
}

float sdTriangle(vec2 p, vec2 p0, vec2 p1, vec2 p2)
{
    vec2 v0 = p1 - p0;
    vec2 v1 = p2 - p1;
    vec2 v2 = p0 - p2;

    vec3 sd = vec3(
        dot(p - p0, normalize(vec2(-v0.y, v0.x))),
        dot(p - p1, normalize(vec2(-v1.y, v1.x))),
        dot(p - p2, normalize(vec2(-v2.y, v2.x)))
    );

    return max(sd[0], max(sd[1], sd[2]));
}

float sdBoat(vec2 p, float s)
{
    vec2[] points = vec2[](
        vec2(+1.5, 0.0) * s,
        vec2(-1.5, 0.0) * s,
        vec2(-2.0, 1.0) * s,
        vec2(+2.0, 1.0) * s,
        vec2(0) // to make the array cyclic
    );
    points[points.length() - 1] = points[0];
    int pointCount = points.length() - 1;

    mat2 normalMat = mat2(0.0, 1.0, -1.0, 0.0);

    float maxSd = -99999.0;

    for (int i = 0; i < pointCount; i++)
    {
        vec2 p0 = points[i];
        vec2 p1 = points[i + 1];
        vec2 normal = normalize(normalMat * (p1 - p0));
        float sd = dot(p - p0, normal);
        maxSd = max(maxSd, sd);
    }

    return maxSd;
}

float sdSail(vec2 p, float s)
{
    return sdTriangle(
        p,
        vec2(vec2(-1.0, 1.0) * s),
        vec2(vec2(+0.0, 2.0) * s),
        vec2(vec2(+1.0, 1.0) * s)
    );
}

float sdFisher(vec2 p)
{
    float body = sdRect(p, vec2(0.015, 0.05)) - 0.008;
    float head = sdCircle(p - vec2(0, 0.09), 0.03);
    float rod = sdRect((p - vec2(0.05, 0.03)) * rotMat(-1.0), vec2(0.004, 0.1));
    float line = sdRect(p - vec2(0.13, -0.17), vec2(0.002, 0.25));

    return opUnion(opUnion(body, head), opUnion(rod, line));
}

float sdHook(vec2 p, float s)
{
    float base = opUnion(sdCircle(p, s * 0.7), sdRect(p - vec2(0, -1.5) * s, vec2(0.3, 1.0) * s));
    float hookCircle = opOnion(sdCircle(p - vec2(0, -3.3) * s, s), 0.2 * s);
    float mask = sdRect(p - vec2(-1.2, -3) * s, vec2(1, 1.0) * s);
    float hook = opSubtraction(hookCircle, mask);
    return opUnion(base, hook);
}

vec2 flipX(vec2 v) { return vec2(-v.x, v.y); }

float waterLevel(float xn, float t)
{
    return 0.7 + sin(iTime * 2.0 + xn * 2.3) * 0.03;
}

float waterGradient(float xn, float t)
{
    return 2.0 * cos(iTime * 2.0 + xn * 2.3) * 0.03;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // Pixel coordinates normalized by the screen height
    vec2 uvn = fragCoord/iResolution.y;
    // The screen width normalized by screen height;
    float nWidth = iResolution.x / iResolution.y;

    float water = waterLevel(uvn.x, iTime);

    vec3 col = vec3(0.79);
    if (uvn.y < water)
    {
        //col = vec3(0.193, 0.434, 0.8) * uv.y / waterLevel;
        col = vec3(0, 0.58, 0.58) * uvn.y / water;
    }

    float[] fish = float[](
        sdMovingFish(uvn - vec2(nWidth * 0.0, 0.12), 		0.2, iTime, 0.1, 3.0),
        sdMovingFish(flipX(uvn - vec2(nWidth * 0.9, 0.2)), 	0.12, iTime, 0.1, 2.0),
        sdMovingFish(uvn - vec2(nWidth * 0.3, 0.33), 		0.1, iTime, 0.3, 6.0),
        sdMovingFish(flipX(uvn - vec2(nWidth * 0.9, 0.4)), 	0.09, iTime, 0.12, 4.0),
        sdMovingFish(uvn - vec2(nWidth * 0.6, 0.47), 		0.08, iTime, 0.16, 2.3),
        sdMovingFish(flipX(uvn - vec2(nWidth * 0.7, 0.55)), 0.07, iTime, 0.15, 2.0),
        sdMovingFish(uvn - vec2(nWidth * 0.2, 0.46), 		0.06, iTime, 0.13, 2.0),
        sdMovingFish(uvn - vec2(nWidth * 0.1, 0.35), 		0.05, iTime, 0.17, 2.5),
        sdMovingFish(flipX(uvn - vec2(nWidth * 0.2, 0.43)), 0.04, iTime, 0.1, 2.7),
        sdMovingFish(flipX(uvn - vec2(nWidth * 0.7, 0.34)), 0.1, iTime, 0.08, 2.2),
        sdMovingFish(uvn - vec2(nWidth * 0.3, 0.23), 		0.1, iTime, 0.07, 2.3)
        );

    for (int i = 0; i < fish.length(); i++)
    {
        if (fish[i] < 0.0)
        {
            col = vec3(0);
        }
    }

    ///// Boat /////

    float boatX = nWidth / 2.0;
    float boatY = waterLevel(boatX, iTime) - 0.03;
    vec2 boatPos = vec2(boatX, boatY);
    mat2 boatRot = rotMat(waterGradient(boatX, iTime));

    vec2 fisherPos = (boatRot * vec2(0.10, 0.10)) + boatPos;
    fisherPos = round(fisherPos * iResolution.y) / iResolution.y; // Round to nearest pixel

    if (sdSail((uvn - boatPos) * boatRot, 0.1) < 0.0)
    {
        col = vec3(0.93);
    }

    if (sdFisher(uvn - fisherPos) < 0.0)
    {
        col = vec3(0);
    }

    if (sdHook(uvn - fisherPos - vec2(0.13, -0.42), 0.01) < 0.0)
    {
        col = vec3(0.5);
    }

    if (sdBoat((uvn - boatPos) * boatRot, 0.1) < 0.0)
    {
        col = vec3(0.99);
    }


    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv - vec2(0.5,0.5);
    vec2 z = p * 1.5;
    vec2 c = vec2(-0.745  + (sin(iTime*0.05)), 0.19 + (sin(iTime*0.05)));
    float t = 0.0;
    vec2 bo = vec2(9999.0,9999.0);
    float bl = 99999.0;
    vec2 wo = vec2(0.0,0.0);
    float wl = 0.0;
    float it = 0.0;
    float os = 0.0;
    for (int i = 0; i < 64; i ++)
    {
     	vec2 nz = vec2(z.x*z.x - z.y*z.y, (1.5 - cos(iTime * 0.033)) * z.x * z.y) +c;
        vec2 no = nz - p;
        float nl = length(no);
        os += nl;
        it += 1.0;
        if (nl > wl)
        {
        	wo = no;
            wl = nl;

        }
        if (nl < bl)
        {
        	bo = no;
            bl = nl;

        }

        float m2 = dot(z.x,z.x);
        if (m2 > 4.0)
        {
           // bl = 1.0;
        	break;   
        }
        z = nz;
        t += 1.0/63.0;
    }

	vec3 tintb = vec3(0.3,0.6,0.8);
    vec3 tintr = vec3(0.8,0.4,0.1);
    float avgo = os/it;

    vec3 col = mix(mix(vec3(t) , tintb, dot(bo,wo)+avgo) , mix(vec3(t) , tintr, sqrt(avgo)) , sqrt(bl));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define t mod(iTime, 100.)*4.
#define sat(v) clamp(v, 0., 1.)
#define spow(v, p) clamp(pow(v, p), 0., 1.)

const float pi = acos(-1.);
const int MAX_ITER = 100;
const float MAX_DIST = 40.;

struct ray {
  vec3 o, d;
};

struct material {
  vec3 diffuse, specular;
  float shine;
};

vec2 repeat(vec2 p, float n) { return mod(p-.5*n, n)-.5*n; }
float vmax(vec3 v) { return max(max(v.x, v.y), v.z); }
mat2 rot(float r) { return mat2(cos(r), sin(r), -sin(r), cos(r)); }

float torus(vec3 p, vec2 c) {
  vec2 q = vec2(length(p.xz)-c.x, p.y);
  return length(q)-c.y;
}

float dist(vec3 p) {
  vec3 sp = p;
  sp.xy = repeat(p.xy, 2.);
  sp.xz = repeat(p.xz, 4.8);
  sp.xz *= rot(t+p.y*8.)+.2*sin(t*.4);
  sp.xy *= rot(pi/2.);

  return torus(sp, vec2(1., .5));
}

ray camera(vec2 uv, vec3 eye, vec3 target, float zoom) {
  ray r;
  r.o = eye;
  vec3 f = normalize(target - eye);
  //vec3 s = normalize(cross(vec3(0,1,0), f));
  vec3 s = cross(vec3(0,1,0), f);
  vec3 u = cross(f, s);
  r.d = normalize(f*zoom + uv.x*s + uv.y*u);
  return r;
}

vec2 march(ray r) {
  float d = 0.;
  for(int i=0; i<MAX_ITER*5; i++) {
    float h = dist(r.o+r.d*d)*.1;
    if(h<0.001*d || d>MAX_DIST) return vec2(d,i);
    d+=h;
  }
  return vec2(d,MAX_ITER);
}


vec3 norm(vec3 p) {
  vec2 e = vec2(-0.001, 0.001);
  return normalize(e.xyy*dist(p+e.xyy) + e.yxy*dist(p+e.yxy) + e.yyx*dist(p+e.yyx) + e.xxx*dist(p+e.xxx));
}

vec3 phong(vec3 p, material m, vec3 eye, vec3 lightPos) {
  vec3 n = norm(p);
  vec3 l = normalize(lightPos-p);
  vec3 v = normalize(eye - p);
  vec3 rf = normalize(reflect(-l, n));

  vec3 diff = m.diffuse * max(0., dot(n, l));
  vec3 spec = m.specular * max(0., pow(dot(rf, v), m.shine));

  return diff + spec;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  vec2 ruv = uv * (1.-uv);
  float vig = pow(ruv.x*ruv.y * 15., .8);;
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1.);

  uv *= 1.+length(uv)*2.;

  uv.y = abs(uv.y);
  vec3 eye = vec3(0,1,-2);
  eye.xz += .1*vec2(sin(cos(t*.05)*8.), cos(sin(t*.04)*12.));
  vec3 target = vec3(0.,1.+2.*sin(t*.1),0.);

  ray r = camera(uv, eye, target, .3+sin(t*.2)*.04);
  vec2 m = march(r);
  if(m.x > MAX_DIST) {
    fragColor = vec4(0.);
    return;
  }

  vec3 p = r.o+r.d*m.x;

  float halo = spow(.5*m.y/float(MAX_ITER), 2.);

  vec3 lightPos = vec3(3,3,-3);
  lightPos.xz += 20.*vec2(sin(t*.25), cos(t*.5));

  material mta;
  mta.diffuse = vec3(.14, .3, .9);
  mta.specular = vec3(1, 0, 0);
  mta.shine = 5.;

  vec3 color = phong(p, mta, eye, lightPos );
  color *= halo*20.;
  color = color*sat(exp(-m.y)) + color*halo;

  color *= vig;
  fragColor = vec4(pow(color, vec3(1./2.2)), 1.);
}

#define PI 3.141592654

vec2 ToPolar(vec2 pos)
{
   	float r = length(pos);
    // a (0, 2*PI)
    float a = atan(-pos.y,-pos.x)+PI;
    return vec2(r,a);
}

vec2 Rotate(vec2 pos, float angle)
{
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle))*pos;
}

float Shake(float from,float to,float rate)
{
    return from + (to-from)*(sin(iTime*rate)*0.5+0.5);
}

vec3 Rgb2hsb(in vec3 c )
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from Iñigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 Hsb2rgb(in vec3 c )
{
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}

// Draw a piece of leaf points to RIGHT ((0,0)->(1,0))
// width (0.,1.)
float DrawLeaf(vec2 pos, float radius, float width)
{
    // width = 1 -> angle = 2*PI

    vec2 polar = ToPolar(pos);
	// limit distance
    width *= smoothstep(radius,0.0,polar.x);
    // limit angle
    float w = abs(polar.y-PI)/PI;// 0 ~ 1 ~ 0
    float c = step(1.0-width,w)*0.2 +  smoothstep(1.0-width,1.0,w);

    return c*step(polar.x,radius);
}

// draw several patterns
float DrawComponents(vec2 pos, float radius)
{
    radius *= Shake(1.,1.5,1.);
    float width = Shake(0.15,0.85,1.5);
	return 
        DrawLeaf(pos, radius, width) + // Basic leaf
        0.3*DrawLeaf(pos, radius*1.5, pow(width*0.3,1.5)); 	// Thin Leaf
}

float Fbm(vec2 pos, float radius)
{
    const int n = 6;

    vec2 center = vec2(0.0);
    float angle = PI/4.*Shake(1.0,1.8,1.0);

    pos.y = abs(pos.y);
    float c = DrawComponents(pos,radius);
	for(int i = 0;i<n;i++)
    {
        // move to next position
        pos -= vec2(0.8,0.6)*Shake(0.0,0.6,0.5)*radius;
        pos = Rotate(pos,angle);
        pos.y = abs(pos.y);

        radius /= 2.*Shake(0.65,1.,1.2);
       	c += DrawComponents(pos,radius)*pow(0.5,float(i));
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = 500.0*(2.0*fragCoord - iResolution.xy)/iResolution.y;

    pos = Rotate(pos, iTime*0.5+Shake(1.,3.5,1.));

    const int n = 6;
    float c = 0.0;
    for(int i = 0;i<n;i++)
    {
    	pos = Rotate(pos,2.0*PI/(float(n)));
        c += Fbm(pos, 250.);
    }

    c = smoothstep(0.0,5.0,c)*2.0;

    vec2 polar = ToPolar(pos);

    vec3 backColor = Rgb2hsb(vec3(0.1,0.15,0.2)*0.3);
    backColor.x = fract(iTime*0.1)+smoothstep(0.0,1000.0,polar.x)*0.1;
    backColor = Hsb2rgb(backColor);

    // color it
    vec3 col = 
        backColor*pow(1.5-length(uv-vec2(0.5)),2.0) + 
        vec3(0.4,0.8,0.88)*pow(c,0.8);

    fragColor = vec4(col,1.0);
}

#define T iTime
vec4 shader(vec2 coord){float D=.01,r,c,s,a,b,h,q;
vec3 C=vec3(1),RD=normalize(vec3(coord.xy-0.5,1)),P=vec3(0,0,-2.),d,p,RP=vec3(2,0,0);
for (int i=0;i<99;++i){p=(P+=RD*(D*.7));
r=p.y*5.+T;
c=cos(r),s=sin(r);
a=min(q=length(P*vec3(.5,3.+sin(T)*.2,3.))-.5,length((mod(p,RP)-.5*RP)*vec3(2.5,1,1))-(1.1+.08*sin(T)));
p.xz*=mat2(c,s,-s,c);
d=abs(p*vec3(1,sin(T+P.y*2.),1))-vec3(.3,9,.3);
b=length(max(d,0.))-min(max(d.x,max(d.y, d.z)),0.);
h=clamp(.5+.5*(a-b)/.7,0.,1.);
D=mix(a,b,h)-.7*h*(1.-h);
if (D<.01) {
C=vec3((1.-max(.0, float(i)/90.))*.7);
C-=vec3(1,.4,.1)*max(.12,pow(max(dot(RD+vec3(.5*sin(T),0,0),normalize(P)),.001),.4));
C=1.-C;
break;}}
C = mix(C, vec3(0,0,1), max(0.0, pow(length(1.*RD.xy), 3.)));
return vec4(C,1);}

// Shadertoy entry point 
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = shader(uv);
}

float Band (float t, float start, float end, float blur){
  float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    float stepFinal = step1 * step2;
    return stepFinal;
}

float rectangle (vec2 uv, float blur, float left, float right, float bottom, float top) {
    float r;
    r = Band (uv.x, left, right, blur);
    r *= Band (uv.y, bottom, top, blur);
    return r;
}

float triangle1 (vec2 uv, float blur, float point) {
    float t = uv.y;
    // times with 2 to get the isoceles triangle shape
    return rectangle (uv*2., blur, -point+t, point-t, -point-t, point+t);
}

float triangle2 (vec2 uv, float blur, float point) {
    float t = uv.y*-1.;
    return rectangle (uv*2., blur, -point+t, point-t, -point-t, point+t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -=0.5; //move center
    uv.x*= iResolution.x/iResolution.y;
    float x = uv.x;
    float y = uv.y;
    float try;
    //vec3 col1, col2, col3;

    try = triangle1(vec2(x,y+.2), 0.001, .6);
    float tri1 = triangle2(vec2(x,y+.1), 0.0001, .3);
    float tri2 = triangle2(vec2(x-.2,y+.25), 0.001, .15);
    float tri3 = triangle2(vec2(x+.2,y+.25), 0.001, .15);
    float tri4 = triangle2(vec2(x,y-.15), 0.001, .15);
    float tri5 = triangle2(vec2(x,y-.275),0.001, .075);
    float tri6 = triangle2(vec2(x+.1,y-.075), 0.001, .075);
    float tri7 = triangle2(vec2(x-.1,y-.075), 0.001, .075);
    float tri8 = triangle2(vec2(x+.2,y+.125), 0.001, .075);
    float tri9 = triangle2(vec2(x-.2,y+.125), 0.001, .075);
    float tri10 = triangle2(vec2(x+.3,y+.325), 0.001, .075);
    float tri11 = triangle2(vec2(x+.1,y+.325), 0.001, .075);
    float tri12 = triangle2(vec2(x-.1,y+.325), 0.001, .075);
    float tri13 = triangle2(vec2(x-.3,y+.325), 0.001, .075);
    float tot = tri1 + tri2 + tri3 + tri4 + tri5 + tri6 + tri7 + tri8 + tri9 + tri10 + tri11 + tri12 + tri13;

    vec3 col = vec3(1); // bg color

    vec3 col1 = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)); // time varying color

    //col += vec3(1.0,1.0,0)*try;
    col -= col1*try; // color for big1 triangle
   // col += vec3(0);
    col += col1*tot; // color for inside triangle

	//col += col1 + col2;
    // Output to screen
    fragColor = vec4(col,1.0);
}

#define Tau 6.2831853071

vec4 pewd (vec2 b){
    b.x *= iResolution.x/iResolution.y;
    b.x += sin(b.y*3.)/15.* sin(.75 + iTime/3.5)*1.3 + iTime/28.;
    b.y += sin(b.x*4.)/3. - iTime/40. + sin(iTime/8.5);

    vec4 a = vec4(0);

    if (mod(b.y + b.y, .2) <= .1)
    a = vec4(.9608,0,.3647,1);

    return a;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = .5/iResolution.xy;
    vec4 a = vec4 (0);

    a += pewd(uv); //centered samples

    for (float i = 0.; i < Tau; i += Tau/10.) //radial samples
    {
        a += pewd(uv + 
                    vec2(sin(i),cos(i))*p
                    );
    }

    fragColor = a/11.;
}

#define MAX_STEPS 128
#define STEP_SIZE 0.05
#define _FoV 45.0

#define JITTER

#define ROTATE

const vec3 _Absorption = vec3(0.9,0.7,0.5);
const vec3 _Emission = vec3(0.3,0.15,0.0);
const vec3 _Light = vec3(1.0,1.0,1.0);
const vec3 _LightPos = vec3(0.0, 0.0, 0.0);
const float _LightIntensity = 0.2;
const float _Density = 9.0;
const float _Radius = 1.0;
const float _Mie = 0.6;
const float _G = 0.2;

//Cornette-Shanks phase function
float phase(float mu, float g)
{
	float g2 = g * g;
	return (3.0 * (1.0 - g2) * (1.0 + mu * mu)) / (2.0 * (2.0 + g2) * pow(1.0 + g2 - 2.0 * g * mu, 1.5));
}

//Ray-sphere intersection
bool raycastSphere(vec3 ro, vec3 rd, out vec3 p0, out vec3 p1, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        float t0 = (-B - D)/(2.0 * A);
        float t1 = (-B + D)/(2.0 * A);
        p0 = ro + rd * t0;
        p1 = ro + rd * t1;
        return true;
    }
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

//iq's gradient noise
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

//Otavio Good's fast spiral noise from https://www.shadertoy.com/view/ld2SzK
const float nudge = 0.739513;	// size of perpendicular vector
float normalizer = 1.0 / sqrt(1.0 + nudge*nudge);	// pythagorean theorem on that perpendicular to maintain scale
float SpiralNoiseC(vec3 p)
{
    float n = 0.0;	// noise amount
    float iter = 1.0;
    for (int i = 0; i < 6; i++)
    {
        // add sin and cos scaled inverse with the frequency
        n += -abs(sin(p.y*iter) + cos(p.x*iter)) / iter;	// abs for a ridged look
        // rotate by adding perpendicular and scaling down
        p.xy += vec2(p.y, -p.x) * nudge;
        p.xy *= normalizer;
        // rotate on other axis
        p.xz += vec2(p.z, -p.x) * nudge;
        p.xz *= normalizer;
        // increase the frequency
        iter *= 1.733733;
    }
    return n;
}

//Fractal (pink) noise function S(f) ∝ 1/f^α, {0 <= α <=2}
float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    float j = 1.0;
    for (int i = 0; i < octaves; i++, j+=1.0)
    {
        val += noise(seed * freq * j) / pow(j, lac);
    }

    return val;
}

//Density distribution function
float sampleVolume(vec3 pos)
{
    float rr = dot(pos,pos);
    rr = sqrt(rr);
    float f = exp(-rr);
    float p = f * _Density;

    if (p <= 0.0)
        return p;

    p += SpiralNoiseC(512.0 + pos * 8.0) * 0.75;
    pos = rotateY(pos, pos.y * SpiralNoiseC(pos * 4.0)* 2.0);
    p += SpiralNoiseC(200.0 + pos * 3.0) * 1.5;
    p *= rr/_Radius;

    p = max(0.0,p);

    return p;
}

vec4 raymarch(vec3 pos, vec3 dir, float ds, int s)
{
    vec4 result = vec4(0.,0.0,0.0,1.0);
    int steps = min(s, MAX_STEPS);
    for (int i = 0; i < steps; i++)
    {
        float p = sampleVolume(pos);
        if (p > 0.0)
        {
            vec3 r = _LightPos - pos;
            float atten = _LightIntensity/dot(r, r);
            vec3 ext = max(vec3(0.000001), (_Absorption * p) + vec3(_Mie * p));
            vec3 trans = exp(-ext * ds);
            vec3 lightDir = normalize(r);
            float mu = dot(lightDir, dir);
            float phase = phase(mu, _G);
            vec3 lum = _Emission + _Light * phase * (1.0-_Absorption) * _Mie * p * atten;
            vec3 integral = (lum - (lum*trans))/ext;

            result.rgb += integral * result.a;
            vec3 div = vec3(0.3333333);
            result.a *= dot(trans, div);

            if (result.a <= 0.1)
                return result;
        }

        pos += dir * ds;
    }

    return result;
}

//4x4 Bayer matrix for ordered dithering
const mat4 _Bayer4x4 = mat4(vec4(0,0.5,0.125,0.625),
                        vec4(0.75,0.25,0.875,.375), 
                        vec4(0.1875,0.6875,0.0625,0.5625), 
                        vec4(0.9375,0.4375,0.8125,0.3125));

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = iMouse.y/iResolution.y;
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0 + (5.0 * zoom));
    //Compute eye vector from field of view
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));


    #ifdef ROTATE
	float t = iMouse.x * 0.01 + iTime * 0.1;
    #else
    float t = iMouse.x * 0.01;
    #endif

    rayDir = rotateY(rayDir, t);
    rayOrigin = rotateY(rayOrigin, t);

    vec4 col = vec4(0.0,0.0,0.0,1.0);
    //col.rgb += smoothstep(0.45, 0.9, abs(noise(rayDir * 256.0)));
    float star = smoothstep(0.45, 0.8, abs(noise(rayDir * 256.0)));
    col.rgb += star * mix(vec3(1.0,0.7,0.2),vec3(0.0,0.5,1.0), star);

    vec3 p0, p1;
    if (raycastSphere(rayOrigin, rayDir, p0, p1, vec3(0.0), _Radius))
    {   
        p0 = (dot(rayOrigin, rayOrigin) < dot(p0, p0)) ? rayOrigin : p0;
        #ifdef JITTER
        //Bayer matrix ordered depth jittering
        float width = (uv.x * iResolution.x);
        float height = (uv.y * iResolution.y);
        width = mod(width, 4.0);
        height = mod(height, 4.0);
        float offset = _Bayer4x4[int(width)][int(height)];
        p0 -= rayDir * offset * STEP_SIZE*2.0;
        #endif

        float dist = length(p1 - p0);
        int s = int(dist/STEP_SIZE) + 1;

        vec4 integral = raymarch(p0, rayDir, STEP_SIZE, s);

        col.rgb = mix(integral.rgb, col.rgb, integral.a);
    }	

    fragColor = col;
}

// Ecolocating the direction of a pulse with four virtual microphones


const float PI = 3.141592;
const vec2 SOURCE = vec2(38.0, 26.0);

const vec2 MIC0 = vec2(-1.0, 1.0);
const vec2 MIC1 = vec2(-1.0, -1.0);
const vec2 MIC2 = vec2(1.0, 1.0);
const vec2 MIC3 = vec2(1.0, -1.0);
const vec2 MIC4 = vec2(0.0, 0.0);

uniform float time;
uniform vec2 mouse;
uniform vec2 resolution;

float cmag(float dist) {
	float phase = dist*2.0 - iTime*10.0;
	float mp = mod(phase, PI * 30.0);
	return (mp > 0.0 && mp < PI*1.0) ? sin(phase)/*(dist*dist)*/ : 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	float aspect = iResolution.x / iResolution.y;
	vec2 pos = ( fragCoord.xy / iResolution.xy );
	pos -= 0.5;
	pos.x *= aspect;
	pos *= 60.;

	float mag = cmag(length(pos+SOURCE));
	float mag0 = cmag(length(MIC0-SOURCE)+length(pos+MIC0));
	float mag1 = cmag(length(MIC1-SOURCE)+length(pos+MIC1));
	float mag2 = cmag(length(MIC2-SOURCE)+length(pos+MIC2));
	float mag3 = cmag(length(MIC3-SOURCE)+length(pos+MIC3));
	float mag4 = cmag(length(MIC4-SOURCE)+length(pos+MIC4));

	vec3 color = vec3( mag );
	color += vec3( mag0, mag1, mag2 );
	color += vec3( mag3, mag3, 0 );
	color += vec3( 0, mag4, mag4 );

    if (mag0+mag1+mag2+mag3+mag4 > 4.8) color = vec3(0.0);
	else if (mag0+mag1+mag2+mag3+mag4 > 4.5) color *= 1.0;
	else color /= 6.0;

    if (length(pos-SOURCE) < 0.2) color = vec3(1.0,0.0,0.0);
	if (length(pos+SOURCE) < 0.2) color = vec3(1.0,0.0,0.0);
	if (length(MIC0+pos) < 0.2) color = vec3(1.0,0.0,0.0);
	if (length(MIC1+pos) < 0.2) color = vec3(0.0,1.0,0.0);
	if (length(MIC2+pos) < 0.2) color = vec3(0.0,0.0,1.0);
	if (length(MIC3+pos) < 0.2) color = vec3(1.0,1.0,0.0);
	if (length(MIC4+pos) < 0.2) color = vec3(0.0,1.0,1.0);

	fragColor = vec4( color, 1.0 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    //noise 
    float noise = fract(sin(dot(uv.xy ,vec2(12.9898,78.233))) * 43758.5453);

    //alter the normalized pixel coordinates so its more centered to the screen
    uv -= .5;

    //alter the shape along the y or x axis (choice is yours).
    //uv.x makes it more circular. uv.y makes it a stretched, thinner oval. 
    uv.y *= (iResolution.x / 1.185 )/(iResolution.y * 2.5);

    //stretch the shape along the uv x axis 
    uv.x *= 5.*cos(iTime / 2.)  + noise;
    //stretch the shape along the uv y axis
    uv.y *= -5. * sin(iTime / 2.) + noise;

    //gets the length of the vector of normalized pixel coordinates
    float leng= length(uv);

    //if length value is less than 0.3
    if(leng < 0.3)
        //make it white
        leng = 1.;

    //this adds another range of colour (grey)
    else if (0.3 < leng && leng <= .6)
        leng = 0.5;
    else
        //make it black
        leng = 0.;

    // Output to screen
    fragColor = vec4(vec3(leng * col),1.0);
}

// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float f (vec3 p)
{
    return sdTorus(p,vec2(1.,.5));
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( vec3 p, float t )
{
    float e = 1.e-3*t;
    vec2 h = vec2(1.0,-1.0)*0.5773;
    return normalize( h.xyy*f( p + h.xyy*e ) + 
					  h.yyx*f( p + h.yyx*e ) + 
					  h.yxy*f( p + h.yxy*e ) + 
					  h.xxx*f( p + h.xxx*e ));
}

vec3 bg (vec2 p)
{    
    return normalize(vec3(fract(fract(p*.5)+sin(p.yx)),1.3)*.5-.5);
}

vec3 lambert (vec3 c, vec3 n, vec4 l)
{
	return max(0.,dot(n,l.xyz))*c*l.w;
}

void mainImage( out vec4 c, in vec2 a )
{
    vec2 s = (a-.5*iResolution.xy)/iResolution.x;
    vec3 o = vec3(0,3.,0);
    vec3 y = vec3(0,1,0);
    vec3 w = normalize(-o);
    if (abs(dot(o,y)) > .999) y = y.yxx;
    vec3 u = normalize(cross(w,y));
	vec3 d = mat3(u,cross(u,w),w) * normalize(vec3(s,.25));
    float t = 0., e = 1.e-4, h = 1.;
    vec3 p = o+d*t;
    float m = 1.e6;
    while (t < m)
    {
        h = f(p);
        if (h < e) break;
        t = min(m,t+h);
        p = o+d*t;
    }
    vec4 l = vec4(normalize(vec3(cos(iTime*.2),1,sin(iTime*.2))),1);
    vec3 k = calcNormal(p,t);
    vec3 n = t > e && t < m ? lambert(vec3(.025),k,l) : lambert(vec3(.5),-bg(10.*s),l);
    c = vec4(sqrt(n),1);
}

mat2 rotate(float a)
{
    return mat2(cos(a), sin(a), -sin(a), cos(a));
}

float pattern(vec2 p)
{
    p *= 15.0;
    return abs(sin(p.x) + sin(p.y));
}

float boxmap(vec3 p)
{
    p *= 0.3;
    vec3 m = pow(abs(normalize(p)), vec3(20));
    vec3 a = vec3(pattern(p.yz),pattern(p.zx),pattern(p.xy));
	return dot(a,m)/(m.x+m.y+m.z);
}

vec3 smin(vec3 a, vec3 b)
{
    float k = 0.08;
	vec3 h = clamp( 0.5 + 0.5*(b-a)/k, 0.0, 1.0 );
	return mix( b, a, h ) - k*h*(1.0-h);
}

vec3 sabs(vec3 p)
{
 	return  p - 2.0 * smin(vec3(0), p); 
}

float map(in vec3 p)
{  
    float s = 3.7;
    float amp = 1.0/s;
    float c = 0.5;
    p = sabs(mod(p, c * 2.0) - c); 
    float de = 100.;
    for(int i=0; i<3; i++){
        p.xy *= rotate(0.4+sin(iTime*0.2+ 0.3*sin(iTime*0.4))*0.2);
        p.yz *= rotate(0.4+sin(iTime*0.3+ 0.5*sin(iTime*0.5))*0.2);
        p = sabs(p); 
        p *= s;
        p -= vec3(0.2*p.z, 0.6*p.x, 0.4) * (s - 1.0);
	    de = abs(length(p*amp) - 0.2) ;
        amp /= s;
    }   
    return de + boxmap(p) * 0.02 - 0.01;
}

vec3 calcNormal(vec3 p){
  vec2 e = vec2(1, -1) * 0.001;
  return normalize(
    e.xyy*map(p+e.xyy)+e.yyx*map(p+e.yyx)+ 
    e.yxy*map(p+e.yxy)+e.xxx*map(p+e.xxx)
  );
}

vec3 doColor(vec3 p){
	return vec3(0.2,0.9,0.2) * boxmap(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 ro = vec3(0.2, 0.1, 0.5)+iTime*0.1;
    vec3 rd = normalize(vec3(p, 2));
    rd.xz *= rotate(sin(iTime*0.3)*0.6);
    rd.yz *= rotate(sin(iTime*0.2)*0.6);
    rd.xy *= rotate(sin(iTime*0.05));   
    vec3 col = mix(
        vec3(0.3, 0.7, 0.8),
        vec3(0.1, 0.1, 0.2),
        smoothstep(0.3, 2.5, length(p)));    
    float t = 0.1, d;
 	for(int i = 0; i < 300; i++)
  	{
        d = map(ro + rd * t);
    	t += 0.1 * d;
    	if(d < 0.001 || t > 5.0) break;
  	}
  	if(d < 0.001)
  	{
	  	vec3 p = ro + rd * t;
	 	vec3 nor = calcNormal(p);
    	vec3 li = normalize(vec3(1));
        vec3 c = doColor(p);
        c *= clamp(dot(nor, li), 0.3, 1.0);
        c *= max(0.5 + 0.5 * nor.y, 0.0);
        c += pow(clamp(dot(reflect(normalize(p - ro), nor), li), 0.0, 1.0), 20.0);
        c.x +=1.0- exp(-t*t*0.15);
        c = clamp(c,0.0,1.0);
        col = mix(col,c,exp(-t*t*0.6));
    }
  	col = pow(col, vec3(0.8));
    col= mix(col, vec3(col.x), clamp(sin(iTime*0.5 + sin(iTime*0.2)*0.5)*2.0-1.0, 0.0, 1.0));
    fragColor = vec4(col, 1.0);
}

