vec3 cell_delta = vec3(-1,0,1);

vec3 lastCell = vec3(0);
vec2 rotate(vec2 v, float angle) { return cos(angle)*v+sin(angle)*vec2(v.y,-v.x);}
float rand(vec2 co) { return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);}
vec2 rand2(vec2 co) { return fract(sin(vec2(dot(co.xy ,vec2(12.9898,78.233)),dot(co.yx,vec2(13.1898,73.231)))) * 43758.5453);}
float rand3(vec3 co) { return fract(sin(dot(co.xyz ,vec3(12.9898,78.233, 49.566))) * 43758.5453);}
mat3 lookat(vec3 fw) { fw=normalize(fw);vec3 rt=normalize(cross(fw,vec3(0.0,1.0,0.0)));return mat3(rt,cross(rt,fw),fw);}

vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

vec3 vpos(vec3 cell) {
    vec3 angle = iTime*cos(cell*123.93);
    vec3 innerPos = cos(angle+rand3(cell))*0.5+.5;
    return cell+innerPos;
}

SDF_RESULT SDF(vec3 ro, vec3 rd) {
    vec4 d1 = vec4(MAX_DIST);
    vec4 d2 = vec4(MAX_DIST);
    vec4 d3 = vec4(MAX_DIST);
    ro -= .5;
    for(int dx = -1; dx <= 1; dx++)
    for(int dy = -1; dy <= 1; dy++)
    for(int dz = -1; dz <= 1; dz++) {
        vec3 local_cell = floor(ro)+vec3(dx,dy,dz);
        vec3 pos = vpos(local_cell);
        float dist = length(pos-ro);

        if(dist < d1.w) {
            d3 = d2;
            d2 = d1;
            d1 = vec4(pos, dist);
            lastCell = local_cell;
        }else if(dist < d2.w) {
            d3 = d2;
            d2 = vec4(pos, dist);
        }else if(dist <d3.w) {
            d3 = vec4(pos, dist);
        }
    }

    //vec3 mid = (d1+d2+d3).xyz/3.0;
    //vec3 dir = normalize(cross((d2-d1).xyz, (d3-d1).xyz));
    //return vec2(length(cross(dir,mid-ro))-.01,0);

    //return vec2( length(ro-d1.xyz-.5)-.1 );
    //return vec2(length(ro-mid)-.1,0);
    return vec2(max(d2-d1,d3-d2).w-.01,0);
}

vec3 EstNormal(vec3 ro, vec3 rd) {
    float cx = SDF(ro+normal_delta.xyy, rd).x-SDF(ro-normal_delta.xyy, rd).x;
    float cy = SDF(ro+normal_delta.yxy, rd).x-SDF(ro-normal_delta.yxy, rd).x;
    float cz = SDF(ro+normal_delta.yyx, rd).x-SDF(ro-normal_delta.yyx, rd).x;
    return normalize(vec3(cx,cy,cz));
}

vec3 scene(vec3 ro, vec3 rd, vec2 uv) {
    vec3 p = ro;
    float t;
    float d;
    float i = 0.0;
    float c = 1.0;
    vec3 pos;
    SDF_RESULT result;
    for(; i < ITR ; i++) {
        result = SDF(pos=ro+rd*t,rd);
           result *= .5;
        t += d = result.x;
        c = min(d,c);
        if(t > MAX_DIST || d < EPS) break;
    }
   	vec3 cell = lastCell;
    vec3 normal = EstNormal(pos, rd);
    vec3 color = hsv2rgb(vec3(rand3(cell),1,1));
    if (i + 1. <= ITR)
    	return color*dot(normal,-rd)/sqrt(t);
    else
        return vec3 (0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float tim=iTime;
	vec2 uv=fragCoord.xy/iResolution.xy;
	tim*=0.5;
	vec3 ro=vec3(cos(tim),cos(tim*0.3)*0.5,cos(tim*0.7))*min(0.5+tim*0.1+cos(tim*0.4)*0.5,1.5);
	vec3 rd=lookat(-ro)*normalize(vec3((fragCoord.xy-0.5*iResolution.xy)/iResolution.y,1.0));

    ro.yz *= .2;
    ro.yz += .5;
