	vec3 color=scene(ro,rd,fragCoord.xy);
	color=clamp(color,0.0,min(tim,1.0));
	fragColor = vec4(color,1.0);
}

// Simple 2d Ring / RingSegment functions - Del 20/01/2019
// updated & added pattern variation...

#define	PI 3.141592
#define	TAU 6.283185

float remap(float s, float a1, float a2, float b1, float b2)
{
    return b1 + (s-a1)*(b2-b1)/(a2-a1);
}

// out: 0->val->0
float SmoothTri2(float t, float val)
{
    return val * (1.0-(0.5+cos(t*TAU)*0.5));
}

// Ring
float ring(vec2 uv, float rad, float thickness)
{
    return abs(rad - length(uv)) - thickness;
}

// Ring Segment (start 0-1, len 0-1)
float ringseg(vec2 uv, float rad, float thickness,float segstart, float seglen)
{
    float d = ring(uv,rad,thickness);
    float rr = fract((atan(uv.x, uv.y)/TAU)+segstart);
    return seglen > rr ? d : 1.0;
}

// Ring Segment pattern
float ringsegpat(vec2 uv, float rad, float thickness,float segstart, float seglen,float pat)
{
    float rr = fract((atan(uv.x, uv.y)/TAU)+segstart);
    float v1 = remap(rr, 0.0, seglen, 0.0, pat);
    thickness *= sin(v1*PI);			// example pattern 1
    //thickness *= SmoothTri2(v1,1.0);	// example pattern 2
    thickness *= fract(v1);				// example pattern 3
    float d = abs(rad - length(uv)) - thickness;
    return seglen > rr ? d : 1.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (2. * fragCoord.xy - iResolution.xy ) / iResolution.y;
    // ring segments
    float s = fract(iTime*0.3);
    float r1 = ringseg(uv,0.5,0.025,s,0.2);
    float r2 = ringseg(uv,0.4,0.025,-s,0.2);
    // whole ring
    float r3 = ring(uv,0.6,0.025);
    // pattern ring
    float r4 = ringsegpat(uv,0.8,0.1,fract(iTime*0.1),1.0,16.0);
    // combine
    float d = min(r4,min(r3,min(r1,r2)));
    float c = smoothstep(0.0,0.01,d);

    vec4 backgroundcol = vec4(0.2,0.2,0.2,1.0);
    vec4 imagecol = vec4(0.7,0.6,0.9,1.0);

    fragColor = mix(imagecol,backgroundcol,c);
}

// --------------------------------------------------------
// HG_SDF
// https://www.shadertoy.com/view/Xs3GRB
// --------------------------------------------------------

#define PI 3.14159265359

void pR(inout vec2 p, float a) {
    p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

float smax(float a, float b, float r) {
    vec2 u = max(vec2(r + a,r + b), vec2(0));
    return min(-r, max (a, b)) + length(u);
}


// --------------------------------------------------------
// Spectrum colour palette
// IQ https://www.shadertoy.com/view/ll2GD3
// --------------------------------------------------------

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos( 6.28318*(c*t+d) );
}

vec3 spectrum(float n) {
    return pal( n, vec3(0.5,0.5,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.0,0.33,0.67) );
}


// --------------------------------------------------------
// Main SDF
// https://www.shadertoy.com/view/wsfGDS
// --------------------------------------------------------

vec4 inverseStereographic(vec3 p, out float k) {
    k = 2.0/(1.0+dot(p,p));
    return vec4(k*p,k-1.0);
}

float fTorus(vec4 p4) {
    float d1 = length(p4.xy) / length(p4.zw) - 1.;
    float d2 = length(p4.zw) / length(p4.xy) - 1.;
    float d = d1 < 0. ? -d1 : d2;
    d /= PI;
    return d;
}

float fixDistance(float d, float k) {
    float sn = sign(d);
    d = abs(d);
    d = d / k * 1.82;
    d += 1.;
    d = pow(d, .5);
    d -= 1.;
    d *= 5./3.;
    d *= sn;
    return d;
}

float time;

float map(vec3 p) {
    float k;
    vec4 p4 = inverseStereographic(p,k);

    pR(p4.zy, time * -PI / 2.);
    pR(p4.xw, time * -PI / 2.);

    // A thick walled clifford torus intersected with a sphere

    float d = fTorus(p4);
    d = abs(d);
    d -= .2;
    d = fixDistance(d, k);
    d = smax(d, length(p) - 1.85, .2);

    return d;
}


// --------------------------------------------------------
// Rendering
// --------------------------------------------------------

mat3 calcLookAtMatrix(vec3 ro, vec3 ta, vec3 up) {
    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww,up));
    vec3 vv = normalize(cross(uu,ww));
    return mat3(uu, vv, ww);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    time = mod(iTime / 2., 1.);

    vec3 camPos = vec3(1.8, 5.5, -5.5) * 1.75;
    vec3 camTar = vec3(.0,0,.0);
    vec3 camUp = vec3(-1,0,-1.5);
    mat3 camMat = calcLookAtMatrix(camPos, camTar, camUp);
    float focalLength = 5.;
    vec2 p = (-iResolution.xy + 2. * gl_FragCoord.xy) / iResolution.y;

    vec3 rayDirection = normalize(camMat * vec3(p, focalLength));
    vec3 rayPosition = camPos;
    float rayLength = 0.;

    float distance = 0.;
    vec3 color = vec3(0);

    vec3 c;

    // Keep iteration count too low to pass through entire model,
    // giving the effect of fogged glass
    const float ITER = 82.;
    const float FUDGE_FACTORR = .8;
    const float INTERSECTION_PRECISION = .001;
    const float MAX_DIST = 20.;

    for (float i = 0.; i < ITER; i++) {

        // Step a little slower so we can accumilate glow
        rayLength += max(INTERSECTION_PRECISION, abs(distance) * FUDGE_FACTORR);
        rayPosition = camPos + rayDirection * rayLength;
        distance = map(rayPosition);

        // Add a lot of light when we're really close to the surface
        c = vec3(max(0., .01 - abs(distance)) * .5);
        c *= vec3(1.4,2.1,1.7); // blue green tint

        // Accumilate some purple glow for every step
        c += vec3(.6,.25,.7) * FUDGE_FACTORR / 160.;
        c *= smoothstep(20., 7., length(rayPosition));

        // Fade out further away from the camera
        float rl = smoothstep(MAX_DIST, .1, rayLength);
        c *= rl;

        // Vary colour as we move through space
        c *= spectrum(rl * 6. - .6);

        color += c;

        if (rayLength > MAX_DIST) {
            break;
        }
    }

    // Tonemapping and gamma
    color = pow(color, vec3(1. / 1.8)) * 2.;
    color = pow(color, vec3(2.)) * 3.;
    color = pow(color, vec3(1. / 2.2));

    fragColor = vec4(color, 1);
}

#define MAX_ITERATIONS 1000
#define COLLISION_DISTANCE  0.00001
#define MAX_DISTANCE 400.0

float Sphere(vec3 p, vec3 pos, float radius)
{
    return length(p-pos) - radius;
}

float Plane(vec3 p, float height)
{
    return abs(p.y - height);
}

float Cube(vec3 p, vec3 pos, vec3 size)
{
    p -= pos;  

    return length(max(abs(p) - size,0.0));
}

float Intersect(float a, float b)
{
    return max(a,b);
}

float Difference(float a, float b)
{
    return Intersect(a, -b);
}

float Union(float a, float b, vec3 colorA, vec3 colorB, out vec3 color)
{
    if(a <= b)
    {
        color = colorA;
        return a;
    }

    color = colorB;
    return b;
}

vec3 TransformPoint(vec3 p, vec3 centerOfRotation, vec3 rotation)
{
    rotation *= 0.017453292519943295;

    vec4 p4 = vec4(p - centerOfRotation, 21.0);

    mat4 zyxRotation = mat4(
        vec4(cos(rotation.z) * cos(rotation.y), sin(rotation.z)*cos(rotation.x) + cos(rotation.z)*sin(rotation.y)*(rotation.x), sin(rotation.z)*sin(rotation.x) - cos(rotation.z)*sin(rotation.y)*cos(rotation.x), 0.0),
        vec4(-sin(rotation.z)*cos(rotation.y),cos(rotation.z)*cos(rotation.x)-sin(rotation.z)*sin(rotation.y)*sin(rotation.x), cos(rotation.z)*sin(rotation.x) + sin(rotation.z)*sin(rotation.y)*cos(rotation.x), 0.0),
        vec4(sin(rotation.y), -sin(rotation.x)*cos(rotation.y), cos(rotation.y)*cos(rotation.x), 0.0),
        vec4(0.0,1.0, 0.0, 1.0)); 

     p4 *= zyxRotation;

    return p4.xyz + centerOfRotation;          
}

float Test(vec3 p, vec3 piv,sampler2D samp, vec3 rot, out vec3 color)
{      

    p = TransformPoint(p, piv, rot);

    vec2 uv = p.xy - piv.xy;

    uv= (uv) / 2.0;

    uv = uv * vec2(0.5);
    uv /= 3.0;

    vec4 tex = vec4(0.15);

    if(abs(uv.x) < 1.0 && abs(uv.y) < 1.0)
    	tex = texture(samp,uv);
    color = tex.rgb;

    p.z += 1.1* (tex.r * 0.8 + tex.g * 0.5 + tex.b * 0.2);


    return  Cube(p, piv, vec3(5.1));
}



float Scene(vec3 p, out vec3 color)
{
   float inOutRotate 
    = p.x * sin(iMouse.y / 254.0) * 1.02;

    float d = Test(p, vec3(-7.5,6.6, 3.0),iChannel0,vec3(-15.0, -15.0, inOutRotate), color);



    vec3 col1;
    float d1 = Test(p, vec3(7.5,6.0, 4.0),iChannel1,vec3(-15.0, 15.0, inOutRotate), col1);

    d = Union(d,d1,color,col1, color);

    d1 =  Test(p, vec3(7.5,-6.0, 4.0),iChannel3,vec3(15.0 + 2.5, 15.0, -inOutRotate), col1);

    d = Union(d,d1,color,col1, color);

    d1 = Test(p, vec3(-8.5,-6.5, 2.0),iChannel2,vec3(15.0, -15.0, inOutRotate), col1);

    d = Union(d,d1,color,col1, color);

    return d;
}

float Raymarching(vec3 cameraPos, vec3 rd, out vec3 color)
{   
    float t = 0.0;
    for(int i=0;i<MAX_ITERATIONS;++i)
    {
        float d = Scene(cameraPos + rd * t, color);

        t += d;

        if(t > MAX_DISTANCE || d < COLLISION_DISTANCE)
            break;
    }

    return t;
}

vec3 GetNormal(vec3 p)
{
    vec2 e = vec2(COLLISION_DISTANCE * 0.111, 1.0);
    vec3 color;
    float d = Scene(p, color);

    vec3 n = vec3(
        d - Scene(p-e.xyy, color),
        d - Scene(p-e.yxy, color),
        d - Scene(p-e.yyx, color));

    return normalize(n);
}

float GetDiffuseLight(vec3 p, vec3 pos)
{
    vec3 normal = GetNormal(p);

    vec3 light = pos - p;

    return dot(normalize(light), normal);
}

vec3 GetShadow(vec3 p, vec3 light)
{
    vec3 dir = normalize(light - p);
    vec3 color;
    float d = Raymarching(p, dir, color);

    if(d < length(light - p))
        return vec3(0.1);

    return vec3(1.0);
}


float GetLight(vec3 p)
{
    vec3 pos1 = vec3(5.0 * sin(2.0 * iTime), 5.0 * cos(2.0 * iTime), -2.0); 

    float diff = 0.25 + 0.5 * clamp(GetDiffuseLight(p, pos1), 0.5, 1.0);

    return diff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    vec3 cameraPos = vec3(0.1,1.0, -30.0  );

    vec3 dir = vec3(normalize(vec3(uv.xy, 1.50 )));

    vec3 color = vec3(0.0) * dir;

    float d = Raymarching(cameraPos, dir, color);

    float light = GetLight( cameraPos +  d * dir);




    // post
    float vgn = smoothstep(1.1,1.5,abs(uv.y)) * smoothstep(1.1,1.5,abs(uv.x));
    color *= 1.8 - (0.44 - vgn) * 0.23;	

    fragColor = vec4(color * light, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y * 3.0 - 1.5;
    uv.x -= 1.0;
    float l = length(uv);
    float s = tan(atan(l)*2.0) / l;
    vec2 op = sin(uv * s * iTime - 1.0);


    // Time varying pixel color
    vec3 col = vec3(op.x, op.y, l/2.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

