void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;

     // camera movement	
	float an = 0.4*iTime;
	vec3 ro = vec3( 2.5*cos(an), 1.0, 2.5*sin(an) );
    vec3 ta = vec3( 0.0, 0.8, 0.0 );

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	// create view ray
	vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

    // rotate and translate box	
	mat4 rot = rotationAxisAngle( normalize(vec3(1.0,1.0,0.0)), iTime );
	mat4 tra = translate( 0.0, 1.0, 0.0 );
	mat4 txi = tra * rot; 
	mat4 txx = inverse( txi );

    // raytrace
	float tmin = 10000.0;
	vec3  nor = vec3(0.0);
	vec3  pos = vec3(0.0);

	// raytrace-plane
	float oid = 0.0;
	float h = (0.0-ro.y)/rd.y;

    if( h>0.0 ) 
	{ 
		tmin = h; 
		nor = vec3(0.0,1.0,0.0); 
		oid = 1.0;
	}

	// raytrace OBJ model;
    //rd.y = mod(rd.y,0.5)-0.25;

    int index = 0;
    float ypos = 1.0;
    for(int i = 0; i<numFaces/3; i++){
        int f1 = faces[index];
        int f2 = faces[index+1];
        int f3 = faces[index+2];

        vec3 v0 = vec3(vertices[(f1*3)-3],vertices[((f1*3)-3)+1],vertices[((f1*3)-3)+2]);
        vec3 v1 = vec3(vertices[(f2*3)-3],vertices[((f2*3)-3)+1],vertices[((f2*3)-3)+2]);
        vec3 v2 = vec3(vertices[(f3*3)-3],vertices[((f3*3)-3)+1],vertices[((f3*3)-3)+2]);
        v0.y+=ypos;
        v1.y+=ypos;
        v2.y+=ypos;

    	vec3 res = triIntersect( ro, rd, v0, v1, v2);
        if( res.x>0.0 && res.x<tmin )
        {
            tmin = res.x; 
            oid = 2.0;
        }
        index += 3;
    }

    // shading/lighting	
	vec3 col = vec3(0.9);
	if( tmin<100.0 )
	{
        vec3 lig = normalize(vec3(-0.8,0.4,0.1));
		pos = ro + tmin*rd;

        // material
		float occ = 1.0;
		vec3  mate = vec3(1.0);
		if( oid<1.5 ) // plane
		{
		    mate = vec3(0.0,0.3,0.0);
			occ = 0.2 + 0.8*smoothstep( 0.0, 1.5, length(pos.xz) );
		}			
		else // model
		{
		    mate = vec3(0.5,0.5,0.5)+tmin*0.05;
		}		
		mate = mate*mate*1.1;

		// lighting
		float dif = clamp( dot(nor,lig), 0.0, 1.0 );

		col = vec3(0.13,0.17,0.2)*occ*3.0 + 1.5*dif*vec3(1.0,0.9,0.8);

        // material * lighting		
		col *= mate;

        // fog
		col = mix( col, vec3(0.9), 1.0-exp( -0.003*tmin*tmin ) );
	}

	col = sqrt( col );

	fragColor = vec4( col, 1.0 );
}

