float Hash(vec3 uv) {
    float dt = dot(abs(uv)*sin(uv), vec3(3.2, 7.2, 5.0));
    float sn = dot(dt, 3.1415926);
    return fract(sin(sn)*sin(sn) * 75.6);
}

float noise(vec3 p)
{
    vec2 add = vec2(1.0, 0.0);
	vec3 i = floor(p);
	vec3 f = fract(p); 
	f *= f * (3.0-2.0*f);

    return mix(
		mix(mix(Hash(i  			), 	Hash(i + add.xyy),f.x),
			mix(Hash(i + add.yxy),		Hash(i + add.xxy),f.x),
			f.y),
		mix(mix(Hash(i + add.yyx),    	Hash(i + add.xyx),f.x),
			mix(Hash(i + add.yxx), 		Hash(i + add.xxx),f.x),
			f.y),
		f.z);
}


float FBM(vec3 p) {
    vec3 coord = p;
    coord += 100.0;
	coord.x += iTime * 0.1;

float n  = noise(coord) * 0.5;   coord *= 3.0;
          n += noise(coord) * 0.3;  coord *= 2.1;
          n += noise(coord) * 0.2; coord *= 2.2;
          n += noise(coord) * 0.0625;


    return pow(abs(n), 1.155);

}

 float MapCloud(vec3 pos){

  float d = 1.0 - 0.3 * abs(3.8 - pos.y);

   d -= 2.2 * FBM( pos*0.3 );
   d = clamp( d, 0.0, 1.0 );

  return d; 
}

vec4 Clouds(vec3 rd,float tmax){


vec4 sum ;

 float t = 0.1;
 for (int i = 0; i < 64; i++) {

 vec3 pos = t*rd;

 float den = MapCloud(pos);

  float dif =  clamp((den - MapCloud(pos+0.5*normalize(vec3(0.0,1.0,0.0))))/0.6, 0.0, 1.0 );

  vec4 col = vec4( 1.0,1.0,1.0,den );

 vec3 lin = vec3(0.5,0.7,0.8)*1.4 + vec3(2.0, 0.6, 0.3)*dif;        

 col.xyz *= lin;

 col.xyz = mix( col.xyz, vec3(0.0,0.0,0.0), 1.0-exp(-0.0018*t*t) );

 col.a *= 1.0;
 col.rgb *= col.a;

  sum += col * (1.0 - sum.a);  
  t += max(0.1,0.05*t);
}

sum =  clamp( sum, 0.0, 1.0 );

return sum;
 }

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/ iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;


    vec3 ro = 4.0*normalize(vec3(sin(2.0*m.x), 0.1*m.y, cos(2.0*m.x)));
	vec3 ta = vec3(0.0, 0.0, 0.0);
    mat3 ca = setCamera( ro, ta, 0.0 );

    vec3 rd =  ca*normalize( vec3(p.xy,1.5));

    vec4 C = Clouds(rd,2000.0);

    C.a *= length(rd.y*2.5);

    vec4 sky = mix(vec4(0.125,0.45,0.9,1.0),vec4(0.85,0.85,0.85,1.0),0.45-rd.y);

    fragColor = mix(sky,C,C.a);
}

#define CS(a) vec2(cos(a),sin(a))

const float PI = 3.14159265359;


float oneLinerFor(in vec2 p, in float num) {
    float a = 2.0 * PI / num;
    return floor( mod( atan(p.y, p.x) + a / 2.0 - PI - iTime , 2.0 * PI) / a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 destColor = vec3(0.0);

    float j = oneLinerFor(p, 10.0);
    vec2 q = p + CS(iTime + j * 2.0 * PI / 10.0) * 0.8;
    destColor += 0.01 / abs(length(q) - 0.3);    

    // Output to screen
    fragColor = vec4(destColor,1.0);
}

#define PI 3.14159265359
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define combine(v1,v2) v1.w < v2.w ? v1 : v2
#define opSubtraction(d1,d2) max(-d1,d2)
#define hash(h) fract(sin(h) * 43758.5453123)

const float WallDistance = 0.03;
const float GlowDistance = 0.01874;
const float MarchDumping = 0.358;

// numbers 0123456789:
int c[] = int[](31599,9362,29671,29391,23497,31183,31215,29257,31727,31695,1040);

/* The glass like shader is based on https://www.shadertoy.com/view/Md3SDB */
// 2d distance functions from http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float dBox2d(vec2 p, vec2 b) {
	return max(abs(p.x) - b.x, abs(p.y) - b.y);
}

vec3 lineTex(vec2 uv)
{
    float stripeSize = 50.0;
    float t = iTime*10.0;
    return vec3(tan((uv.x+uv.y+(-t/stripeSize))*stripeSize)*stripeSize,tan((uv.x+uv.y+(-t/stripeSize))*stripeSize)*stripeSize,tan((uv.x+uv.y+(-t/stripeSize))*stripeSize)*stripeSize);
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
	vec2 pa = p-a, ba = b-a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h );
}

vec3 line( in vec3 buf, in vec2 a, in vec2 b, in vec2 p, in vec2 w, in vec4 col )
{
   float f = sdLine( p, a, b );
   float g = fwidth(f)*w.y;
   return mix( buf, col.xyz, col.w*(1.0-smoothstep(w.x-g, w.x+g, f)) );
}

float sdTriangle( in vec2 p0, in vec2 p1, in vec2 p2, in vec2 p )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float sdCapsule( vec3 p, float h, float r )
{
    p.z -= clamp( p.z, 1.0, h );
    return length( p ) - r;
}

float opElongate( in vec3 p, in vec2 size, in vec3 h )
{
    vec3 q = abs(p)-h;
    return sdTorus( max(q,0.0), size ) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec4 map(vec3 p){
    float speed = iTime*15.0;
    vec3 prevP = p;

    float cmnR = 0.035;
    float floorD = p.y+3.0;
    float sole0 = opElongate(p+vec3(-0.05,0,2.9), vec2(0.3,0.11), vec3(0.3,0.05,0.001));
   	sole0 = opSubtraction(length(p+vec3(0,0,2.6))-0.3, sole0)-cmnR;

    float sole1 = opElongate(p+vec3(0.05,0,1.8), vec2(0.35,0.11), vec3(0.6,0.05,0.001));
   	sole1 = opSubtraction(length(p+vec3(0.2,0,1.45))-0.3, sole1)-cmnR;
    sole1 = opSubtraction(length(p+vec3(0.1,0,2.15))-0.3, sole1);

    float sole2 = opElongate(p+vec3(0.1,0,0.7), vec2(0.4,0.11), vec3(0.75,0.05,0.001));
   	sole2 = opSubtraction(length(p+vec3(0.3,0,1.1))-0.3, sole2)-cmnR;

    float sole3 = opElongate(p+vec3(0,0,-0.5), vec2(0.35,0.11), vec3(0.6,0.05,0.001));
   	sole3 = opSubtraction(length(p+vec3(0,0,-1.1))-0.8, sole3)-cmnR;

    p.x = -abs(p.x);
    float sole4 = sdCapsule(p+vec3(0.7,0,-0.3),1.3,0.2);
    p = prevP;

    float sole5 = opElongate(p+vec3(0,0,-2.5), vec2(0.35,0.12), vec3(0.3,0.05,0.001));
   	sole5 = opSubtraction(length(p+vec3(0,0,-1.95))-0.7, sole5)-cmnR;

    vec4 res = combine(vec4(vec3(0.9,0.3,0.3),sole0),vec4(vec3(0.9,0.3,0.3),sole1));
    vec4 res2 = combine(vec4(vec3(0.9,0.3,0.3),sole2),vec4(vec3(0.9,0.3,0.3),sole3));
    vec4 res3 = combine(vec4(vec3(0.9,0.3,0.3),sole4),vec4(vec3(0.9,0.3,0.3),sole5));
    vec4 res4 = combine(res,res2);
    vec4 res5 = combine(res3,res4);

    return res5;
}

float getBit(int num,int bit)
{
	return float( num>>bit & 1);
}

float Sprite3x5(int sprite,vec2 p)
{
	float bounds = float(all(lessThan(p,vec2(3,5))) && all(greaterThanEqual(p,vec2(0,0))));
	return getBit(sprite,int((2.0 - p.x) + 3.0 * p.y)) * bounds;
}

float Digit(int n,vec2 p)
{
	n = int(mod(float(n),11.0));
    return Sprite3x5(n == 0 ? c[0] : n == 1 ? c[1] : n == 2 ? c[2] : n == 3 ? c[3] : n == 4 ? c[4] : n == 5 ? c[5] : n == 6 ? c[6] : n == 7 ? c[7] : n == 8 ? c[8] : n == 9 ? c[9] : n == 10 ? c[10] : 0,p);
}

