	uicol = line( uicol, vec2(0.2, 0.0), vec2(-0.2, 0.0), uvRef, vec2(0.002,1.0), vec4(white,2.0) );

    // guage
    float guageBg = dBox2d(uv+vec2(-1.0, -0.5), vec2(0.11,0.36));
