    float ay2 = sin(iTime*2.1)*0.06;

    float rotVal = radians(-iTime*30.0);
    mat2 m = rot(rotVal);

	float td = sdTriangle(vec2(0.12+ax,0.12+ay)*m,vec2(-0.12+ax2,0.12+ay2)*m,vec2(-0.12+ax,-0.12+ay)*m,uv+vec2(-1.0,0.55));
