vec2 scale(vec2 st, vec2 n) {
	return st * n;
}

vec2 rotate(vec2 st, float a) {
    st = mat2(cos(a),-sin(a),
              sin(a),cos(a))*(st-.5);
    return st;
}

float fill(float x, float size) {
    return 1. - step(size, x);
}

float rectSDF(vec2 st, vec2 s) {
	return max(abs(st.x/s.x), abs(st.y/s.y));
}

float circleSDF(vec2 st) {
	return length(st - .5) * 2.;
}


float circleSDF2(vec2 st, float r) {
    float v = (length(st) - r);
	return v * 2.;
}

float starSDF2(vec2 uv, int V, float s) {
	// st = st * 4. - 2.;
    vec2 st = uv;
    float a = atan(st.y, st.x) / TAU;
    float seg = a * float(V);
    a = ((floor(seg) + 0.5)/float(V) + mix(s, -s, step(.5, fract(seg)))) * TAU;
	return abs(dot(vec2(cos(a), sin(a)), st));
}

float stroke(float x, float size, float w) {
	float d = step(size, x + w * .5) - step(size, x - w * .5);
	return clamp(d, 0., 1.);
}

float fakeAtan(float v) {
    float abs_v = abs(v);
    return log(abs_v + 1.)*sign(v);
}

float triSDF(vec2 uv) {
    vec2 st = (uv * 2. - 1.) * 2.;
    return max(abs(st.x) * 0.866025 + st.y * 0.5, -st.y * 0.5);
}

float hexSDF(vec2 uv) {
	vec2 st = abs(uv * 2. - 1.);
    return max(abs(st.y), st.x * 0.866025 + st.y * 0.5);
}

float crossSDF(vec2 uv, float s) {
	vec2 size = vec2(.25, s);
    return min(rectSDF(uv.xy, size.xy), rectSDF(uv.xy, size.yx));
}

float polySDF(vec2 uv, int V) {
	float a = atan(uv.x, uv.y) + PI;
    float r = length(uv);
    float v = TAU/float(V);
    return cos(round(a / v) * v - a) * r;
}

float heartSDF(vec2 uv) {
	vec2 st = uv;
    float r = length(st) * 5.0;
    st = normalize(st);
    return r -
        	((st.y*pow(abs(st.x), 0.67))/
        	(st.y + 1.5) - (2.) * st.y + 1.26);
}

float flowerSDF(vec2 uv, int N) {
	vec2 st = uv;
    float r = length(st) * 2.;
    float a = atan(st.y, st.x);
    float v = float(N) * .5;
    return 1. - (abs(cos(a * v)) * .5 + .5) / r;
}

float rhombSDF(vec2 uv) {
	return max(triSDF(uv), triSDF(vec2(uv.x, 1. - uv.y)));
}

float vesicaSDF(vec2 st, float r, float w) {
	vec2 offset = vec2(w * .5, 0.);
    return max(circleSDF2(st - offset, r), circleSDF2(st + offset, r));
}


float spiralSDF(vec2 uv, float t) {
    vec2 st = uv - .5;
    float r = dot(st,st);
    float a = atan(st.y,st.x);
    return abs(sin(fract(log(r)*t+a*0.159)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st =  ( fragCoord - .5* iResolution.xy ) / iResolution.y;

    // Time varying pixel color
    vec3 color = 0.5 + 0.5*cos(iTime+st.xyx+vec3(0,2,4));

    // vec3 color = vec3(0);
    color -= stroke(circleSDF2(st, 0.08), .4, .035);
    color -= stroke(starSDF2(st, 4 + 2 * int(sin(iTime) * 2.0), 0.296), 0.12, 0.02);
  	color -= fill(starSDF2(st, 8 * int((sin(iTime) + 2.) * 2.), 0.296), 0.033);
    color -= stroke(heartSDF(scale(st, vec2(3.2, 3.5)) - vec2(0., 0.3)), 0.27, 0.18);

    vec2 c = vec2(-2., 0);
    color -= fill(flowerSDF(rotate(st + vec2(1.0, 0.5), iTime + 1.) * 5., 3), 0.02);
    color -= fill(flowerSDF(rotate(st + vec2(0.0, 0.5), iTime) * 5., 3), 0.02);

    color -= stroke(vesicaSDF(rotate(st, HALF_PI) + vec2(0.5, -1.), 0.1, 0.25 * (sin(iTime * 1.5)*0.2 + 1.)), .4, .03);
    color -= stroke(vesicaSDF(rotate(st, HALF_PI) + vec2(0.5, 0.), 0.1, 0.25 * (cos(iTime * 1.5)*0.2 + 1.)), .4, .03);

    color -= stroke(crossSDF(st + vec2(0., -0.4), 0.8), .12, .05);
    color -= stroke(rhombSDF(st + vec2(0.5, 0.1)), .12, .03);
    vec3 col = vec3(0.);
    // col += polySDF(st, 4);
    color += stroke(polySDF(st, 3 + int(round((cos(iTime)+1.) * 7.)) ), .28, .035);
    // Output to screen
    fragColor = vec4(color,1.0);
}

vec2 complexMultiply( vec2 a, vec2 b )
{
    return vec2(dot(a, b * vec2(1.,-1.)), dot(a, b.yx));
}
vec2 complexPower( vec2 a, int power )
{
    vec2 o = vec2(1.,0.);
    int i = 0;
    while(i < power) {
        complexMultiply(o, a);
        i++;
    }
    return o;
}
vec2 mandelbrot(vec2 z, vec2 c, int power) {
    return complexMultiply(z, z) + c;
    //return complexPower(z, power) + c;
}
vec2 pinwheel(float t) {
    float BIGF = .25;
    float LITTLEF = .9;
    BIGF *= 4.;
    LITTLEF *= 4.;	
    float BIGR = 1.;
    float LITTLER = .4;
    BIGR *= 2.;
    LITTLER *= 2.;
    return (BIGR - LITTLER) * vec2(cos(BIGF * t), sin(BIGF * t)) +
        LITTLER * vec2(cos(LITTLEF * t), sin(LITTLEF * t));
}
