#define NUM 256   // change this

vec4 data[NUM];

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int id = (int(fragCoord.x) + iFrame) % NUM;

    data[0    ] = vec4(sin(iTime+0.0), 0.0, 0.0, 1.0 );
	data[id   ] = vec4(0.0, sin(iTime+2.0), 0.0, 1.0 );
	data[NUM-1] = vec4(0.0, 0.0, sin(iTime+4.0), 1.0 );

    fragColor = data[0] + data[id] + data[NUM-1];
}

#define  PI 3.142857142
#define DPI 6.285714285

float pol(in vec2 uv, float n, float tam){

    float a = atan(uv.x, uv.y) - 3.14;
    float r = DPI/n;
    float d = cos(floor(0.5+a/r)*r-a)*length(uv);
    d = 1.0-step(tam,d); 
    return d;
}

float Circle (vec2 uv, float blur, float r) {
    float d = length(uv);
    float c = smoothstep(r,r-blur,d);
    return c;
}
mat2 scale(vec2 _scale){
    return mat2(_scale.x,0.0,0.0,_scale.y);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy; 
    vec2 uv1 = fragCoord/iResolution.xy; 
    vec2 uv2= fragCoord/iResolution.xy; 
    vec2 uv3 = fragCoord/iResolution.xy;
    vec2 uv4 = fragCoord/iResolution.xy; 
    vec2 pos= vec2(-1.,0.);
    vec2 pos1= vec2(-1.,0.);

    vec3 col;
    uv -= .5;
    uv1 -= .5;
    uv2 -= .5;
    uv3 += pos;
    uv4 += pos1;

   	uv.x *= iResolution.x/iResolution.y;
	uv1.x *= iResolution.x/iResolution.y;
	uv2.x *= iResolution.x/iResolution.y;
    uv3.x *= iResolution.x/iResolution.y;
    uv4.x *= iResolution.x/iResolution.y;

   	uv = scale( vec2(sin(iTime)/1.5+1.) ) *uv;
    float p1 = pol(uv, 6.0, 0.1);

    uv1 = rotate2d( sin(iTime)*PI) * uv1;
    float t1= pol(uv1,3.0,0.1);

    vec2 translate = vec2(cos(iTime),sin(iTime));
    uv2 += translate*0.4;
    float r1= pol(uv2,4.0,0.1);

    pos = vec2(-1.+sin(iTime)/2.,0.5);
    float c0 = Circle(uv3-pos ,.001,.08);

    pos1 = vec2(-.9,0.5+sin(iTime)/2.);
    float c1 = Circle(uv4 -pos1,.001,.08);

    col += vec3(p1)*vec3(0,0.5,1);
	col += vec3(t1)*vec3(1.,0.,0.);
    col += vec3(r1)*vec3(0.,1.,0.);
    col += vec3(c0)*vec3(.5,.5,0.);
    col += vec3(c1)*vec3(0.,.5,.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define SPIRAL_COUNT 5.
#define RADIUS 2.
#define TWISTPOW 1.

vec4 color = vec4(1.0,0.0, 1.0,1.0);
vec4 colorBackground = vec4(0.0,0.0, 0.0,1.0);

//my first shader
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float t = iTime*0.5;

	vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float angle = atan(uv.y,uv.x) +t ;

    angle -= TWISTPOW *length(uv) ;

    vec4 col = color * vec4(cos( SPIRAL_COUNT * angle));

    vec3 center = vec3(clamp(RADIUS-length(uv*2.2),0.,1.));


    //gradient
    col = ( mix(col,color , smoothstep(RADIUS,0., length(center)  )) ) ;

    //center black
    col *= vec4( smoothstep(0.0,0.5*RADIUS, length(uv)*2. ));

    fragColor =  mix(color,colorBackground, length(col));
}

const float minDrawDist = 10.0;
const int   maxRayMarcs = 1000;
const float cellSize    = 1000.0;
const float ballRadius  = 200.0;

vec3 camPos;

float getDist(vec3 rayPos)
{
    float t = iTime / 4.0;
	float t1 = 1000.0 - (t - floor(t)) * 1000.0;
	float t2 = 2000.0 - (t - floor(t)) * 2000.0;
	float t4 = - (t/6.282 - floor(t/6.282)) * 16000.0;
    rayPos.z -= t4;
    vec3 vertex = floor(-((camPos - rayPos) - ballRadius) / cellSize) * cellSize;
	float y = vertex.y;
    vertex.z += (sin((vertex.z + y + 8.0 * sqrt(2.0 * abs(t - floor(t) - .5)) ) / 2.0) + 1.0) * 250.0; //floor(iTime * 500.0 / cellSize) * cellSize;
    vertex.y += abs(cos((vertex.z ) / 2000.0 + iTime) * 400.0);
    vertex.y += abs(sin((vertex.z ) / 250.0 + iTime) * 200.0);

//vertex.z += (sin(iTime * 3.0) + 1.0) * 180.0; //floor(iTime * 500.0 / cellSize) * cellSize;


	vec3 pos = camPos + vertex;
	return distance(pos, rayPos) - ballRadius;
}

vec3 getNormal(vec3 p)
{
    float d = getDist(p);
    vec2  e = vec2(0.01, 0.0);

    vec3 n = d - vec3(
	    getDist(p - e.xyy),
	    getDist(p - e.yxy),
	    getDist(p - e.yyx)
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 outputPixel = vec4(0.0);

	vec2 position = gl_FragCoord.xy;

	camPos = vec3(
		iResolution.x / 2.0, 
		iResolution.y / 2.0, 
		-600.0
	);

	vec3 lightPos = vec3(
		iResolution.x * 6.0 + sin(11.0 / 10.0 * iTime + .5) * 2000.0, 
		cos(7.0 / 10.0 * iTime + .5) * iResolution.y * 10.0,
		cos(3.0 / 10.0 * iTime + 1.5) * 5000.0
	);

	vec3 rayPos = vec3(position, 0.0);
	vec3 rayVec = normalize(rayPos - camPos);

	vec4 ballColor = vec4(1.0, .4, .4, 1.0);

	vec3 curRayPos = rayPos * minDrawDist;
	for (int i = 0; i < maxRayMarcs; i ++)
	{ 
		float distToBall = getDist(curRayPos);
		if (distToBall < .1)
		{
			vec3  normal = getNormal(curRayPos);
            		float light  = dot(normalize(lightPos - curRayPos), normal);
           		light = clamp(abs(light), 0.0, 1.0) + 0.02;
			light *= 1000000.0 / pow(abs(curRayPos.z - camPos.z), 2.0);
			outputPixel = pow(light, 1.0 / 1.8) * ballColor;


			break;

		} else
		{
			curRayPos += rayVec * 20.0;
		}
	}


	fragColor = outputPixel;
}

