    vec2 bo = vec2(9999.0,9999.0);
    float bl = 99999.0;
    vec2 wo = vec2(0.0,0.0);
    float wl = 0.0;
    float it = 0.0;
    float os = 0.0;
    for (int i = 0; i < 64; i ++)
    {
     	vec2 nz = vec2(z.x*z.x - z.y*z.y, (1.5 - cos(iTime * 0.022)) * z.x * z.y) +c;
        vec2 no = nz - p;
        float nl = length(no);
        os += nl;
        it += 1.0;
        if (nl > wl)
        {
        	wo = no;
            wl = nl;

        }
        if (nl < bl)
        {
        	bo = no;
            bl = nl;

        }

        float m2 = dot(z.x,z.x);
        if (m2 > 4.0)
        {
           // bl = 1.0;
        	break;   
        }
        z = nz;
