		float squareSize = 0.5;
		vec3 p = mod ( ray.position - plane_1.center - ray.epsilon, squareSize * 2.0 );
		float s = squareSize;
		float l = s;
		float h = s;
		if ( ( p.x < l && p.y < l && p.z < l ) ||
			 ( p.x > h && p.y < l && p.z > h ) ||
			 ( p.x < l && p.y > h && p.z > h ) ||
			 ( p.x > h && p.y > h && p.z < l ) ) {
			material = material_plane_1b;
		} else {
			material = material_plane_1w;
		}
	}
	return material;
}

