//Пересечение луча со сферой
float intersectSphere(Ray ray, Sphere sphere, inout vec3 normal) {
	float t0, t1, t;

	vec3 l = sphere.center - ray.origin;
	float tca = dot(l, ray.direction);
	if ( tca < 0.0 ) return ray.far;
	float d2 = dot (l, l) - (tca * tca);
	float r2 = sphere.radius*sphere.radius;
	if ( d2 > r2 ) return ray.far;
	float thc = sqrt(r2 - d2);
	t0 = tca - thc;
	t1 = tca + thc;

	if ( t0 < 0.0 ) {
		t = t1;
	} else if ( t1 < 0.0 ) {
		t = t0;
	} else {
		t = min(t0,t1);
	}
	if (t<0.0) return ray.far;
	vec3 p = ray.origin + t * ray.direction;
	float flagInside = -sign(dot(ray.direction, p - sphere.center));
	normal = normalize ( p - sphere.center ) * flagInside;
	return t;
}
//Пересечение луча с плоскостью
float intersectPlane(Ray ray, Plane plane, inout vec3 normal) {
	float det = dot ( plane.normal, ray.direction );
	if (abs(det) < ray.epsilon) return ray.far;

	float t = - ( dot ( plane.normal, ray.origin ) + length(plane.center) ) / dot ( plane.normal, ray.direction );

	if ( t < 0.0 ) return ray.far;

	float flagInside = -sign(dot(ray.direction, plane.normal));
	normal = plane.normal;
	normal *= flagInside;

	return t;	
}
//Пересечение луча с кубом
float intersectBox(Ray ray, Box box, inout vec3 normal) {

	vec3 minp = box.center - box.size;
	vec3 maxp = box.center + box.size;

	float tmin = (minp.x - ray.origin.x) / ray.direction.x;
	float tmax = (maxp.x - ray.origin.x) / ray.direction.x;
	if (tmin > tmax) { 
		float s = tmin; 
		tmin = tmax; 
		tmax = s; 
	}
	float tymin = (minp.y - ray.origin.y) / ray.direction.y;
	float tymax = (maxp.y - ray.origin.y) / ray.direction.y;
	if (tymin > tymax) { 
		float s = tymin; 
		tymin = tymax; 
		tymax = s; 
	}
	if ((tmin > tymax) || (tymin > tmax)) return ray.far;
	if (tymin > tmin) tmin = tymin;
	if (tymax < tmax) tmax = tymax;
	float tzmin = (minp.z - ray.origin.z) / ray.direction.z;
	float tzmax = (maxp.z - ray.origin.z) / ray.direction.z;
	if (tzmin > tzmax) { 
		float s = tzmin; 
		tzmin = tzmax; 
		tzmax = s; 
	}
	if ((tmin > tzmax) || (tzmin > tmax)) return ray.far;
	if (tzmin > tmin) tmin = tzmin;
	if (tzmax < tmax) tmax = tzmax;

	float t;
	if ( tmin < ray.epsilon && tmax < ray.epsilon ) return ray.far;
	if ( tmin < ray.epsilon ) {
		t = tmax;
	} else if ( tmax < ray.epsilon ) {
		t = tmin;
	} else {
		t = min(tmin, tmax);
	}

	float flagInside = 1.;
	if ( tmin < ray.epsilon && tmax > ray.epsilon ) flagInside = -1.;

	vec3 p = ray.origin + t * ray.direction;

	if ( abs(p.x - maxp.x) < ray.epsilon ) {
		normal = vec3(1,0,0) * flagInside;
	} else if ( abs(p.x - minp.x) < ray.epsilon ) {
		normal = vec3(-1,0,0) * flagInside;
	} else if ( abs(p.y - maxp.y) < ray.epsilon ) {
		normal = vec3(0,1,0) * flagInside;
	} else if ( abs(p.y - minp.y ) < ray.epsilon ) {
		normal = vec3(0,-1,0) * flagInside;
	} else if ( abs(p.z - maxp.z ) < ray.epsilon ) {
		normal = vec3(0,0,1) * flagInside;
	} else {
		normal = vec3(0,0,-1) * flagInside;
	}
	return t;
}


//---------------------------------------------------------------------------------
void rayMarch(inout Ray ray) {
	ray.origin += ray.near * ray.direction;

	ray.hit 		= false;
	ray.distance 	= ray.far;
	ray.position 	= ray.origin + ray.distance * ray.direction;
	ray.normal 		= vec3(0);
	ray.object.id 	= -1;

	Sphere sphere;
	Plane plane;

	float d;
	vec3 normal;

	float radius = 0.25;

	for (float i=0.; i<4.; i++) {
		vec3 position = vec3(sin(i*1000.0), sin(i*10000.0), sin(i*100.0));
		vec3 velocity = vec3(cos(i*200.0+i*400.0+500.0), cos(i*400.0+i*2000.0+500.0), cos(i*600.0)) / 2.0;
		vec3 center = position + velocity * u_time;

		center = mod ( center, 4.0 ) - 2.0;
		if ( center.x >  1.0 ) center.x =  2.0 - center.x;
		if ( center.y >  1.0 ) center.y =  2.0 - center.y;
		if ( center.z >  1.0 ) center.z =  2.0 - center.z;
		if ( center.x < -1.0 ) center.x = -2.0 - center.x;
		if ( center.y < -1.0 ) center.y = -2.0 - center.y;
		if ( center.z < -1.0 ) center.z = -2.0 - center.z;

		sphere = Sphere(center, radius);
		d = intersectSphere ( ray, sphere, normal );
		if (ray.distance > d) {
			ray.hit 	 	= true;
			ray.distance 	= d;
			ray.position 	= ray.origin + ray.distance * ray.direction;
			ray.normal   	= normal;
			ray.object.id 	= int(i);
		}
	}

	Plane planes[5];
	planes[0] = Plane(vec3( 0,			-1. - radius, 0			 ), vec3( 0, 1, 0));
	planes[1] = Plane(vec3( 0,			 1. + radius, 0			 ), vec3( 0,-1, 0));
	planes[2] = Plane(vec3(-1. - radius	,0,			  0			 ), vec3( 1, 0, 0));
	planes[3] = Plane(vec3( 1. + radius ,0,			  0			 ), vec3(-1, 0, 0));
	planes[4] = Plane(vec3( 0,			 0, 		  1. + radius), vec3( 0, 0,-1));

	for (int i=0; i<5; i++) {
		d = intersectPlane ( ray, planes[i], normal );
		if (ray.distance > d) {
			ray.hit 	 	= true;
			ray.distance 	= d;
			ray.position 	= ray.origin + ray.distance * ray.direction;
			ray.normal   	= normal;
			ray.object.id 	= 4;
		}
	}
}

vec3 lighting(Ray ray, Light lights[lightCount], Material material) {
	vec3 col = vec3(0);

	for (int i=0; i<lightCount; i++) {
		Light light = lights[i];

		float lightDistance = length(light.position - ray.position);

		//Тень
		float shadowFactor = 1.0;

		Ray ray1 = ray;
		{
			ray1.origin 	= ray.position;
			ray1.direction 	= normalize(light.position - ray.position);
			ray1.near 		= 0.01;
			ray1.far		= lightDistance;
		}
		rayMarch ( ray1 );


		if ( ray1.distance < ray1.far) {
			shadowFactor = clamp(1. - (lightDistance - 3.*ray1.distance) / lightDistance, 0.2, 1.);
		}

		float distanceAttenuationPower = 0.2;
		vec3 lightDir 	= normalize(light.position - ray.position);
		vec3 eyeDir 	= - ray.direction; //normalize ( ray.origin - ray.position );
		vec3 reflected 	= reflect ( -lightDir, ray.normal );

		vec3 color = light.colorAmbient * material.colorAmbient;

		color += shadowFactor * light.colorDiffuse * material.colorDiffuse * max(dot(ray.normal,lightDir), 0.0);
		color += shadowFactor * light.colorSpecular * material.colorSpecular * pow ( max ( dot(reflected, eyeDir), 0.0) , material.shininess ) * step(1., shadowFactor) ;	
		color *= min ( 1.0/pow(length(ray.position - ray.origin), distanceAttenuationPower), 1.0);

		col += color/float(lightCount);
	}

	return col;
}

//Цвет отражений
vec3 rayReflect(in Ray ray, in Light lights[lightCount]) {
	//Цвет отражений
	vec3 color = vec3(0);
	//Фактор отражения
	float factorReflect = 1.0;

	for (int i=0; i<4; i++) {

		rayMarch (ray);

		if (ray.hit) {
			Material material = getMaterial(ray);
			color += factorReflect * lighting(ray, lights, material);
			factorReflect *= material.reflectivity;
			if (factorReflect < 0.01) break;
			//Следующий луч
			{
				ray.origin 		= ray.position;
				ray.direction 	= reflect (ray.direction, ray.normal);
				ray.near 		= 0.01; //Отступ
			}
		} else {
			//Цвет фона
			color += vec3(0.1);
			break;
		}
	}
	return color;
}
//Цвет преломлений
vec3 rayRefract(in Ray ray, in Light lights[lightCount]) {
	vec3 color = vec3(0);
	//Фактор отражения
	float factorRefract = 1.0;

	for (int i=0; i<4; i++) {

		rayMarch (ray);

		if (ray.hit) {
			//Достигли фигуры
			Material material = getMaterial(ray);
			factorRefract *= material.refractivity;
			if (factorRefract < 0.01) break;
			//Луч преломления сквозь фигуру
			{
				ray.origin 		= ray.position;
				ray.direction 	= refract (ray.direction, ray.normal, material.indexRefractive);
				ray.near 		= 0.01; //Отступ
			}

			//Пересечение луча с границей 2 той же фигуры 
			rayMarch (ray);

            if (! material.flagSolid) {


				if (ray.hit) {
					//Достигли границы 2 фигуры
					//Луч преломления сквозь границу 2 фигуры
					{
						ray.origin 		= ray.position;
						ray.direction 	= refract (ray.direction, ray.normal, material.indexRefractive);
						ray.near 		= 0.01; //Отступ
					}
					//Уменьшаем фактор преломления
					factorRefract *= material.refractivity;
					//Цвет за границей 2 фигуры
					color += factorRefract * rayReflect(ray, lights);

					//Луч отражения от границы 2
					{
						ray.origin 		= ray.position;
						ray.direction 	= reflect (ray.direction, ray.normal);
						ray.near 		= 0.01; //Отступ
					}
					//Отражение от границы 2
					rayMarch (ray);

					if (ray.hit) {
						//Отраженный луч достиг границы 1 фигуры
						//Луч преломления сквозь границу 1 фигуры
						{
							ray.origin 		= ray.position;
							ray.direction 	= refract (ray.direction, ray.normal, material.indexRefractive);
							ray.near 		= 0.01; //Отступ
						}
						//Уменьшаем фактор преломления
						factorRefract *= material.refractivity;
					}
				}
			}			
			color += factorRefract * rayReflect(ray, lights);
		} else {
			break;
		}
	}

	return color;
}
