const float PI =3.14159265359;
const float PI2 =2.0*PI;
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
   vec2 mouse = iMouse.xy/iResolution.xy;


    float freq1=mouse.x*10.0;
    float freq2= .730;
    float freq3=mouse.y*10.0;

vec3 col;
    if(uv.x>0.66){
    // Time varying pixel color
        // align with pi, use stepping for new colors on each frequency pass
        float hue=mod(floor(iTime/freq1),256.0)/256.0;

       col =hsv2rgb(vec3( hue,uv.y,.50 + 0.5*sin(iTime*freq1)));
    }else if(uv.x>0.33){
         // align with pi, use stepping for new colors on each frequency pass
        float hue=mod(floor(iTime/freq2),256.0)/256.0;

       col =hsv2rgb(vec3( hue,uv.y,.50 + 0.5*sin(iTime*freq2)));
    }else{
  // align with pi, use stepping for new colors on each frequency pass
        float hue=mod(floor(iTime/freq3),256.0)/256.0;

       col =hsv2rgb(vec3( hue,uv.y,.50 + 0.5*sin(iTime*freq3)));
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

