    vec2 p = fragCoord * .01;
    p += 1.0;
    //tile

    // draw center
    //mdist += step(mdist,.02);

    vec3 col = vec3(0.);

    for(float i = 1. ; i < OCTAVES; i++)
    {
    	col += vec3(getCell(p)) / i;
        p *= OCTAVES;
    }

    //gamma
    col = pow(col,vec3(2.2));
    // make pretty :)
    col.r *= uv.x;
    col.b *= 1.-uv.y;
    col.g *= .8 * 1.-uv.x;


    fragColor = vec4(col,1.0);
}

#define ASCNUMS  48 // ASCII code for 0
#define ASCALPHA 64 // ASCII code for a

bool keyPressed(int ascii) {
	return (texture(iChannel2,vec2((float(ascii)+0.5)/256.,0.5/3.0)).x > 0.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // first part just sets the scale using the keyboard

    float WIDTH; // percent width of grid-line relative to square

         if (keyPressed(ASCNUMS+1))
	    WIDTH = 0.001;
    else if (keyPressed(ASCNUMS+2))
	    WIDTH = 0.002;
    else if (keyPressed(ASCNUMS+3))
	    WIDTH = 0.004;
    else if (keyPressed(ASCNUMS+4))
	    WIDTH = 0.008;
    else if (keyPressed(ASCNUMS+5))
	    WIDTH = 0.016;
    else if (keyPressed(ASCNUMS+6))
	    WIDTH = 0.032;
    else if (keyPressed(ASCNUMS+7))
	    WIDTH = 0.064;
    else if (keyPressed(ASCNUMS+8))
	    WIDTH = 0.256;
    else if (keyPressed(ASCNUMS+9))
	    WIDTH = 0.512;
    else if (keyPressed(ASCNUMS+0))
	    WIDTH = 1.000;
    else 
	    WIDTH = 0.128;

    float aa;

         if (keyPressed(ASCALPHA+17)) // Q
	    aa = 2.;
    else if (keyPressed(ASCALPHA+23)) // W
	    aa = 3.;
    else if (keyPressed(ASCALPHA+ 5)) // E
	    aa = 4.;
    else if (keyPressed(ASCALPHA+18)) // R
	    aa = 5.;
    else if (keyPressed(ASCALPHA+20)) // T
	    aa = 6.;
    else if (keyPressed(ASCALPHA+25)) // Y
	    aa = 7.;
    else if (keyPressed(ASCALPHA+21)) // U
	    aa = 8.;
    else if (keyPressed(ASCALPHA+ 9)) // I
	    aa = 16.;
    else if (keyPressed(ASCALPHA+15)) // O
	    aa = 32.;
    else if (keyPressed(ASCALPHA+16)) // P
	    aa = 64.;
    else 
	    aa = 1.;

    // second part just sets up some interactivity with the mouse

	vec2 center = iResolution.xy/2.;

    vec2 m = iMouse.xy - center;

    float len = length(m);

    float scale = iResolution.x / len;

    mat2 rot = len > 0. ?
        mat2(m.x, m.y, -m.y, m.x) / len :
    	mat2(1., 0., 0., 1.);

	// press space bar to stop rotation
    if (!keyPressed(32)) {
	    float t = -iTime/60.;
	    rot *= mat2(cos(t), sin(t), -sin(t), cos(t));
    }

    vec2 xy = (fragCoord.xy - center) * rot ;

    // third part does all the rendering

    // normalize coords and slide everything up and over half a square
    vec2 uv = xy/iResolution.xx + .5/scale;

    // scale coords up and center them around the origin
    vec2 st = fract( scale * uv ) - .5;

	float edge  = aa/len;
    float solid = (WIDTH - edge) / 2.;

    st = 1. - clamp( (abs(st) - solid) / edge, 0., 1. );

  	fragColor = vec4(smoothstep(0., 1., max(st.x, st.y)));
}

