#define N_SEG 12

mat3 flyerMat[2], flMat;
vec3 flyerPos[2], flPos, qHit, qHitTransObj, sunDir, trkA, trkF, cPt[N_SEG],
   cPtOrg, cUpCirc, cDnCirc, oDir, oNorm;
float tCur, dstFar, flyVel, tLen[N_SEG], hTop, rLoop, sHzRamp, rCirc, sDnRamp, sUpRamp,
   sHzStr, pathDir, vfLoop, ptFac;
int idObj, idObjGrp;
const int idFus = 1, idPipe = 2, idWing = 3, idStrut = 4, idHstab = 5,
   idFin = 6, idLeg = 7, idAxl = 8, idWhl = 9, idNose = 10, idCan = 11, idPlt = 12;
const float pi = 3.14159;

float GrndHt (vec2 p)
{
  mat2 qRot;
  vec2 q;
  float h, wAmp;
  qRot = 1.9 * mat2 (0.8, -0.6, 0.6, 0.8);
  q = 0.04 * p;
  h = 0.;
  wAmp = 1.;
  for (int j = 0; j < 4; j ++) {
    h += wAmp * Noisefv2 (q);
    wAmp *= 0.45;
    q *= qRot;
  }
  return 5. * h;
}

vec3 GrndNf (vec3 p)
{
  const vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (GrndHt (p.xz) - vec2 (GrndHt (p.xz + e.xy), GrndHt (p.xz + e.yx)), e.x).xzy);
}

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float FlyerDf (vec3 p, float dMin)
{
  vec3 q, qq;
  vec2 ws;
  float d, wr, h;
  int ig;
  ig = idObjGrp / 256;
  q = p;
  wr = -0.2 + q.z;
  d = PrRoundBoxDf (q, vec3 (0.07 * (1. - 0.8 * wr * wr),
     0.11 * (1. - 0.6 * wr * wr), 1.), 0.05);
  d = max (d, - PrRoundBoxDf (q - vec3 (0., 0.16, 0.), vec3 (0.04, 0.03, 0.07), 0.04)); 
  DMINQ (idObjGrp + idFus);
  q = p;  q -= vec3 (0., 0.17, 0.5);
  d = PrCylDf (q, 0.015, 0.15);
  DMINQ (idObjGrp + idCan);
  q = p;  q.y -= 0.17;
  d = PrSphDf (q, 0.04); 
  DMINQ (idObjGrp + idPlt);
  q = p - vec3 (0., 0.03, 0.8);  q.x = abs (q.x) - 0.1;
  d = PrCapsDf (q, 0.02, 0.15);
  DMINQ (idObjGrp + idPipe);
  q = p - vec3 (0., 0.03, 1.1);
  d = PrConeDf (q, vec3 (0.8, 0.6, 0.06));
  DMINQ (idObjGrp + idNose);
  q = p - vec3 (0., ((ig == 1) ? 0.12 : 0.09), 0.4);
  qq = q;
  if (ig == 1) qq.y += 0.12;
  h = (ig == 1) ? 0.24 : 0.42;
  qq.y = mod (qq.y, h) - 0.5 * h;
  wr = q.z / 0.24;
  d = PrRoundBoxDf (qq, vec3 (1.2, 0.01 * (1. - wr * wr), 0.24), 0.005);
  d = max (d, abs (q.y) - 0.26);
  DMINQ (idObjGrp + idWing);
  h = (ig == 1) ? 0.12 : 0.09;
  q = p - vec3 (0., h, 0.42);  q.xz = abs (q.xz) - vec2 (0.5, 0.1);
  h = (ig == 1) ? 0.24 : 0.21;
  d = PrCylDf (q.xzy, 0.01, h);
  DMINQ (idObjGrp + idStrut);
  h = (ig == 1) ? 0.24 : 0.15;
  q = p - vec3 (0., h, 0.42);  q.x = abs (q.x) - 0.1;
  h = (ig == 1) ? 0.12 : 0.15;
  d = PrCylDf (q.xzy, 0.01, h);
  DMINQ (idObjGrp + idStrut);
  q = p;  q.z -= -0.9;
  wr = q.z / 0.15;
  d = PrRoundBoxDf (q, vec3 (0.42, 0.005 * (1. - wr * wr), 0.15), 0.002);
  q.x = abs (q.x);
  d = max (d, 0.02 - 1.5 * q.x - q.z);
  DMINQ (idObjGrp + idHstab);
  q = p;  q.z -= -0.87;
  q.yz = Rot2D (q.yz, 0.15);
  ws = q.yz / vec2 (0.37, 0.15);
  ws *= ws;
  d = PrRoundBoxDf (q, vec3 (0.005 * (1. - ws.y), 0.37, 0.15 * (1. - 0.3 * ws.x)), 0.002);
  d = max (d, - q.y);
  DMINQ (idObjGrp + idFin);
  q = p - vec3 (0., -0.24, 0.7);  q.x = abs (q.x) - 0.135;
  q.xy = Rot2D (q.xy, -0.55);
  q.yz = Rot2D (q.yz, 0.15);
  d = PrCylDf (q.xzy, 0.013, 0.13);
  DMINQ (idObjGrp + idLeg);
  q = p - vec3 (0., -0.34, 0.715);  q.x = abs (q.x) - 0.22;
  d = PrCylDf (q.yzx, 0.01, 0.035);
  DMINQ (idObjGrp + idAxl);
  q.x -= 0.01;
  d = PrCylDf (q.yzx, 0.1, 0.015);
  DMINQ (idObjGrp + idWhl);
  return dMin;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  for (int j = 0; j < 2; j ++) {
    idObjGrp = (j + 1) * 256;
    dMin = FlyerDf (flyerMat[j] * (p - flyerPos[j]), dMin);
  }
  return 0.8 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0005, -0.0005);
  v = vec4 (- ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float PropelDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  q = p - vec3 (0., 0.02, 1.07);
  d = PrCylDf (q, 0.3, 0.007);
  if (d < dMin) { dMin = d;  qHitTransObj = q; }
  return dMin;
}

float TransObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  for (int j = 0; j < 2; j ++) {
    dMin = PropelDf (flyerMat[j] * (p - flyerPos[j]), dMin);
  }
  return dMin;
}

float TransObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 50; j ++) {
    d = TransObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec4 FlyerCol (int ig, int id, vec3 vn)
{
  vec3 col, bCol;
  vec2 w;
  float spec, s;
  spec = 0.2;
  bCol = vec3 (0.7, 0.4, 0.2);
  col = (ig == 2) ? vec3 (0.1, 0.9, 0.) : vec3 (1., 0., 0.);
  if (id == idFus) {
    if (ig == 2) col = mix (col, bCol, step (mod (4. * qHit.z, 1.), 0.5));
    if (qHit.z > 1.01) {
      col = vec3 (0.9);
      spec = 0.4;
      if (length (abs (qHit.xy + vec2 (0., 0.05)) * vec2 (0.6, 1.)) < 0.03) col *= 1. -
         0.7 * SmoothBump (0.2, 0.8, 0.05, mod (64. * ((ig == 1) ? qHit.y : qHit.x), 1.));
    }
    w = abs (qHit.xz) - vec2 (0.09, 0.12);
    if (abs (qHit.y - 0.1) < 0.04 && max (w.x, w.y) < 0.) col = vec3 (0.2, 0.15, 0.05);
    qHit.z += 0.3;
    if (ig == 2) col = mix (vec3 (0.9), col,
       (1. - SmoothBump (0.05, 0.08, 0.005, length (qHit.yz))) *
       (1. - SmoothBump (-0.01, 0.03, 0.005, length (qHit.yz))));
    else col = mix (vec3 (0.2), col,
       (1. - SmoothBump (0.02, 0.04, 0.01, length (abs (qHit.yz) - 0.04))));
  } else if (id == idWing) {
    if (ig == 2) col = mix (col, bCol, step (mod (4. * abs (qHit.x) + 0.25, 1.), 0.5));
    s = 1.2 / 8.;
    s = mod (qHit.x + 0.5 * s, s) - 0.5 * s;
    col *= 1. + 0.1 * SmoothBump (-0.01, 0.01, 0.002, s);
    if (abs (qHit.y) > 0.1) {
      if (abs (qHit.x) > 0.7)
         col *= 1. - 0.6 * SmoothBump (-0.158, -0.147, 0.002, qHit.z);
      if (qHit.z < -0.155)
         col *= 1. - 0.6 * SmoothBump (0.695, 0.705, 0.002, abs (qHit.x));
      if (qHit.y * vn.y > 0.) {
        qHit.x = abs (qHit.x) - 0.8;
        qHit.z -= 0.03;
        if (ig == 2) col = mix (vec3 (0.9), col,
           (1. - SmoothBump (0.08, 0.12, 0.01, length (qHit.xz))) *
           (1. - SmoothBump (-0.01, 0.03, 0.01, length (qHit.xz))));
        else col = mix (vec3 (0.2), col,
           (1. - SmoothBump (0.03, 0.06, 0.01, length (abs (qHit.xz) - 0.06))));
      }
    }
  } else if (id == idHstab) {
    if (ig == 2) col = mix (col, bCol, step (mod (4. * abs (qHit.x) + 0.25, 1.), 0.5));
    col *= 1. - 0.6 * SmoothBump (-0.062, -0.052, 0.002, qHit.z);
  } else if (id == idFin) {
    if (ig == 2) col = mix (col, bCol, step (mod (4. * qHit.y, 1.), 0.5));
    col *= 1. - 0.6 * SmoothBump (-0.062, -0.052, 0.002, qHit.z);
  } else if (id == idNose) {
    if (qHit.z > -0.04) {
      col = mix (col, vec3 (1.), 0.5);
      spec = 0.4;
    } else col = vec3 (0.1);
  } else if (id == idPipe) {
    col = vec3 (0.8, 0.8, 0.85) * (0.5 + 0.5 * SmoothBump (0.1, 0.9, 0.05, mod (16. * qHit.z, 1.)));
    spec = 0.4;
  } else if (id == idStrut || id == idLeg) {
    col = 0.6 * col + vec3 (0.4);
  } else if (id == idAxl) {
    col = vec3 (0.3, 0.2, 0.);
  } else if (id == idCan) {
    col = vec3 (0.2, 0.2, 0.22);
  } else if (id == idPlt) {
    col = vec3 (0.4, 0.2, 0.1);
    spec = 0.;
    if (qHit.z > 0.02 && qHit.y < 0.03) col = (ig == 2) ? vec3 (0.9) : vec3 (0.6, 0.4, 0.4);
    if (qHit.z > 0. && length (vec2 (abs (qHit.x) - 0.011, qHit.y - 0.015)) < 0.01) {
      col = vec3 (0., 0.2, 0.);
      spec = 0.4;
    }
    if (qHit.z > 0. && ig == 2 && length (qHit.xy - vec2 (0., -0.02)) < 0.012) col = vec3 (0.1);
  } else if (id == idWhl) {
    if (length (qHit.yz) < 0.07) col = vec3 (0.4, 0.4, 0.4);
    else {
      col = vec3 (0.02);
      spec = 0.;
    }
  }
  return vec4 (col, spec);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + d * rd);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += h;
    if (sh < 0.05) break;
  }
  return 0.5 + 0.5 * sh;
}

vec3 SkyBgCol (vec3 ro, vec3 rd)
{
  vec3 col, clCol, skCol;
  vec2 q;
  float f, fd, ff, sd;
  if (rd.y > -0.02 && rd.y < 0.03 * Fbm1 (16. * atan (rd.z, - rd.x))) {
    col = mix (vec3 (0.4, 0.5, 0.7), vec3 (0.3, 0.41, 0.55), smoothstep (-0.02, 0.01, rd.y));
  } else {
    q = 0.02 * (ro.xz + 0.5 * tCur + ((100. - ro.y) / rd.y) * rd.xz);
    ff = Fbm2 (q);
    f = smoothstep (0.2, 0.8, ff);
    fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
    clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
       smoothstep (0., 0.05, abs (fd)));
    sd = max (dot (rd, sunDir), 0.);
    skCol = vec3 (0.4, 0.5, 0.8) + step (0.1, sd) * vec3 (1., 1., 0.9) * min (0.3 * pow (sd, 64.) +
       0.5 * pow (sd, 2048.), 1.);
    fd = smoothstep (0.01, 0.1, rd.y);
    col = mix (mix (vec3 (0.8, 0.8, 0.7), skCol, 0.3 + 0.7 * fd), clCol, 0.1 + 0.9 * f * fd);
  }
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col, roo;
  float dstObj, dstGrnd, dstPropel, sh, f, s;
  int ig, id;
  dstObj = ObjRay (ro, rd);
  dstPropel = TransObjRay (ro, rd);
  if (dstObj < dstPropel) dstPropel = dstFar;
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    ig = idObj / 256;
    id = idObj - 256 * ig;
    col4 = FlyerCol (ig, id, ((ig == 1) ? flyerMat[0] : flyerMat[1]) * vn);
    if (id == idWing || id == idHstab) vn = VaryNf (128. * ro, vn, 0.1);
    sh = ObjSShadow (ro, sunDir);
    col = col4.rgb * (0.2 + 0.2 * max (dot (vn.xz, - normalize (sunDir.xz)), 0.) +
       0.6 * sh * max (dot (vn, sunDir), 0.)) +
       sh * col4.a * pow (max (0., dot (sunDir, reflect (rd, vn))), 32.);
  } else if (rd.y < 0.) {
    roo = ro;
    dstGrnd = - ro.y / rd.y;
    ro += dstGrnd * rd;
    vn = GrndNf (ro);
    f = 1. - clamp (0.5 * pow (vn.y, 4.) + Fbm2 (0.5 * ro.xz) - 0.5, 0., 1.);
    s = 1. - smoothstep (0.2, 0.6, dstGrnd / dstFar);
    vn = VaryNf (4. * ro, vn, 4. * f * f * s);
    col = mix (vec3 (0.35, 0.5, 0.2), vec3 (0.3, 0.4, 0.1),
       smoothstep (0.2, 0.8, Fbm2 (0.3 * ro.xz)));
    col = mix (vec3 (0.33, 0.45, 0.15), col, s);
    col = col * (0.1 + 0.2 * max (dot (vn.xz, - normalize (sunDir.xz)), 0.) +
       0.1 * max (vn.y, 0.) + 0.8 * max (dot (vn, sunDir), 0.));
    col = mix (col, SkyBgCol (roo, rd), pow (1. + rd.y, 32.));
  } else col = SkyBgCol (ro, rd);
  if (dstPropel < dstFar) col = vec3 (0.1) * (1. - 0.3 * SmoothBump (0.25, 0.27,
     0.006, length (qHitTransObj.xy))) + 0.7 * col;
  return clamp (col, 0., 1.);
}

void PathSetup ()
{
  ptFac = 20.;
  cPtOrg = vec3 (2. * pathDir, 0., -3.);
  hTop = 1.5;
  rLoop = 2.2;
  sHzRamp = 1.;
  rCirc = 2.;
  sDnRamp = 1.5;
  sUpRamp = 1.3 * sDnRamp;
  sHzStr = sDnRamp - sUpRamp + 3. * sHzRamp;
  vfLoop = 0.3;
  cDnCirc = cPtOrg + vec3 (- rCirc * pathDir, 0., -2. * sHzRamp);
  cUpCirc = cPtOrg + vec3 (- rCirc * pathDir, 2. * hTop, 2. * sDnRamp + 6. * sHzRamp);
  cPt[0] = cDnCirc;   cPt[0].xz += vec2 (- rCirc * pathDir, sHzStr);
  cPt[1] = cPt[0];    cPt[1].z += sHzStr;
  cPt[3] = cUpCirc;   cPt[3].x -= rCirc * pathDir;
  cPt[4] = cUpCirc;   cPt[4].x += rCirc * pathDir;
  cPt[2] = cPt[3];    cPt[2].z -= 2. * sHzRamp;
  cPt[5] = cPt[4];    cPt[5].z -= 2. * sHzRamp;
  cPt[7] = cPtOrg;
  cPt[8] = cPtOrg;
  cPt[6] = cPt[7];    cPt[6].z += 4. * sHzRamp;
  cPt[9] = cDnCirc;   cPt[9].x += rCirc * pathDir;
  cPt[10] = cDnCirc;  cPt[10].x -= rCirc * pathDir;
  cPt[11] = cPt[0];
  tLen[0] = 0.;
  for (int k = 1; k < N_SEG; k ++) tLen[k] = length (cPt[k] - cPt[k - 1]);
  tLen[4] = pi * rCirc;
  tLen[8] = 2. * pi * rLoop * (1. + vfLoop);
  tLen[10] = pi * rCirc;
  for (int k = 1; k < N_SEG; k ++) tLen[k] += tLen[k - 1];
}

void FlyerPM1 (float t)
{
  vec3 vel, va, flVd;
  vec2 cs;
  float oRl;
  flPos = vec3 (dot (trkA, sin (trkF * t)), 10. + 3. * SmoothBump (0.25, 0.75, 0.2, mod (0.001 * t, 1.)), t);
  vel = vec3 (dot (trkF * trkA, cos (trkF * t)), 0., 1.);
  va = cross (vec3 (dot (trkF * trkF * trkA, - sin (trkF * t)), 0., 0.), vel) / length (vel);
  flVd = normalize (vel);
  oRl = 20. * length (va) * sign (va.y) + 0.4 * (Fbm1 (0.02 * t) - 0.5);
  cs = sin (oRl + vec2 (0.5 * pi, 0.));
  flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) *
     mat3 (flVd.z, 0., flVd.x, 0., 1., 0., - flVd.x, 0., flVd.z);
}

void FlyerPM2 (float t, float vu)
{
  vec3 p, p1, p2, u;
  vec2 cs;
  float w, ft, ftt, s, h, oRl;
  int ik;
  h = 2. * sin (0.43 * pi * t) + sin (0.79 * pi * t);
  oRl = 0.01 * pi * h;
  t = mod (t, tLen[N_SEG - 1]);
  for (int k = 1; k < N_SEG; k ++) {
    if (t < tLen[k]) {
      t -= tLen[k - 1];
      p1 = cPt[k - 1];
      p2 = cPt[k];
      w = tLen[k] - tLen[k - 1];
      ik = k;
      break;
    }
  }
  oNorm = vec3 (0., 1., 0.);
  ftt = t / w;
  ft = ftt;
  if (ik == 4 || ik == 10) oRl += -0.25 * pi * SmoothBump (0.05, 0.95, 0.05, ft);
  if (ik == 4) oRl += 2. * pi * smoothstep (0.4, 0.6, ft);
  if (ik == 2 || ik == 6) {
    oDir = p2 - p1;
    p.xz = p1.xz + oDir.xz * ft * vec2 (pathDir, 1.);
    p.y = p1.y + oDir.y * smoothstep (0., 1., ft);
    oDir.y *= 6. * ft * (1. - ft);
    oDir = normalize (oDir);
  } else if (ik == 4) {
    ft *= pi;
    p = cUpCirc;
    u = vec3 (- cos (ft) * pathDir, 0., sin (ft));
    p.xz += rCirc * u.xz;
    oDir = cross (oNorm, u);
    oDir.xz *= pathDir;
  } else if (ik == 8) {
    ft = (ft < 0.5) ? ft * (1. + vfLoop * (1. - 2. * ft)) :
       ft * (1. + 2. * vfLoop * (ft - 1.5)) + vfLoop;
    p = 0.5 * (cPt[7] + cPt[8]);
    ft *= 2. * pi;
    u = vec3 (0., cos (ft), sin (ft));
    p.yz += rLoop * (vec2 (1., 0.) - u.yz);
    oNorm = u;
    oDir = normalize (vec3 (0., 2. * pi * rLoop * vec2 (sin (ft), - cos (ft))));
  } else if (ik == 10) {
    ft *= pi;
    p = cDnCirc;
    u = vec3 (cos (ft) * pathDir, 0., - sin (ft));
    p.xz += rCirc * u.xz;
    oDir = cross (oNorm, u);
    oDir.xz *= pathDir;
  } else {
    oDir = p2 - p1;
    p = p1 + oDir * ft;
    oDir = normalize (oDir);
  }
  p *= ptFac;
  p.y += 2. + 0.1 * h;
  p.y += 8.;
  flPos = p;
  if (vu > 0.) {
    oDir *= -1.;
    oRl *= -1.;
  }
  oRl += 0.2 * (Fbm1 (t) - 0.5) * SmoothBump (0.1, 0.9, 0.1, ftt);
  cs = sin (oRl * pathDir + vec2 (0.5 * pi, 0.));
  flMat = mat3 (cs.x, - cs.y, 0., cs.y, cs.x, 0., 0., 0., 1.) * AxToRMat (oDir, oNorm);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 dateCur;
  vec3 ro, rd, vd, col;
  vec2 canvas, uv, uvv, uvs, ori, ca, sa;
  float az, el, s, t, sr;
  int tpTrk;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uvs = uv;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  dateCur = iDate;
  tCur = mod (tCur, 3600.) + floor (dateCur.w / 3600.);
  tpTrk = 1;
  t = 0.01 * tCur;
  if (length (uvs) < 1.5 * SmoothBump (0.2, 0.9, 0.01, mod (t, 1.))) tpTrk = 2;
  if (tpTrk == 1) {
    flyVel = 20.;
    trkA = 8. * vec3 (1.9, 2.9, 4.3);
    trkF = 0.15 * vec3 (0.23, 0.17, 0.13);
    s = mod (0.022 * tCur, 1.);
  } else if (tpTrk == 2) {
    tCur = 100. * mod (t, 1.) + 10.;
    flyVel = 0.8;
    pathDir = (mod (t, 2.) > 1.) ? 1. : -1.;
    PathSetup ();
  }
  for (int j = 0; j < 3; j ++) {
    t = tCur * flyVel;
    if (tpTrk == 1) {
      if (j < 2) t += (j == 0) ? 4.5 : 10.5;
      else t += 1.5 + 12. * SmoothBump (0.25, 0.75, 0.15, s);
    } else if (tpTrk == 2) {
      if (j < 2) t += ((j == 0) ? 0.3 : 0.8);
    }
    if (tpTrk == 1) {
      FlyerPM1 (t);
    } else if (tpTrk == 2) {
      FlyerPM2 (t, 0.);
    }
    if (j < 2) {
      flyerPos[j] = flPos;
      if (tpTrk == 1) flyerPos[j].x += (j == 0) ? -0.1 : 0.1;
      flyerMat[j] = flMat;
    } else {
      ro = flPos;
      if (tpTrk == 2) ro += 0.7 * oNorm + 0.5 * cross (oNorm, oDir) * sin (0.2 * t);
    }
  }
  if (tpTrk == 1) {
    ro.y += 0.5 + SmoothBump (0.25, 0.75, 0.15, mod (2. * s, 1.)) * sign (s - 0.5);
    ro.x += 9. * SmoothBump (0.25, 0.75, 0.15, mod (2. * s, 1.)) * sign (s - 0.5);
    vd = normalize (0.5 * (flyerPos[0] + flyerPos[1]) - ro);
    az = 0.5 * pi + atan (- vd.z, vd.x);
    el = asin (vd.y);
  } else if (tpTrk == 2) {
    az = 0.;
    el = 0.;
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  dstFar = 300.;
  sunDir = normalize (vec3 (cos (0.01 * tCur), 2., - sin (0.01 * tCur)));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = 0.; a < naa; a ++) {
    uvv = uv;
    if (naa > 1.) uvv += Rot2D (vec2 (0.5 / canvas.y, 0.), sr * (0.667 * a + 0.5) * pi);
    rd = normalize (vec3 (uvv, 3.));
    if (tpTrk == 2) rd = rd * flMat;
    rd = vuMat * rd;
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (col, 1.);
}

float PrBoxDf (vec3 p, vec3 b)
{
  vec3 d;
  d = abs (p) - b;
  return min (max (d.x, max (d.y, d.z)), 0.) + length (max (d, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrConeDf (vec3 p, vec3 b)
{
  return max (dot (vec2 (length (p.xy), p.z), b.xy), abs (p.z) - b.z);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

mat3 AxToRMat (vec3 vz, vec3 vy)
{
  vec3 vx;
  vx = normalize (cross (vy, vz));
  vy = cross (vz, vx);
  return mat3 (vec3 (vx.x, vy.x, vz.x), vec3 (vx.y, vy.y, vz.y), vec3 (vx.z, vy.z, vz.z));
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

// variant of grey    https://shadertoy.com/view/4sVfWD
// variant of colored https://shadertoy.com/view/4dVfWD

void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;

    float y = floor( 6.*U.y/R.y -1. ),       
          n = y < 0. ? 1e-5 : exp2(y) ,   // checker scale
          x = (floor(U.x/n)*n - 32. ) / R.x,
          t = float(iFrame),
        //v = (1.+60.*x);
          v = exp2(log2(60.)*x);          // fps

    ivec2 I = ivec2(U/n);                 // chechers

    v = exp2( floor(log2(v)) ); // bands of same fps (or horrible aliasing)
                                // left: 60fps  right: swap at 2 fps

    O =  x < 0. ? vec4( pow( .5, 1./2.2 ) ) 
                : vec4( ( int(t/v) + I.x + I.y ) % 2 );
}

vec2 rot2(vec2 point, float alpha, vec2 center){
    vec2 temp;
    temp.x = center.x + (point.x - center.x) * cos(alpha) - (point.y - center.y) * sin(alpha);
	temp.y = center.y + (point.y - center.y) * cos(alpha) + (point.x - center.x) * sin(alpha);
    return temp;
}


float PseudoScalarProduct(vec2 a, vec2 b) {
    return a.x * b.y - b.x * a.y;
}

vec3 P(vec3 r){
    mat3 A = mat3(vec3(sin(iTime), 0., 0.),
                  vec3(0., cos(iTime), 0.),
                  vec3(0., 0., .0));
    vec3 B = A * r;
	return B;
}

float triangle (vec2 A, vec2 B, vec2 C, vec2 uv) {
    float p1 = PseudoScalarProduct(vec2(B.x - A.x, B.y - A.y), vec2(uv.x - A.x, uv.y - A.y));
    float p2 = PseudoScalarProduct(vec2(C.x - B.x, C.y - B.y), vec2(uv.x - B.x, uv.y - B.y));
    float p3 = PseudoScalarProduct(vec2(A.x - C.x, A.y - C.y), vec2(uv.x - C.x, uv.y - C.y));

    return step(3., abs(sign(p1) + sign(p2) + sign(p3)));
    //if ((p1 >= 0. && p2 >= 0. && p3 >= 0.) || (p1 <= 0. && p2 <= 0. && p3 <= 0.)) return 1.;
    //else return 0.;
}

float leftTriangle (vec2 uv) {
    vec2 A = vec2(-0.2, 0.2);
	vec2 B = vec2(-0.8, 0.2);
    vec2 C = vec2(-0.5, 0.8);

    vec2 center = vec2((A.x + B.x +C.x)/3., (A.y + B.y + C.y)/3.);

    float alpha = iTime;

    A = rot2(A, alpha, center);
    B = rot2(B, alpha, center);
    C = rot2(C, alpha, center);


    return triangle (A, B, C, uv);
}

float rightTriangle (vec2 uv) {
    vec2 A = vec2(0.2, 0.2);
	vec2 B = vec2(0.8, 0.2);
    vec2 C = vec2(0.5, 0.8);

    vec2 center = vec2((A.x + B.x +C.x)/3., (A.y + B.y + C.y)/3.);

    float alpha = sin(iTime) * 3. + iTime * 4.;

    A = rot2(A, alpha, center);
    B = rot2(B, alpha, center);
    C = rot2(C, alpha, center);

    return triangle (A, B, C, uv);
}

float centralTriangle(vec2 uv) {
    vec3 A = vec3(-0.2, -0.2, 1);
	vec3 B = vec3(0.2, -0.2, 1);
    vec3 C = vec3(0, 0.2, 1);

    A = P(A);
    B = P(B);
    C = P(C);

    return triangle (A.xy, B.xy, C.xy, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy * 2. -1.;
    uv.x *= iResolution.x / iResolution.y;

    //vec3 col = vec3(0.8);
    vec3 col = vec3(uv.x * uv.y, uv.y * -uv.x, uv.y);


    col += (vec3(1., 0., 0.) - col) * leftTriangle (uv);
    col += (vec3(uv.x, uv.y, uv.x * uv.y) - col) * rightTriangle(uv);
    col += (cos(iTime+uv.xyy+vec3(0,2,4)) - col) * centralTriangle(uv);


    fragColor = vec4(col,1.0);
}

