void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    mat2 rotation_matrix = mat2(cos(t), sin(t), -sin(t), cos(t));

    vec2 center = iResolution.xy / vec2(2.0, 2.0);
    vec2 st = fragCoord.xy - center;


    vec2 rt_hard = st * rotation_matrix;
    vec2 rt_soft = normalize(st) * rotation_matrix;


    vec3 color = vec3(mix(rt_hard, rt_soft, abs(tan(iTime / 2.0))), 0.5);
	fragColor = vec4(color, 1.0);

}

