// Cookie Collective rulz

#define ITER 100.
#define PI 3.141592
#define time iTime
#define EPS 0.01

#define MAT_SHIELD 0.
#define MAT_BACK 1.


mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}


vec2 moda (vec2 p, float per)
{
  float a = atan(p.y, p.x);
  float l = length(p);
  a = mod (a-per/2., per)-per/2.;
  return vec2(cos(a),sin(a))*l; 
}


float smin( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}


vec2 mat_min (vec2 a, vec2 b)
{
  if (a.x < b.x) return a;
  else return b;
}


float spiral (vec2 p, float sa, float b)
{
  float a = atan(p.y, p.x);
  float l = length(p);

  float n = (log(l/sa)/b - a) / (2.*PI);

  float upper_ray = sa * exp(b *(a + 2.*PI*ceil(n)));
  float lower_ray = sa * exp(b *(a + 2.*PI*floor(n)));

  return min(abs(upper_ray - l), abs(l-lower_ray));
}


float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r, abs(p.z)-h);}


float roots (vec3 p)
{
  p.x += sin(p.z)*0.3;
  p.xz = moda(p.xz, 2.*PI/7.);
  p.x -= 3.;
  p.y += sin(p.x)*0.5;
  return cyl(p.yzx, 0.2-p.x*0.2, 2.);
}


float base (vec3 p)
{
  p.xy -= vec2(0.8,2.5);
  p.x -= sin(p.y)*0.7;
  return cyl(p.xzy, 1.08-p.y*0.2, 2.);
}


float bud (vec3 p)
{
  float s = length(p)-2.35;
  p.x = spiral(p.xy, PI/5., .3);
  float d = cyl (p.xzy, 0.4-p.y*0.15, 1.5);
  d = max(s, d);
  return d;
}


float island (vec3 p)
{
  p.y += 2.5;
  float s = length(vec3(p.x*0.5, p.y+2.7, p.z*0.5))-5.;
  return s;
}


float pic (vec3 p)
{
  p.x += 0.7;
  p.x += sin(p.y*0.5);
  float c3 = cyl(vec3(p.x*2., p.z, p.y), 0.5-p.y*0.5, 1.5);
  return c3;
}


float pics (vec3 p)
{
  p.yz = moda(p.yz, 2.*PI/8.);
  p.y -= 6.;
  return min(cyl(vec3(p.x, p.z, p.y+3.), 0.3, 2.),pic(p));
}


float rings (vec3 p)
{  
  float c1 = cyl(p.yzx, 2.5, 0.1);
  float c2 = max(-cyl(p.yzx, 3.5,1.), cyl(p.yzx, 4., 0.05));
  return min (c1, c2);
}


float top_shield (vec3 p)
{
  p.xy *= rot(-PI/2.);
  float s = max(-p.y,length(p)-1.8);
  p.xz = moda(p.xz, PI/2.);
  float c = cyl(vec3(p.y-1., p.z, p.x), 0.5, 5.);
  return max(-c, s);
}


vec2 shield (vec3 p)
{
  float t1 = floor(time);
  float t2 = fract(time);

  t2 = pow(t2, 4.);
  float anim1 = PI/5.*(t1+ t2);
  float anim2 = floor(time)+sin(fract(time)*PI/2.);

  float anim = mix(anim1, anim2, 0.2);
  p.yz *= rot(anim);
  return vec2(min(min(rings(p), pics(p)),top_shield(p)), MAT_SHIELD);

}


vec2 background (vec3 p)
{
  float tree = smin(bud(vec3(p.x-2.85, p.y-5.9, p.z+0.1)),smin(roots(p),base(p), 0.5), 0.1);
  return vec2(min(island(p), tree), MAT_BACK);
}


vec2 SDF (vec3 p)
{
  vec2 b = background(p);
  p += vec3(3.,-4., 5.);
  return mat_min(shield(p/0.5)*0.5, b);
}


vec3 getnorm (vec3 p)
{
  vec2 eps = vec2(0.1,0.);
  return normalize(vec3(SDF(p+eps.xyy).x - SDF(p - eps.xyy).x,
                        SDF(p+eps.yxy).x - SDF(p - eps.yxy).x,
                        SDF(p+eps.yyx).x - SDF(p - eps.yyx).x
                        )
                   );
}


float lighting(vec3 p)
{
  vec3 n = getnorm(p);
  return dot(n, normalize(vec3(2.,10., -3.)))*0.5+0.5;
}


vec3 color_ramp(float t)
{
  float sm = smoothstep(0., 0.5, t);
  float sm2 = smoothstep(0.5, .8, t);
  vec3 col1 = mix(vec3(0.8, 0., 0.7), vec3(0.9,0.2,0.), sm);
  return mix(col1,vec3(0.9,0.8,0.), sm2);
}


vec3 getcam (vec3 ro, vec3 target, vec2 uv, float fov)
{
  vec3 forward = normalize(target - ro);
  vec3 right = normalize(cross(vec3(0.,1.,0.), forward));
  vec3 up = normalize(cross(forward, right));
  return forward * fov + right * uv.x + up * uv.y;
}


// courtesy of Alkama
vec3 pales (vec2 uv)
{
  uv *= rot(time*.3);
  return vec3(0.8,0.1,0.)+floor(cos(atan(uv.y, uv.x)*12.));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 ro = vec3(10.,5.,-10.0); 
  vec3 p = ro;
  vec3 target = vec3(-2.5, 3., 0.);
  vec3 rd = getcam(ro, target, uv, 1.);

  vec3 col = vec3(0.);
  float shad, pd, t=0., tmax=50.;
  vec2 d = vec2(0.);
  float outline = 1.;

  for(float i = 0.; i<ITER; i++)
  {
    pd = d.x;
    d = SDF(p);
  // Tricks from lnae : https://www.shadertoy.com/view/4tdyRj
  if (d.x > pd + EPS && d.x < 0.14) outline = 0.;

  if (d.x < EPS || t > tmax) break;
    t+=d.x*0.4;
    p = ro + rd * t;
  }

  if (t<=tmax)
  {
    if(d.y == MAT_BACK) col = mix(vec3(0.4,0.1,0.), vec3(0.9,0.8,0.8), lighting(p));
    if (d.y == MAT_SHIELD) col = color_ramp(lighting(p));
  }
  else col = vec3(0.8) + pales(uv-vec2(0.8))*(1.-length(uv))*0.4;

  col *= outline;
  col += texture(iChannel0, uv*0.5).rgb * 0.1;

  fragColor = vec4(col, 1.);
}

#define CS(a) vec2(cos(a),sin(a))

const float PI = 3.14159265359;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 destColor = vec3(0.1, 0.8, 0.6);
    float f = 0.0;

    for (float i = 0.0; i < 10.0; i++) {
        vec2 P = CS(iTime + i * 2.0 * PI / 10.0);
        P = abs(p + P);
        // ここは足す
        f += 0.001 / (P.x * P.y);

    }

    // Output to screen
    fragColor = vec4(vec3(destColor * f),1.0);
}

float N21(vec2 p)
{
    p = fract(p * vec2(6574.5414, 8961.8778));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}
vec2 N22(vec2 p)
{
    float x = N21(p);
    float y = N21(p + x);
    return vec2(x, y);
}
vec2 GetPos(vec2 id)
{
    vec2 n = N22(id) * 24.;
    return sin(n) * .4;
}
float sn(vec2 uv)
{
    vec2 lv = fract(uv * 10.0);
    vec2 id = floor(uv * 10.0);

    lv = lv * lv * (3. - 2. * lv);

    float bl = N21(id);
    float br = N21(id +vec2(1, 0));
    float b = mix(bl, br, lv.x);

    float tl = N21(id + vec2(0,1));
    float tr = N21(id +vec2(1, 1));
    float t = mix(tl, tr, lv.x);

    return mix(b, t, lv.y);
}
float stars(vec2 uv)
{

    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);
    vec2 pos = GetPos(id);
    float distance = length(gv - pos);
    float shineRate = abs(sin(iTime * 0.8 * N21(id)));

    float star = smoothstep(0.06, clamp(0.9 - sqrt(iTime / 10.0), 0.001, 0.059), distance) * shineRate;
    return star - 0.15;
}

vec3 animate(vec2 uv)
{
    float zoomout = sqrt((iTime * 0.25));
    float andromedaFade = sqrt(iTime / 8.);
    uv *= clamp(zoomout, 0.0, 1.8);

    vec3 spaceCol = vec3(0.0, 0.0, 0.10);
    vec3 andromedaColPink = vec3(0.8, 0.0, 0.7);
    vec3 andromedaColGreen = vec3(0.2, 0.9, 0.2);

    spaceCol += stars(uv);

    vec2 androUV = uv;
    float s = sin(iTime * 0.08);
    float c = cos(iTime * 0.08);
    mat2 rotmat = mat2(c, -s, s, c);
    //androUV -= iTime * 0.2;
    androUV *= rotmat;
    float andromedaPinkNoise = sn(androUV * 0.03) * clamp(andromedaFade, 0.0, 1.6);
    andromedaPinkNoise += sn(androUV * 0.25) * 0.125;
    andromedaPinkNoise += sn(androUV * 0.125) * 0.625;
    andromedaPinkNoise /= 5.0;
    spaceCol +=  mix(spaceCol, andromedaColPink, andromedaPinkNoise);


    s = sin(iTime * 0.03);
    c = cos(iTime * 0.03);
    rotmat = mat2(c, s, -s, c);
    vec2 androUV2 = uv;
    androUV2.y += iTime * 0.3;
    androUV2 *= rotmat;
    float andromedaGreenNoise = sn(androUV2 * 0.03) * clamp(andromedaFade, 0.0, 1.6);
    andromedaGreenNoise += sn(androUV2 * 0.25) * 0.125;
    andromedaGreenNoise += sn(androUV2 * 0.125) * 0.625;
    andromedaGreenNoise /= 3.0;
    spaceCol +=  mix(spaceCol, andromedaColGreen, andromedaGreenNoise);

    return spaceCol;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y * 10.;
    vec3 col = animate(uv);
    fragColor = vec4(col, 1.0);
}

