float sawsin(float x) {
    return 4.0 * abs( floor(x + 0.5 - 0.75) - x + 0.75) - 1.0;
}

float sawcos(float x) {
    return -4.0 * abs( floor(x + 0.5) - x) + 1.0;
}

const float PI = 3.14159;

float L1sinc(float x) {
    if (x == 0.0) return PI / 2.0;
    return sin(2.0 * PI * x) / (4.0 * x);	
}

float L1L2cos(float x) {
	return L1sinc(mod(x,0.5) - 0.25);
}

float L1L2sin(float x) {
	return L1L2cos(x + 0.25);
}

float Lsin(float x) {
	return sawsin(x)*mix(1.0, L1L2sin(x), sin(iTime)*0.5+0.5);
}

float Lcos(float x) {
	return sawcos(x)*mix(1.0, L1L2cos(x), sin(iTime)*0.5+0.5);
}

void cdist(vec2 c, float r, int i, float ph) {
	set_line_width_px(1.0);
    set_source_rgba(0.0,0.0,0.0,0.6);
    float iph = 1.0 / float(i);
    float n0 = ph * iph;
    move_to(r * vec2(Lcos(n0),Lsin(n0)));
    for (int k = 1; k <= i; ++k) {
    	float n = (float(k) + ph) * iph;
        line_to(r * vec2(Lcos(n),Lsin(n)));
    }
    stroke();
    for (int k = 0; k < i; ++k) {
    	float n = (float(k) + ph) * iph;
        reddot(r * vec2(Lcos(n),Lsin(n)));
    }
}

const float radius = 0.07;

vec2 cart2rad(vec2 p) {
    float n = abs(p.x) + abs(p.y);
    float i = 0.0;
    if (p.x < 0.0) {
        // works also for 
        i = 2.0*n - p.y;
    } else {
        i = p.y;
    }
    float nc = max(1.0,n)*4.0;
    float r = n*radius;
    float ph = mod(i / nc,1.0);
    //ph = ph * 2.0 * 3.1415926;
    return r * vec2(Lcos(ph), Lsin(ph));
}

void paint() {
    float t = iTime;


    set_source_rgba(vec4(1.0));
    clear();

    set_line_width_px(1.0);
    set_source_rgba(0.0, 0.0, 0.0, 1.0);

    float k = 0.0;
    int n0 = 0;
    int n1 = 1;
    for (int i = 0; i < 10; ++i) {
    	int n2 = n0 + n1;
        n0 = n1; n1 = n2;
        cdist(vec2(0.0), k, max(1,i*4), 0.0); 
        k = k + radius;
    }
 #if 1   
    for (int y = -9; y < 9; ++y) {
        for (int x = -9; x < 9; ++x) {
            int mx = max(abs(x),abs(x+1));
            int my = max(abs(y),abs(y+1));
            if ((abs(mx)+abs(my)) > 9)
                set_source_rgba(0.0,0.0,1.0,0.3);
            else
            	set_source_rgba(0.0,0.0,1.0,1.0);              
            move_to(cart2rad(vec2(x,y)));
            line_to(cart2rad(vec2(x+1,y)));
            line_to(cart2rad(vec2(x+1,y+1)));
            line_to(cart2rad(vec2(x,y+1)));
            close_path();
            stroke();
            if ((abs(x)+abs(y)) >= 9)
                set_source_rgba(0.0,0.0,1.0,0.3);
            else
            	set_source_rgba(0.0,0.0,1.0,1.0);              
            bludot(cart2rad(vec2(x,y)));
        }
    }
    #endif
}

