void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	 aspect = vec2(iResolution.x / iResolution.y, 1.0);
	 ScreenH = min(iResolution.x,iResolution.y);
	 AA = ScreenH*0.4;
	 AAINV = 1.0 / AA;

    init(fragCoord);

    paint();

    blit(fragColor);
}

#ifdef GLSLSANDBOX
void main() {
    mainImage(gl_FragColor, gl_FragCoord.xy);
}
#endif

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord-.5*iResolution.xy )/iResolution.y;

    // Time varying pixel color
     vec3 col = 0.5+ 0.5*sin(iTime+uv.xyx+vec3(0,2,4));

    float ray = iResolution.x*10.;
    vec2 center= vec2(iResolution.x/2.,iResolution.y/2.);
    float  xa =(fragCoord.x - center.x)*(cos(iTime));
    float xb = (fragCoord.y - center.y)*(cos(iTime));

    if( ( xa*xa + xb*xb ) <= ray){
         col = 0.5+ 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 c, in vec2 a )
{
    vec3 o = vec3(sin(iTime),2.3,cos(iTime));
    vec3 w = normalize(-o);
    vec3 u = normalize(cross(w,vec3(0,1,0)));
	vec3 d = mat3(u,cross(u,w),w)*
        normalize(vec3((2.*a-iResolution.xy)/max(iResolution.x,iResolution.y),.25));
    float t = 0., e = 1.e-4, m = 5., h = 1.;
    while (t < m && h > e)
    {
        h = length(o+d*t+vec3(0,0,1))-2.;
        t = min(m,t+h);
    }
    c = vec4(.2*t);
}

float xor(float a, float b)
{
    return a * (1. - b) + b * (1.0 - a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    float a = iTime * 0.3;
    float s = sin(a);
    float c = cos(a);


    uv *= mat2(c, -s, s, c);
    uv *= mix(15.0, 20.0,sin(iTime) * 0.5 + 0.5);

    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    float m = 0.0;
    float t = iTime;

    for(float y = -1.; y <= 1.0; y++)
    {
        for(float x = -1.; x <= 1.0; x++)
        {
            vec2 offs = vec2(x, y);
            float d = length(gv - offs);
            float dist = length(id + offs) * 0.3;

            float r = mix(.3, 1.5, sin(dist - t) * 0.5 + 0.5);
            m = xor(m, smoothstep(r, r * 0.9, d)) * 1.2;
        }
    }
    col += m;

    float uvL = length(uv) / 4.;
    col *= vec3(uvL * sin(iTime * 2.) * 0.5 + 0.5, uvL * cos(iTime * 4.) * 0.5 + 0.5,uvL * sin(iTime * 6.) * 0.5 + 0.5);
    fragColor = vec4(col, 1.0);
}

#define pi (3.14159265358979323846)
#define EPSILON (0.0001)

vec2 rotate(vec2 v, float a)
{
  float c = cos(a);
  float s = sin(a);
  return vec2(
    v.x*c-v.y*s,
    v.x*s+v.y*c
  );
}

float sphere(vec3 p, float r)
{
  return length(p)-r;
}

float scene(vec3 p)
{
  float b = sphere(p, 1.6);
  if(b > 0.001) return b; // optimisation

  vec3 disp = vec3(0);
  float f = 0.5;
  disp.x = texture(iChannel0, p.zy * 0.05 + iTime * 0.02).x * f;
  disp.z = texture(iChannel0, p.xy * 0.05 + iTime * 0.03).z * f;
  disp.y = texture(iChannel0, p.xz * 0.05 + iTime * 0.04).y * f;

  return sphere(p + disp, 1.0 + sin(iTime*2.4) * 0.15);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 cam = vec3(0, -0.15, -3.5);
  vec3 dir = normalize(vec3(uv,1));

  float cam_a2 = sin(iTime) * pi * 0.1;
  cam.yz = rotate(cam.yz, cam_a2);
  dir.yz = rotate(dir.yz, cam_a2);

  float cam_a = iTime * pi * 0.1;
  cam.xz = rotate(cam.xz, cam_a);
  dir.xz = rotate(dir.xz, cam_a);

  vec3 color = vec3(0.16, 0.12, 0.10);
  float t = 0.00001;
  const int maxSteps = 128;
  for(int i = 0; i < maxSteps; ++i) {
      vec3 p = cam + dir * t;
      float d = scene(p);

      if(d < 0.0001 * t) {
          color = vec3(1.0, length(p) * (0.6 + (sin(iTime*3.0)+1.0) * 0.5 * 0.4), 0);
          break;
      }

      t += d;
  }

  fragColor.rgb = color;
  fragColor.a = 1.0;
}

const float minDrawDist = 0.0;
const int   maxRayMarcs = 500;
const float cellSize    = 900.0;
const float ballRadius  = 120.0;
vec4 color;
vec3 camPos;

// Thank, Íñigo Quílez...)
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float getDist(vec3 rayPos)
{
    rayPos.x -= sin(iTime) * 800.0;
    rayPos.y -= cos(iTime) * 800.0;
    rayPos.z += float(iFrame) * 20.0;

    vec3 vc = camPos + floor(-((camPos - rayPos) - ballRadius) / cellSize) * cellSize;
    vec3 vl = vc; vl.x -= cellSize;
    vec3 vr = vc; vr.x += cellSize;
    vec3 vu = vc; vu.y -= cellSize;
    vec3 vd = vc; vd.y += cellSize;
    vec3 vb = vc; vb.z -= cellSize;
    vec3 vf = vc; vf.z += cellSize;

    float r    = 50.0;
    float dist = min(
        min(
            sdCapsule(rayPos, vl, vr, r),
            sdCapsule(rayPos, vu, vd, r)
        ),  sdCapsule(rayPos, vb, vf, r)
    );

    float distToBall = distance(vc, rayPos) - ballRadius;

    if (distToBall <= 0.1) {
        dist = distToBall;
        color = vec4(abs(atan(iTime)), 0, abs(tan(iTime)), 1.0);
    }

	return dist;
}

vec3 getNormal(vec3 p)
{
    float d = getDist(p);
    const vec2 e = vec2(0.01, 0.0);

    vec3 n = d - vec3(
	    getDist(p - e.xyy),
	    getDist(p - e.yxy),
	    getDist(p - e.yyx)
    );

    return normalize(n);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec4 outputPixel = vec4(0.0);

	vec2 position = gl_FragCoord.xy;

	camPos = vec3(
		iResolution.x / 2.0, 
		iResolution.y / 2.0, 
		-600.0
	);

	vec3 rayPos = vec3(position, 0.0);
	vec3 rayVec = normalize(rayPos - camPos);

	color = vec4(abs(cos(iTime)), .2, abs(sin(iTime)), 1.0);

	vec3 curRayPos = rayPos * minDrawDist;
	for (int i = 0; i < maxRayMarcs; i ++)
	{ 
		float distToBall = getDist(curRayPos);
		if (distToBall < .1)
		{
			vec3  normal = getNormal(curRayPos);
            float light  = dot(normalize(camPos - curRayPos), normal);

            light = clamp(light, 0.0, 1.0) + 0.02;
			light *= 1000000.0 / pow(abs(curRayPos.z - camPos.z), 2.0);
			outputPixel = pow(light, 1.0 / 1.8) * color;
			break;

		} else
		{
			curRayPos += rayVec * 30.0;
		}
	}

	fragColor = outputPixel;
}

