vec3 getNormal(vec3 p) {

    // This is a somewhat hacky way of getting the normal for this heightmap
    // The above getNormal function is imperfect due to the non-continous heightmap

    // Get the normal of the value noise (not the stepped heightmap)

    vec2 eps = vec2(0.005, 0.00);
    vec3 normal = vec3(
    	(valueNoise((p.xz + eps.xy) * HSCALE) * VSCALE) - (valueNoise((p.xz - eps.xy) * HSCALE) * VSCALE),
		2.0 * eps.x,
		(valueNoise((p.xz + eps.yx) * HSCALE) * VSCALE) - (valueNoise((p.xz - eps.yx) * HSCALE) * VSCALE)
    );

    // Work out whether the point is in a horizontal or vertical section
    float stepEps = 0.012;
    float stepSection = mod(p.y / STEPHEIGHT, 1.0);
    bool isOnCliff = true;
    if (stepSection < stepEps || stepSection > (1.0 - stepEps)) {
    	isOnCliff = false;
    }

    // Adjust the normal accordingly
    if (isOnCliff) {
    	normal = vec3(normal.x, 0.0, normal.z);
    }
    else {
    	normal = vec3(0.0, 1.0, 0.0);
    }

    // Normalise and return the normal
    normal = normalize(normal);
    return normal;

}

vec3 getDiffuse(vec3 p) {

    // Get which band the point belongs to
    float eps = 0.01;
    p.y += eps;
    float height = floor(p.y / STEPHEIGHT) * STEPHEIGHT;

    // Color function from iq: https://www.shadertoy.com/view/Xds3zN
    vec3 col = 0.45 + 0.35*sin(vec3(0.15,0.08,0.10)*(height*124.2));

    // Increase saturation
    col = increaseSaturation(col);

    // Darken the lower colors
    col = mix(vec3(0.0), col, 0.1 + 0.9 * (height / VSCALE));

    return col;

}

vec3 getColor(vec3 p) {

    // Directional light source
    vec3 lightDir = normalize(vec3(0.8, 1.0, -0.8));

    // The intensity/color of light (all three values are the same for white light)
    vec3 lightCol = vec3(1.0);

    // Applies the 'base color' of the light
    vec3 baseLightCol = vec3(1.0, 1.0, 1.0);
    lightCol *= baseLightCol;

    // Applies normal-based lighting
    vec3 normal = getNormal(p);
    float normalLight = max(0.05, saturate(dot(normal, lightDir)));
    //float normalLight = max(0.1, step(0.5, dot(normal, lightDir)));
    lightCol *= normalLight;

    // Gets the diffuse lighting
    vec3 diffuse = getDiffuse(p);//vec3(0.368, 0.372, 0.901);

    // Get the final color
    vec3 col = lightCol * diffuse;
    return col;

}

vec3 render(vec3 ro, vec3 rd) {

    vec3 skyCol = vec3(0.9, 0.9, 0.9);
    float fogFalloff = 0.1;
    float fogDist = 90.0;

    float t = raytrace(ro, rd);

    vec3 col = vec3(0.0);

    // If the terrain is hit
    if (t >= 0.0) {

        // Get the color of the terrain
        col = getColor(ro + t * rd);

        // Add the fog (better fog functions exist though)
        float fogAmount = exp(fogFalloff * (t - fogDist));
        col = mix(col, skyCol, fogAmount);

    }

    // If the terrain is not hit
    else {
        col = skyCol;
    }

    return col;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // Normalises the fragCoord
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv - 0.5;
    p.x *= iResolution.x/iResolution.y;

    // Gets the direction of the ray and the origin
    vec3 ro = vec3(0.0, VSCALE + 2.0, 0.0) + vec3(1.0, 0.0, 1.0) * iTime * 0.5;
    vec3 rd = normalize(vec3(p, 1.4));

    // Rotates the ray depending on the mouse position. I lifted this from
    // https://www.shadertoy.com/view/XtGGRt, but it seems to be the common approach
    vec2 mo = iMouse.xy / iResolution.xy-.5;
    mo = (mo==vec2(-.5))?mo=vec2(0.16, -0.1):mo; // Default position of camera
    mo.x *= iResolution.x/iResolution.y;
    mo *= 3.0;
    rd.yz *= mm2(mo.y);
    rd.xz *= mm2(mo.x);

    // Render and output the ray to screen
    vec3 col = render(ro, rd);
    float gamma = 2.2;
    col = pow(col, vec3(1.0 / gamma));
    fragColor = vec4(col,1.0);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = fragCoord - iResolution.xy / 2.0;
    coord /= iResolution.x;

    vec2 offset = round(coord / 0.025) * 0.025;
    vec4 col = vec4(0.0);

    for(int i = 0; i < 3; i++) {
        float t = iTime + float(i)*2.1 + (abs(coord.x) + abs(coord.y))*10.0;
        float r = (pow(sin(t), 3.0) + 1.0) * 0.02;

        if(length(coord - offset) < r) {
           col[i] = 1.0;
        }
    }

    fragColor = col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Wyznaczam środek ekrani
    vec2 zero = iResolution.xy/2.0;
    vec3 col;
    //defniniuję kolory, dla tła gradient zależny od czasu:
    vec3 background = vec3(((sin(iTime/5.0)+1.0)/2.0), fragCoord.x/iResolution.x, fragCoord.y/iResolution.y);
    //Ciemnoszary dla koloru linii
    vec3 line = vec3(0.1, 0.1, 0.1);
    //Zielony dla kuleczki
    vec3 ball = vec3(0.2, 0.9, 0.0);
    //Białawy dla drogi
    vec3 road = vec3(0.9, 0.9, 0.9);
    //Określam położenie piłeczki
    vec2 ballvec = vec2(zero.x-(sin((sin(iTime/4.0)*50.0+100.0)/20.0+iTime*3.0)*110.0), sin(iTime/4.0)*50.0+110.0);
    //Ustawiam domyślny kolor piksela jako kolor tła
    col = background;
    //Rysuję piłeczkę pierwszą (na ostatnim planie) kiedy jest po lewej stronie
    if (length(fragCoord-ballvec)<10.0 && sin((sin(iTime/4.0)*50.0+100.0)/20.0+iTime*3.0) >= 0.0) {col = ball;}
    //Tworzę drogę
    if (fragCoord.y <= iResolution.y/2.0){
        //Określam ruchy obydwu krawędzi drogi
        float x1 = zero.x-fragCoord.x-(sin(fragCoord.y/20.0+iTime*3.0)*100.0);
        float x2 = zero.x-fragCoord.x-(sin(fragCoord.y/20.0+iTime*3.0)*120.0);
        //Warunkuję kolorowanie drogi przez znajdowanie się pomiędzy granicami
        if (fragCoord.x-zero.x > x2*100.0 && fragCoord.x-zero.x < x1*110.0 || fragCoord.x-zero.x < x2*100.0 && fragCoord.x-zero.x > x1*110.0)col=road;
        //Koloruję obrys drogi
        if (abs(x1)<2.0 || abs(x2)<2.0){
            col=line;
        }
    }
    //Maluję piłeczkę ostatnią (na pierwszym planie) kiedy jest po prawej stronie
    if (length(fragCoord-ballvec)<10.0 && sin((sin(iTime/4.0)*50.0+100.0)/20.0+iTime*3.0) <= 0.0) {col = ball;}
    //Uzupełniam obraz
    fragColor = vec4(col,1.0);
}

//Generate periodic coordinates from p.
//Coords repeat in x,y with period c.x, c.y
vec2 opRep( in vec2 p, in vec2 c)
{
    return mod(p,c)-0.5*c;
}


// signed distance to a regular octogon
float sdOctogon( in vec2 p, in float r )
{
  // pi/8: cos, sin, tan.
  const vec3 k = vec3(-0.9238795325,   // sqrt(2+sqrt(2))/2 
                       0.3826834323,   // sqrt(2-sqrt(2))/2
                       0.4142135623 ); // sqrt(2)-1 

  // reflections
  p = abs(p);
  p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
  p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);

  // Polygon side.
  return length(p - vec2(clamp(p.x, -k.z*r, k.z*r), r))*sign(p.y - r);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 2.0*fragCoord/iResolution.xy -vec2(1.0);

    //Generate periodic coords
    vec2 rep_uv = opRep(uv, vec2(0.5));

    //Pass periodic coords to distnace function to create repeating shape
    float d = sdOctogon(rep_uv, 0.2);

    vec3 col = vec3(smoothstep(0.0, 1.0, d)*iTime*10.0) * vec3(0.4, 0.9, 0.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.141592

const vec3 cPos = vec3(0.0, 0.0, -2.0);
const vec3 cDir = vec3(0.0, 0.0, 1.0);
const vec3 cUp = vec3(0.0, 1.0, 0.0);
const float depth = 1.0;
const vec3 lPos = vec3(10.0, 10.0, -10.0);

vec3 rotate(vec3 p, float angle, vec3 axis) {
  vec3 a = normalize(axis);
  float s = sin(angle);
  float c = cos(angle);
  float r = 1.0 - c;
  mat3 m =
      mat3(a.x * a.x * r + c, a.y * a.x * r + a.z * s, a.z * a.x * r - a.y * s,
           a.x * a.y * r - a.z * s, a.y * a.y * r + c, a.z * a.y * r + a.x * s,
           a.x * a.z * r + a.y * s, a.y * a.z * r - a.x * s, a.z * a.z * r + c);
  return m * p;
}

float sphere(vec3 p, float r) { return length(p) - r; }

float fractal(vec3 p) {
  float result = 100.0;
  for (float i = 0.0; i < 10.0; i++) {
    p = rotate(p, iTime, vec3(0.2, 0.3, 0.5));
    result = min(result, sphere(p, exp(-i / 1.2)));
    result =
        max(result, -sphere(p, exp(-i / 1.2 * abs(1.5 * sin(iTime / 2.0)))));
    p = abs(p) - exp(-i / 1.2) / 2.0;
  }
  return result;
}

float distFunc(vec3 p) { return fractal(p); }

vec3 getNormal(vec3 p) {
  float d = 0.001;
  return normalize(
      vec3(distFunc(p + vec3(d, 0.0, 0.0)) - distFunc(p + vec3(-d, 0.0, 0.0)),
           distFunc(p + vec3(0.0, d, 0.0)) - distFunc(p + vec3(0.0, -d, 0.0)),
           distFunc(p + vec3(0.0, 0.0, d)) - distFunc(p + vec3(0.0, 0.0, -d))));
}

vec3 rayMarching(vec3 color, vec2 p) {
  vec3 cSide = cross(cDir, cUp);
  vec3 ray = normalize(cSide * p.x + cUp * p.y + cDir * depth);
  vec3 rPos = cPos;
  float rLen = 0.0;
  for (float i = 0.0; i < 22.0; i++) {
    float distance = distFunc(rPos);
    color = clamp(vec3(0.8, 0.2, 0.6) / distance, 0.0, 1.0);
    if (distance < 0.01) {
      vec3 normal = getNormal(rPos);
      float diffuse = clamp(dot(normal, -lPos), 0.0, 1.0) + 0.03;
      color = (color / diffuse / 50.0);
      break;
    }
    rLen += distance;
    rPos = cPos + rLen * ray;
  }
  return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 p =
      (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
  vec3 color = rayMarching(vec3(0.0), p);
  fragColor = vec4(color, 1.0);
}

// Returns the matrix that rotates a given point by 'a' radians

mat2 mm2(in float a) {

    float c = cos(a);
    float s = sin(a);
    return mat2(c, s, -s, c);

}

float saturate(float t) {

    return clamp(t, 0.0, 1.0);

}

// Hash functions by Dave Hoskins: https://www.shadertoy.com/view/4djSRW

vec3 hash33(vec3 p3) {
	p3 = fract(p3 * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);

}

float sphereDist(vec3 p) {

    // Get the position of the current box and position in that box
    float boxSize = 2.0;
    vec3 i = floor(p / boxSize) * boxSize;
    vec3 f = fract(p / boxSize) * boxSize;

    // Get the position of the center of the ball inside the box
    // Designed so that the ball can't within a certain radius of the boundary of the box
    float ballRadius = 0.025;
    float edgeRadius = 0.1;
    float radiusSum = ballRadius + edgeRadius;
    vec3 hashVal = hash33(i);
    //vec3 relativeSpherePos = vec3(boxSize / 2.0);
    vec3 relativeSpherePos = vec3(radiusSum) + hashVal * (boxSize - 2.0 * radiusSum);

    // Get the distance to the sphere
    float sphereDist = distance(f, relativeSpherePos) - ballRadius;

    // Get the distance to the nearest possible other circle box
    float xDist = min(distance(f.x, -edgeRadius), distance(f.x, boxSize + edgeRadius));
    float yDist = min(distance(f.y, -edgeRadius), distance(f.y, boxSize + edgeRadius));
    float zDist = min(distance(f.z, -edgeRadius), distance(f.z, boxSize + edgeRadius));
    float nearestBox = min(xDist, min(yDist, zDist));

    // Calculate and return a lower bound on the distance to the nearest sphere
    float dist = min(sphereDist, nearestBox);
    return dist;

}

float distanceMap(vec3 p) {

  	float dist = sphereDist(p);
    return dist;

}

float raymarch(vec3 ro, vec3 rd) {

    // Parameters
    int maxSteps = 200;
    float maxDist = 250.0;
    float surfaceDist = 0.005;

    // Main raymarching loop
    float t = 0.0;
    for (int i = 0; i < maxSteps; i++) {

        vec3 p = ro + t * rd;
        float d = distanceMap(p);

        // A surface is found
        if (d < surfaceDist) {
        	return t;
        }

        // The ray has travelled too far from the origin
        if (t > maxDist) {
        	return -1.0;
        }

        t += d;
    }

    // Too many steps were taken from the origin
    return -1.0;

}

vec3 getNormal(vec3 p) {

    // Samples neighbouring points to generate a normal vector
    vec2 eps = vec2(0.005, 0.00);
    float dist = distanceMap(p);
    vec3 normal = vec3(
    	dist - distanceMap(p - eps.xyy),
		dist - distanceMap(p - eps.yxy),
		dist - distanceMap(p - eps.yyx)
    );
    normal = normalize(normal);
    return normal;

}

vec3 getColor(vec3 p) {

    // Directional light source
    vec3 lightDir = normalize(vec3(1.0, 1.0, -1.0));

    // The intensity/color of light (all three values are the same for white light)
    vec3 lightCol = vec3(1.0);

    // Applies the 'base color' of the light
    vec3 baseLightCol = vec3(1.0, 1.0, 1.0);
    lightCol *= baseLightCol;

    // Applies normal-based lighting
    vec3 normal = getNormal(p);
    float normalLight = saturate(dot(normal, lightDir));
    lightCol *= normalLight;

    // Applies hard-shadow lighting
    //float eps = 0.01;
    //bool isInShadow = (raymarch(p + normal * eps, lightDir) > 0.00);
    //if (isInShadow) {
    //	lightCol *= 0.3;
    //}

    // Gets the diffuse lighting
    // Color function from iq https://www.shadertoy.com/view/Xds3zN
   	//vec3 diffuse = 0.45 + 0.35*sin(vec3(0.05,0.08,0.10)*(iTime*5.0));
    //vec3 diffuse = vec3(0.992, 0.627, 0.251);
    vec3 diffuse = vec3(0.663, 0.388, 0.106);

    // Get the final color
    vec3 col = lightCol * diffuse;
    return col;

}

vec3 render(vec3 ro, vec3 rd) {

    float t = raymarch(ro, rd);

    vec3 col = vec3(0.0);

    if (t >= 0.0) {
    	col = getColor(ro + t * rd);
    }

    else {
        col = vec3(0.9, 0.9, 0.9);
    }

    return col;

}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // Normalises the fragCoord
    vec2 uv = fragCoord/iResolution.xy;
    vec2 p = uv - 0.5;
    p.x *= iResolution.x/iResolution.y;

    // Gets the direction of the ray and the origin
    vec3 ro = vec3(0.0, 0.0, 0.0) + vec3(0.0, 1.0, 0.0) * iTime;
    vec3 rd = normalize(vec3(p, 0.9));

    // Rotates the ray depending on the mouse position. I lifted this from
    // https://www.shadertoy.com/view/XtGGRt, but it seems to be the common approach
    vec2 mo = iMouse.xy / iResolution.xy-.5;
    mo = (mo==vec2(-.5))?mo=vec2(-0.2,0.2):mo; // Default position of camera
    mo.x *= iResolution.x/iResolution.y;
    mo *= 3.0;
    rd.yz *= mm2(mo.y);
    rd.xz *= mm2(mo.x);

    // Render and output the ray to screen
    vec3 col = render(ro, rd);
    float gamma = 2.2;
    col = pow(col, vec3(1.0 / gamma));
    fragColor = vec4(col,1.0);

}

#define HIT_NONE -1.
#define HIT_WATER 0.
#define HIT_OPAQUE 1.
#define MAX_DIST 10.
#define EPSILON .001
#define MIN_DIST EPSILON
#define RAYMARCH_STEPS 400

const float uRefraction = 1.33;
const float uWaveAmp = 0.051;
const float uWaveFreq = 0.1;
float uWaterHeight = 0.75;
const float uWaterWidth = 0.988;
const float uShape = 1.0;


mat2 Rotate(float r) {
    float c = cos(r);
    float s = sin(r);
    return mat2(c, s, -s, c);
}

float Box(vec3 p, vec3 size) {
    vec3 d = abs(p) - size;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float Sphere(vec3 p, float radius) {
    return length(p) - radius;
} 

float GetNoise(vec3 p) {
    float time = iTime * 0.005;
    vec4 noise = texture(iChannel0, (p.xz * 0.01 + time));
    noise += texture(iChannel0, (p.xz * 0.09+ time));
    noise += texture(iChannel0, (p.xz * 0.1+ time));
    float wave = (0.05 * (uWaveAmp * 4.0)) * sin((uWaveFreq * 90.0) * (p.x + p.z *0.5) + iTime * 2.0);
    wave *= sin(time + p.x * cos(time + p.z)) * 3.0;
    return wave + length(noise * 0.1) * uWaveAmp;
}

vec2 GetWater(vec3 p) {    
    float wave = GetNoise(p);
    float water = mix(Sphere(p - wave, uWaterWidth * uWaterHeight), Box(p + vec3(0.0, 0.2, 0.0), vec3(uWaterWidth, uWaterHeight + wave, uWaterWidth)), uShape);
    float block = Box(p + vec3(0.0, -0.85, 0.0), vec3(uWaterWidth + 0.02, 3.0, uWaterWidth + 0.02));

    water = max(water, block);

    return vec2(water - 0.015, HIT_WATER);
}

vec2 GetOpaque(vec3 p) {
    float balls = Sphere(p + vec3(-0.1, -0.0, 0.0), 0.2);

    balls = min(balls, Sphere(p + vec3(0.4, -0.1, 0.0), 0.3));

    balls = min(balls, Box(p + vec3(0.0, 0.17, 0.0), vec3(40.0, 1.0, 40.0)*vec3(1.0, 0.03, 1.0)));

    balls = min(balls, Box(p + vec3(0.0, 0.0, 0.4), vec3(0.2, 1.3, 0.1)));
    balls = min(balls, Box(p + vec3(0.4, 0.0, -0.4), vec3(0.1, 1.0, 0.1)));

    float block = Box(p + vec3(0.0, -0.85, 0.0), vec3(uWaterWidth + 0.02, 1.01, uWaterWidth + 0.02));

    balls = max(balls, block);

    //balls = min(balls, p.y + 0.15);

    return vec2(balls, HIT_OPAQUE);
}

vec2 MinScene(vec2 a, vec2 b) {
    if (a.x < b.x) return a;
    return b;
}

vec2 GetOpaqueScene(vec3 p) {
    vec2 balls = GetOpaque(p);
    return balls;
}

vec2 GetTransparentScene(vec3 p) {
    vec2 water = GetWater(p);
    return water;
}

vec2 GetScene(vec3 p) {
    vec2 transparent = GetTransparentScene(p);
    vec2 opaque = GetOpaqueScene(p);
    vec2 scene = MinScene(transparent, opaque);

    return scene;
}

vec3 GetNormal(vec3 p) {
    vec2 e = vec2(0.0, EPSILON);
    return normalize(GetScene(p).x - vec3(
        GetScene(p - e.yxx).x,
        GetScene(p - e.xyx).x,
        GetScene(p - e.xxy).x
    ));
}

vec3 GetOpaqueNormal(vec3 p) {
    vec2 e = vec2(0.0, EPSILON);
    return normalize(GetOpaqueScene(p).x - vec3(
        GetOpaqueScene(p - e.yxx).x,
        GetOpaqueScene(p - e.xyx).x,
        GetOpaqueScene(p - e.xxy).x
    ));
}

vec2 RayMarchAll(vec3 ro, vec3 rd) {
    float t = 0.0;

    for (int i = 0; i < RAYMARCH_STEPS; ++i) {
        vec2 scene = GetScene(ro + rd * t);
        if (scene.x < MIN_DIST) {
            return vec2(t, scene.y);
        }
        t += scene.x * 0.5;
        if (t > MAX_DIST) break;
    }

    return vec2(MAX_DIST, HIT_NONE);
}

vec2 RayMarchOpaque(vec3 ro, vec3 rd) {
    float t = 0.0;

    for (int i = 0; i < RAYMARCH_STEPS; ++i) {
        vec2 scene = GetOpaqueScene(ro + rd * t);
        if (scene.x < MIN_DIST) {
            return vec2(t, scene.y);
        }
        t += scene.x;
        if (t > MAX_DIST) break;
    }

    return vec2(MAX_DIST, HIT_NONE);
}

float NormalDistributionFunctionGGXTR(vec3 n, vec3 m, float a)
{
    float a2 = a * a;
    float NdotM = max(0.0, dot(n, m));
    float NdotM2 = NdotM * NdotM;
    float denom = (NdotM * (a2 - 1.0) + 1.0);
    denom = 3.14 * (denom * denom);
    return a2 / denom;
}

float GeometryGGX(float NdotV, float a)
{
    float k = (a + 1.0) * (a + 1.0) / 8.0;
    return NdotV / (NdotV * (1.0 - k) + k);
}

float GeometrySmith(float NdotV, float NdotL, float a)
{
    float g1 = GeometryGGX(NdotL, a);
    float g2 = GeometryGGX(NdotV, a);
    return g1 * g2;
}

vec3 FresnelSchlick(vec3 v, vec3 h, vec3 F0)
{
    float VdotH = max(0.0, dot(v, h));
    return F0 + (1.0 - F0) * pow(1.0 - VdotH, 5.0);
}

vec3 ShadePBR(vec3 n, vec3 v, vec3 l, vec3 albedo, float roughness, float metallic, out vec3 BRDF, float shadow)
{
    vec3 F0 = mix(vec3(0.04), albedo, metallic);
    vec3 h = normalize(v + l);
    roughness = roughness * roughness;
    float NdotL = max(0.0, dot(n, l));
    float NdotV = max(0.0, dot(n, v));
    float D = NormalDistributionFunctionGGXTR(n, h, roughness);
    vec3 F = FresnelSchlick(v, h, F0);
    float G = GeometrySmith(NdotV, NdotL, roughness);
    vec3 Kd = (1.0 - F) * (1.0 - metallic);
    vec3 radiance = vec3(5.0, 4.0, 3.0);
    vec3 num = D * F * G;
    float denom = 4.0 * NdotL * NdotV;
    vec3 specularBRDF = num / max(denom, .01) * shadow;

    BRDF = specularBRDF;

    return (albedo * 0.08) + ((Kd * albedo / 3.14 + specularBRDF) * radiance * (NdotL * shadow));
}

float Shadow(vec3 P, vec3 N, vec3 L, float noise) {

    float t = 0.01;
    vec3 rd = L;
    vec3 ro = P;
	float shadowContrib = 1.0;
    for (int i = 0; i < 30; i++)
    {
    	vec2 scene = GetOpaqueScene(ro + rd * t);
        shadowContrib = min(shadowContrib, 2.*scene.x / t);
        t += clamp(scene.x, 0.02, 0.1);
        if (scene.x < EPSILON || t > 0.9) break;
    }

    return clamp(shadowContrib, 0.0, 1.0);
}

vec3 ShadeOpaque(vec3 P, vec3 N, vec3 V, vec3 L) {
    vec3 P2 = P * 2.0;
    vec3 brdfRef;
    vec3 shade = ShadePBR(N, V, L, vec3(1.0, 0.5, 0.2), 0.4, 0.0, brdfRef, Shadow(P, N, L, 0.0));
    float checker = mod(floor(P2.x) + floor(P2.y) + floor(P2.z), 2.0);
    float NdotL = max(0.6, dot(N, L));

    return mix(shade, shade * 0.8, checker);
}

vec3 ShadeOpaqueInWater(vec3 P, vec3 N, vec3 V, vec3 L) {
    float noise = GetNoise(P);
    vec3 P2 = P * 2.0;
    vec3 brdfRef;
    vec3 shade = ShadePBR(normalize(0.2 * P + N), V, L, mix(vec3(0.1, 0.2, 0.3), vec3(1.0, 0.5, 0.2), pow((P.y + 0.14) / uWaterHeight, 2.0)), 1.0, 0.0, brdfRef, Shadow(P, N, L, noise * 1.0));
    float checker = mod(floor(P2.x) + floor(P2.y) + floor(P2.z), 2.0);

    shade *= 2.0;
    shade = mix(shade, shade * 0.8, checker);


    vec2 scene = RayMarchAll(P + N * 0.1, N);
    vec3 p = (P + N * 0.1) + N * scene.x;

    shade = mix(shade, shade * 8.2, noise * 7.0);
    return shade ;
}

vec3 ShadeTransparent(vec3 P, vec3 N, vec3 V, vec3 L) {
    vec3 brdfRef;
    vec3 shade = ShadePBR(N, V, L, vec3(0.0, 0.0, 0.0), 0.09, 0.0, brdfRef, 1.0);

    vec3 rd = normalize(reflect(-V, N));
    vec3 ro = P + rd * 0.01;
    vec2 scene = RayMarchOpaque(ro, rd);
    if (scene.y > HIT_NONE) {
        vec3 p = ro + rd * scene.x;
        vec3 n = GetOpaqueNormal(p);
        vec3 v = normalize(-rd);
        vec3 opaque = ShadeOpaque(p, n, v, L);
        return opaque * 0.5;
    }
    return brdfRef;
}

vec3 GetColor(vec3 ro, vec3 rd, vec3 lightDir, out float dist)
{
    vec3 water = vec3(0.0);
    vec3 color = vec3(0.0);
    vec2 scene = RayMarchAll(ro, rd);
	vec3 V = -normalize(rd);

    dist = scene.x;

    if (scene.y > HIT_NONE) {
        vec3 p = ro + rd * dist;
        vec3 n = GetNormal(p);
        if (scene.y == HIT_WATER) {
            water = ShadeTransparent(p, n, V, lightDir);
            rd = normalize(refract(rd, n, 1.0 / uRefraction));
            ro = p;
            scene = RayMarchOpaque(ro, rd);
            p = ro + rd * scene.x;
            n = GetOpaqueNormal(p);
            dist = scene.x;
            color = ShadeOpaqueInWater(p, n, V, lightDir) + water;
        } else {
            color = ShadeOpaque(p, n, V, lightDir);
        }
    }
    return color;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 lightDir = normalize(vec3(0.2, 0.1, -0.2));
    vec3 color = vec3(.0);

    vec2 ar = vec2(iResolution.x / iResolution.y, 1.0);
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * ar;
    vec3 ro = vec3(uv.x, uv.y + 1.5, -4.0);
    vec3 rd = normalize(vec3(uv.x, uv.y - 0.85, 3.0));

    float mx = (iMouse.x / iResolution.x * 2.0 - 1.0) * 3.14;
    float my = (iMouse.y / iResolution.y * 2.0 - 1.0);

    uWaterHeight += clamp(my, -0.3, 0.8);

    ro.xz *= Rotate(mx);
    rd.xz *= Rotate(mx);

    vec2 scene = RayMarchAll(ro, rd);
    float dist = scene.x;

	color = GetColor(ro, rd, lightDir, dist);

    color *= 1.0 - (dist / MAX_DIST);

    // tone mapping
    color = color / (color + 1.0);

    // gamma correction
    color = pow(color, vec3(1.0 / 2.2));


    fragColor = vec4(color, 1.0);
}

float noise(vec3 uv) {
  return fract( sin( dot( uv ,vec3(123.,65.,44.) ) ) * 4811.424 );
}

float pnoise(vec3 uv,float f) {

  vec3 t = uv * f;
  vec3 v1 = floor(t);
  vec3 v2 = fract(t);

  float a0 = noise(v1 + vec3(0.,0.,0.));
  float b0 = noise(v1 + vec3(1.,0.,0.));
  float c0 = noise(v1 + vec3(0.,1.,0.));
  float d0 = noise(v1 + vec3(1.,1.,0.));

  float a1 = noise(v1 + vec3(0.,0.,1.));
  float b1 = noise(v1 + vec3(1.,0.,1.));
  float c1 = noise(v1 + vec3(0.,1.,1.));
  float d1 = noise(v1 + vec3(1.,1.,1.));

  float o1 = mix(a0,b0,v2.x);
  float o2 = mix(c0,d0,v2.x);
  float o3 = mix(o1,o2,v2.y);

  float o4 = mix(a1,b1,v2.x);
  float o5 = mix(c1,d1,v2.x);
  float o6 = mix(o4,o5,v2.y);

  float o7 = mix(o3,o6,v2.z);

  return o7;
}

float fbm(vec3 uv , float f ) {
  float o = 0.;
  float n = 1.;

  for ( int i = 0 ; i < 5 ; ++ i ) {
    o += pnoise( uv , f ) * n;
    f *= 2.; 
    n *= 0.5;
  }

  return o;
}


mat4 createVTW(vec3 eye,vec3 center,vec3 up) {
  vec3 f = normalize( center - eye );
  vec3 s = normalize( cross(f,up) );
  vec3 u = cross(s,f);
  return mat4(
    vec4(s,0.),
    vec4(u,0.),
    vec4(-f,0.),
    vec4(0.,0.,0.,1)
  );
}


vec3 rayDirection(float fieldofView,vec2 size,vec2 fragCoord) {
  vec2 xy = fragCoord - size / 2.0;
  float z = size.y / tan(radians(fieldofView) / 2.0 );
  return normalize(vec3(xy,-z));
}


vec2 sdf(vec3 p) {

  vec3 p2 = p + vec3( iTime * .15 , iTime * -.08 , iTime * -.12 );

  //cloud sphere
  float cloudPlane = dot( p + vec3(0,-0.4,0) , vec3(0.,1.,0.) );
  float l0 = length( p + vec3(1.7,-0.15,1.0) ) - 0.7;
  float l1 = length( p + vec3(-0.8,-0.15,-1.0) ) - 0.7;
  float l2 = min(l0,l1);
  float cl = max(l2,-cloudPlane);

  //cloud detail
  float l4 = fbm( p2 , 5.0 ) - 0.8;
  float l5 = mix ( cl , l4 , .2 );

  //plane
  float l6 = dot( p , vec3(0.,1.,0.));

  if ( l5 < l6 ) {
    return vec2(l5,1);
  } else {
    return vec2(l6,2);
  }

}


vec3 createnormal(vec3 p) {

  float e = 0.0001;

  return normalize( vec3(
    sdf( vec3(p.x+e,p.y,p.z) ).x - sdf( vec3(p.x-e,p.y,p.z) ).x ,
    sdf( vec3(p.x,p.y+e,p.z) ).x - sdf( vec3(p.x,p.y-e,p.z) ).x ,
    sdf( vec3(p.x,p.y,p.z+e) ).x - sdf( vec3(p.x,p.y,p.z-e) ).x
  ));

}


vec3 lightingMaterial00 ( vec3 lightvec , vec3 p ) {

  vec3 normal = createnormal(p);
  vec3 s = p + normal * 0.01;

  float result = 0.9;
  float depth = 0.1;
  float minlight = 0.1;

  for( int I=0; I< 20 ; ++I ) {

    vec3 p2 = s + lightvec * depth;
    float len = sdf(p2).x;
    result = min( result , (len * 2.6 / depth) + minlight ) ;
    depth += len ;

    if( len < 0.00001 ) {
      result = minlight;
      break;
    }

  }

  return mix( vec3(0.2,0.1,0.9) , vec3( result ), 0.9 );

}





vec3 lightingMaterial01 ( vec3 lightvec , vec3 p ) {

  vec3 normal = createnormal(p);
  vec3 s = p + normal * 0.01;

  float result = 1.;
  float depth = 0.;
  float minlight = .1;

  for( int I=0; I< 15 ; ++I ) {

    vec3 p2 = s + lightvec * depth;
    float len = sdf(p2).x;
    result = min( result , len * 4.6 / depth + minlight );
    depth += len ;

    if( len < 0.001 ) {
      result = minlight;
      break;
    }

  }

  return mix ( vec3( 0.2 , 0.1 , 0.0 ) , vec3 ( result ) , .5 );

}


vec4 render( vec2 fragCoord , vec3 eye , vec3 worldDir , float start , float end ) {

  vec3 lightvec = normalize( vec3( 1.0 ,1.2, -0.8) );

  vec3 sky = vec3(0.25,0.4,0.65);
  vec3 cloud = vec3(0.,0.,0.);
  vec3 background = sky;
  float ratio = 1.0;

  float depth = start;
  for(int I=0;I<200;++I) {

    vec3 p = eye + worldDir * depth;
    if ( 17. < depth ) {
      break;
    }

    vec2 ss = sdf(p);
    float len = ss.x;
    int material = 0;

    if( len < 0.01 ) {

      material = int(ss.y);

      if ( material == 1 ) {
        ratio = min( ratio , 0.9 );
        vec3 l = lightingMaterial00( lightvec , p );
        cloud = mix(cloud , l , ratio);
        ratio *= .7;
        if ( ratio < 0.0001 ) {
          break;
        }
        depth += max(len, 0.001 * noise( worldDir * iTime ) + 0.002 );
      }

      if ( material == 2 ) {
        vec3 ground = lightingMaterial01( lightvec , p );
        background = ground;
        break;
      }

    } else {
      depth += max(len, 0.01 );
    }

  }


  //vec3 o = mix ( background , cloud , ( 1. - ratio ) );
  vec3 o = background + cloud * .9 ; //* (1.0 - ratio);
  return vec4( o , 1. );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord * (1.0/iResolution.x) - vec2(0.5, 0.5);

    float t = iTime * .1;
//    vec3 eye = vec3( sin(t) * 5. , 0.2, cos(t) * 5. );

    vec3 eye = vec3( 5. ,2.5, 4. );
    vec3 center = vec3(0.,0.,0.);
    vec3 up = vec3(0.,1.,0.);
    mat4 vtw = createVTW(eye,center,up);

    vec3 viewDir = rayDirection(45.,iResolution.xy,fragCoord);
    vec3 worldDir = (vtw * vec4(viewDir,0.)).xyz;

   fragColor = render( fragCoord , eye , worldDir , 0. , 1000. );
}

// variant of https://shadertoy.com/view/wdBGWz


void mainImage(out vec4 O, vec2 u) {
