const float FLT_MAX = 1.0 / 0.0;
const int MAX_NB_BOUNCES = 3;
const int PIXEL_SAMPLING_SIZE = 25;
const int PIXEL_SAMPLING_GRID_SIZE = 5;
const int NB_LIGHTS = 2;

int subObjectIdC = 0;

struct Material
{
    float Ka;	// ambiant        coefficient
    float Kd;	// diffuse        coefficient
    float Ks;	// specular       coefficient
    float Kn;	// specular power coefficient
};



struct ShadeInfo
{
	vec3 shadeCol;
    float Ks;
};


struct LightInfo
{
	vec3 pos;
    vec3 col;
    float pow;
};
LightInfo lights[NB_LIGHTS];
//parametre camera
vec3 camPos=vec3(6,4,-5);

const vec3 camObj = vec3(3,1,-8);
const float camFovY = 90.0;

//parametre sphere
 vec3 center = camObj + vec3(0,1,2);
const float rayonS = 1.0;
const vec3 sphereCol = vec3(1.0,0,0);
const Material sphereMat = Material(0.2,0.7,1.0,50.0);
const int      sphereId     = 1;

//parametre lumiere
const vec3     ambiantCol   = vec3(0,0,1);		// blue

const vec3     light1Col     = vec3(1,1,1);		// white
      vec3     light1Pos     = vec3(8,10,-12);
const float    light1Pow = 0.8;

const vec3     light2Col     = vec3(1,1,0.5);		// white/yellow
      vec3     light2Pos     = vec3(3,10,1);
const float    light2Pow = 0.5;




// Plane parameters
const vec3     planePos     = vec3(0, 0.1, 0);
const vec3     planeNormal  = vec3(0, 1.0, 0);
const vec3     planeCol1    = vec3(1.0);		// white
const vec3     planeCol2    = vec3(0.4);		// gray
const Material planeMat     = Material(0.2/*Ka*/, 1.0/*Kd*/, 0.2/*Ks*/,  5.0/*Kn*/);
const int      planeId      = 2;



// Sky parameters
const vec3     skyCol       = vec3(0);			// black
const int      skyId        = 0;

const int antialliazing = 1;


//CSG objects parameters
const vec3     csgSphere1Pos 	= camObj + vec3(-1.125, 2, 0);
const float    csgSphere1Radius = 1.4;
const vec3	   csgSphere2Pos 	= camObj + vec3(1.125, 2, 0);
const float	   csgSphere2Radius = 1.4;
const vec3	   csgSphere3Pos 	= camObj  + vec3(0, 2.5, 0);
const float	   csgSphere3Radius = 0.5;
const vec3	   csgSphere4Pos 	= camObj  + vec3(0, 2.4, -0.5);
const float	   csgSphere4Radius = 0.5;
const vec3	   csgCol1 			= vec3(1.0, 0.5, 0.0); //orange
const vec3	   csgCol2 			= vec3(0.4, 1.0, 1.0); //light cyan
const vec3     csgCol3			= vec3(1.0, 0.0, 1.0); //magenta
const vec3     csgCol4			= vec3(5.0, 5.0, 0.0); //hyper yellow
const Material csgMat 			= Material(0.2, 1.0, 0.1, 90.0);
const int 	   csgId			= 3;



float raySphere(vec3 rayPos, vec3 rayDir,vec3 center, float rayonS,out vec3 intersecPt, out vec3 normal)
{
	vec3 diff = rayPos - center ; 
     float a =       dot(rayDir, rayDir);
    float b = 2.0 * dot(diff  , rayDir);
    float c =       dot(diff  , diff  ) - rayonS * rayonS;

    float d = b*b - 4.0*a*c;


    if (d >= 0.0)
    {
        float sd = sqrt(d);
    	float t1 = (-b - sd ) / (2.0 * a);
    	float t2 = (-b + sd ) / (2.0 * a);

       float dir = 1.0;
       float t = -1.0;
        if (t1 > 0.0 )
          t = t1;
        else if (t2 > 0.0)
        {
        	t = t2;
        }else
            return t;
        intersecPt = rayPos + t * rayDir;
        normal     = normalize(intersecPt - center) * dir;

        return t;

     }
    return -1.0;
}



float rayPlane(    vec3 rayPos    ,     vec3 rayDir, vec3 planePos, vec3 planeNormal,
               out vec3 intersecPt, out vec3 normal)
{
    float den = dot(planeNormal, rayDir);
    if (abs(den) <= 0.000001)
        return -1.0;
    float t = dot(planeNormal, planePos - rayPos) / den;
    intersecPt = rayPos + t * rayDir;
     normal = -sign(den) * planeNormal;

    return t;

}

float rayCSG(vec3 rayPos, vec3 rayDir, out vec3 interPt, out vec3 normal, out int subObjectId)
{
	vec3 interPt1;
    vec3 normal1;
    vec3 interPt2;
    vec3 normal2;
    vec3 interPt3;
    vec3 normal3;
    vec3 interPt4;
    vec3 normal4;

    float dist1 = raySphere(rayPos, rayDir, csgSphere1Pos, csgSphere1Radius, interPt1, normal1);
    float dist2 = raySphere(rayPos, rayDir, csgSphere2Pos, csgSphere2Radius, interPt2, normal2);
    float dist3 = raySphere(rayPos, rayDir, csgSphere3Pos, csgSphere3Radius, interPt3, normal3);   
    float dist4 = raySphere(rayPos, rayDir, csgSphere4Pos, csgSphere4Radius, interPt4, normal4);


    if (dist1 > 0.0) //intersection s1/s2
    {	
        float dist12 = distance(interPt1, csgSphere2Pos);
        if (dist12 < csgSphere2Radius){ 
        	float dist14 = distance(interPt1, csgSphere4Pos); // s1 vers s4
            if (dist14 >csgSphere4Radius){
            	interPt= interPt1;
                subObjectId = 1;
                normal = normal1;
                return dist1;
            	}
            	float dist41 = distance(interPt4, csgSphere1Pos);
            	float dist42 = distance(interPt4, csgSphere2Pos);

        	if ((dist41 < csgSphere2Radius) && (dist42 < csgSphere1Radius ) && (dist4 > 0.0))
             { 
                interPt= interPt4;
                subObjectId = 4;
                normal = normal4;
                return dist4;
            }
                }
            }

    if (dist2 > 0.0) //intersection s2/s1
    {	
        float dist21 = distance(interPt2, csgSphere1Pos);
        if (dist21 < csgSphere1Radius)
        {
            float dist24= distance(interPt2, csgSphere4Pos);
            if  (dist24 > csgSphere4Radius)
            {
                interPt= interPt2;
                subObjectId = 2;
                normal = normal1;
                return dist2;
            }
            dist24 = distance(interPt4, csgSphere2Pos);
            float dist14 = distance(interPt4, csgSphere1Pos);
            if ((dist24 < csgSphere2Radius) && (dist14 < csgSphere1Radius) && (dist4 > 0.0)){
            	interPt = interPt4;
                 subObjectId = 4;
                 normal = normal4; 
                 return dist4;
            }
                }

    //intersection s3/s4

    if (dist3 > 0.0){
        // distance sphere 3 / origine 4
    	float dist34 = distance(interPt3, csgSphere4Pos);
		if (dist34     > csgSphere4Radius)  
        {
        	interPt = interPt3;
            subObjectId = 3;
            normal = normal3; 
            return dist3;
        }
        float dist43 = distance( interPt4, csgSphere3Pos);
            if ((dist43 < csgSphere3Radius) && (dist4 > 0.0))
            {
            interPt = interPt4;
            subObjectId = 4;
            normal = normal4; 
            return dist4;
            }
    	}
	}
return -1.0;
}

void computeCameraRayFromPixel( in vec2 pixCoord, out vec3 rayPos, out vec3 rayDir)
{

   float focal = 1.0 / tan(radians(camFovY) / 2.0);
   vec3 cz = normalize(camObj - camPos);
     vec3 up = vec3(0,1,0);
   vec3 cx = normalize(cross(-up, cz));
   vec3 cy = normalize(cross( cz, cx));

   vec2 pt = (2.*pixCoord - iResolution.xy) / iResolution.y;

   rayPos = camPos;
   rayDir = normalize(pt.x * cx - pt.y * cy + focal * cz);
}


float computeNearestIntersection(vec3 rayPos, vec3 rayDir,
                                 out int objectId, out vec3 intersecI, out vec3 normalI)
{
 	 float minDist  = FLT_MAX;
          objectId = skyId;   
    vec3 intersecS, normalS;

   //test sphere
      float distS = raySphere(rayPos, rayDir, center, rayonS, intersecS, normalS);
    if ((distS > 0.0) && (distS < minDist))
    {
        objectId  =  sphereId;
        minDist   =     distS;
        intersecI = intersecS;
          normalI =   normalS;
    }
    //test CSG
     vec3 intersecC, normalC;
    float distC =  rayCSG(rayPos, rayDir,intersecC, normalC, subObjectIdC);
    if ((distC > 0.0) && (distC < minDist))
    {
        objectId  =   csgId;
        minDist   =   distC;
	    intersecI =   intersecC;
    	normalI   =   normalC;
    }

    //test plane
     vec3 intersecP, normalP;
    float distP =  rayPlane(rayPos, rayDir,  planePos,  planeNormal, intersecP, normalP);
    if ((distP > 0.0) && (distP < minDist))
    {
        objectId  =   planeId;
        minDist   =     distP;
	    intersecI = intersecP;
    	  normalI =   normalP;
    }
       if (objectId == skyId)
        minDist = -1.0;

    return minDist;

}



vec3 getSphereColorAtPoint(vec3 pt)
{
    return sphereCol;
}

vec3 getPlaneColorAtPoint(vec3 pt)
{
    vec3 worldX = vec3(1,0,0);
    vec3 axisX  = normalize(worldX - dot(worldX, planeNormal) * planeNormal);

     vec3 axisY  = normalize(cross(planeNormal, axisX));

     vec3 diff = pt - planePos; 
    float u = dot(diff, axisX);
    float v = dot(diff, axisY);

    return mod(floor(u * 0.5) + floor(v * 0.5), 2.0) < 1.0  ? planeCol1 : planeCol2;

}
vec3 getCSGColorAtPoint(vec3 pt) 
{
	if (subObjectIdC == 1)
        return csgCol1;
    if (subObjectIdC == 2)
        return csgCol2;
    if (subObjectIdC == 3)
        return csgCol3;
    if (subObjectIdC == 4)
        return csgCol4;
    return vec3(3);
}



vec3 getObjectColorAtPoint(int objectId, vec3 pt, out Material objectMat)
{
    if (objectId == sphereId)
    {
        objectMat = sphereMat;
        return getSphereColorAtPoint(pt);
    }
    else if (objectId == planeId)
    {
        objectMat = planeMat;
        return getPlaneColorAtPoint(pt);
    }
    else if (objectId == csgId) 
    {
        objectMat = csgMat;
        return getCSGColorAtPoint(pt);
    }

    return skyCol;
}



vec3 computePhongShading(vec3 objectCol, Material objectMat, float shadowFactor, vec3 N, vec3 L, vec3 R, vec3 V, LightInfo lights )
{

     vec3 ambiant  = objectMat.Ka * ambiantCol;


    vec3 diffuse  = objectMat.Kd *  objectCol * lights.col *     max(dot(N,L), 0.)*shadowFactor;
    vec3 specular = objectMat.Ks *              lights.col * pow(max(dot(R,V), 0.), objectMat.Kn)*shadowFactor;

    vec3 phongCol = ambiant + diffuse + specular;

    return phongCol;   
}

float getShadowFactorAtPoint(vec3 I, vec3 N, Material objectMat, vec3 L, float Ldist)
{
	int object;
    vec3 inter;
    vec3 nI;
    float shadowPoint;



    float intersec =computeNearestIntersection(I, L,object,  inter, nI);
    if ((intersec < 0.0) || (intersec > Ldist))
    {	shadowPoint = 0.5;
    }else {
        shadowPoint = 0.0;
}
return  shadowPoint;

}

void AnimeScene(float time)
{
 	//animate the sphere position
    const float pi = 3.1415926535;
    const float rs = 2.0;
    const float spr = 5.0;
    float as = 2.0 * pi * time / spr;

    center = camObj + rs * vec3(-sin(as),0.0,cos(as))+vec3(0,1,0);

    //anime ligth position
    light1Pos += vec3(0,10.5 +9.5 * cos(time)-10.,0);

    float targetDist = length(camObj - camPos);
    camPos -=vec3(0,0,targetDist);
    camPos += targetDist*vec3(sin(time), max(sin(time*0.5),0.),cos(time));
}



vec4 RaytraceAtPixelCoord_RT(vec2 pixCoord)
{
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(pixCoord, rayPos, rayDir);

    ShadeInfo infos[MAX_NB_BOUNCES];
    int nbBounces = 0;


    int objectId;
    vec3 intersecI, normalI;

    vec3 color;


    do
    {


        float distI = computeNearestIntersection(rayPos, rayDir, objectId, intersecI, normalI);
        vec3 color;
        Material objMat;


        if (distI <= 0.0) //pt dans le ciel
        {
            infos[nbBounces].shadeCol = skyCol;
            infos[nbBounces].Ks = 0.0 ; 
            break;
        }


        //objets

        vec3 V =- rayDir;
        vec3 objColor = getObjectColorAtPoint(objectId, intersecI, objMat);
         vec3 I = intersecI + normalI*0.01;
         vec3 RayRefract = 2.0 * normalI * dot(normalI, V) - V;

         for (int l = 0 ; l < NB_LIGHTS ; l++){   		
            vec3 L = normalize(lights[l].pos - intersecI);

           vec3 R = normalize(2.0 * normalI * dot(normalI, L) - L);


            float Ldist = length(lights[l].pos - I);

             float shadowFactor = getShadowFactorAtPoint(I,normalI, objMat,L,Ldist);
             color += computePhongShading(objColor, objMat,  shadowFactor,normalI, L, R, V, lights[l]);

         }
            infos[nbBounces].shadeCol= color;
            infos[nbBounces].Ks = objMat.Ks;

          rayDir = RayRefract;
             rayPos = I;

               nbBounces++;


        }

    while (nbBounces < MAX_NB_BOUNCES);
    vec3 resCol = vec3(0);

    for (int i =0; i<=nbBounces; i++)
    {
        resCol = infos[nbBounces -i].Ks *resCol + infos[nbBounces - i].shadeCol; 

    }

    return vec4(resCol,1);
}



vec2 noise2(vec2 location , vec2 delta)
{
	const vec2 c= vec2 (12.9898 , 78.233);
    const float m = 43758.5453;
    return vec2(fract(sin(dot(location + delta,c))*m),
                      fract(sin(dot(location+ vec2(delta.y,delta.x),c)) * m));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float time = iTime;
     AnimeScene(time);

    lights[0].pos = light1Pos;
    lights[0].col = light1Col;
    lights[0].pow = light1Pow;
    lights[1].pos = light2Pos;
    lights[1].col = light2Col;
    lights[1].pow = light2Pow;


    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(fragCoord, rayPos, rayDir);

    int  objectId; 
    vec3 intersecI, normalI;


    float distI = computeNearestIntersection(rayPos, rayDir, objectId, intersecI, normalI);

    float pixel = 1.0/iResolution.y;
    float sousPixel = pixel/float(PIXEL_SAMPLING_GRID_SIZE); 
    vec4 finalColor = vec4(0);


    if (antialliazing == 0)
    {
         for (int i = 0; i < PIXEL_SAMPLING_GRID_SIZE; i++)
    {
        	for (int j = 0 ; j < PIXEL_SAMPLING_GRID_SIZE; j++)
        {
           finalColor +=RaytraceAtPixelCoord_RT(fragCoord + vec2(float(i) * sousPixel, float(j) * sousPixel));

        }
    }
    finalColor /=pow(float(PIXEL_SAMPLING_GRID_SIZE), 2.0);
    }else {
        vec2 delta = vec2 (600.0, 5468.265);
        for (int i= 0; i < PIXEL_SAMPLING_SIZE; i++)
    {
            finalColor += RaytraceAtPixelCoord_RT(fragCoord + noise2(fragCoord, delta) * pixel);
        }
        finalColor /= float(PIXEL_SAMPLING_SIZE);
    }


    fragColor= finalColor;
}

