// Understandable form
void mainImage( out vec4 color, in vec2 cord )
{
    // Normalized pixel coordinates (from -1 to 1) on the center
     vec2 u = 2. * cord/iResolution.xy - 1.;

    // Storing the dot value in u.x to later use
    // Second sin is for the wave
    // u.y * 1e2 are the lines
    // dot(u,u) equates to distance * distance
    // 1.5 for the line thickness
    float wave = (9. / exp(u.x = dot(u,u) ) 
                			* sin(6. * (u.x - iTime) ));
     color = vec4( 
         keyToggle(49)
         ? wave
         : 1.5 - sin( wave + u.y * 1e2));
}

#define MAX_ITERATION 100

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{ 
	vec2 p = (2.0*fragCoord.xy - iResolution.xy) / iResolution.y;
    vec2 c = p + vec2(-0.745, 0.0);
    vec2 z = vec2(0.0);

	int iter;
    for (iter = 0 ;iter < MAX_ITERATION && length(z) <= 20.0; ++iter) {
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;
    }

    //http://iquilezles.org/www/articles/mset_smooth/mset_smooth.htm
    float smoothIter = float(iter) - log2(log2(dot(z,z))) + 4.0; 
    vec3 color = 0.5 + 0.5*cos(3.0 + smoothIter*0.15 + vec3(0.0,cos(iTime),sin(iTime)));

    fragColor = vec4(color, 1.0);
}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

float Circle(vec2 uv, vec2 pos , float r, float blur){
    float d = length(uv  - pos);

    return smoothstep(r, r - blur, d);
}

float gearEmpty(vec2 uv, vec2 pos, float scale, float blur){
 	uv -= pos;
    uv *= rotate2d(3.0 + iTime);
    uv /= scale;
    float a = atan(uv.x, uv.y);

    float r = length(uv)*2.0;

    float f = smoothstep(-.5,1., cos(a*10.))*0.2+0.5;
    float step1 = smoothstep(f - blur,f,r);
    float step2 = smoothstep(f,f+blur,r);;
    return step1 - step2;
}

float gearFull(vec2 uv, vec2 pos, float scale, float blur){
 	uv -= pos;
    uv *= rotate2d(3.0 + iTime);
    uv /= scale;
    float a = atan(uv.x, uv.y);

    float r = length(uv);

    float f = smoothstep(-.5,1., cos(a*10.))*0.2+0.5;    
    return 1.0 -smoothstep(f,f+blur,r);;
}

float gearFull(vec2 uv, vec2 pos, float scale, float high, float blur){
 	uv -= pos;
    //uv *= rotate2d(3.0 + iTime);
    uv /= scale;
    float a = atan(uv.x, uv.y);

    float r = length(uv);

    float f = smoothstep(-.5,1., cos(a*10.))*0.2 * high +0.5;    
    return 1.0 -smoothstep(f,f+blur,r);;
}

float plot (vec2 uv, float pct){    
  return  smoothstep( pct-0.01, pct, uv.y) -
          smoothstep( pct, pct+0.01, uv.y);
}

float cosLine(vec2 uv, float pct){
    uv *= 2.;
    uv.y -= smoothstep(-0.8, 1.,cos(uv.x*20.0)) * 0.2 ;
    return plot(uv, pct);
}

float Smiley(vec2 uv, vec2 p, float size, float blur){
    uv -= p;    
    uv /= size;
    float mouth = Circle(vec2(uv.x, uv.y / sin(iTime)), vec2(0.), .3, blur);
    mouth-= Circle(vec2(uv.x, uv.y / abs(sin(iTime))), vec2(0., .1), .3, blur);

    float mask = Circle(uv, vec2(.0), .4, blur);
    mask -= Circle(uv, vec2(-.13, .2), .07, blur);
    mask -= Circle(uv, vec2(.13, .2), .07, blur);   


    if(uv.y < 0.05)
       mask -= mouth;
    return mask;
}

float Band(float t, float start, float end, float blur){
 	float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);
    return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur){
	float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band1 * band2;
}

float Line(vec2 uv, vec2 begin, vec2 end, float width, float blur){
    uv -= begin;
 	vec2 div = end - begin;
    uv *= rotate2d(atan(div.x, div.y));
    float d = length(div);
    return Rect(uv, - width/2., width/2., .0, d, blur);        
}

float Wheel(vec2 uv, vec2 pos, float scale, float blur){
    //gearFull((uv-pos)*rotate2d(3.0 + iTime), vec2(.0), scale, .5, blur)
 	float result = gearFull(uv, pos, scale, .2, blur) - Circle(uv, pos, .45 * scale, blur);
    for(int i = 0; i < 12; i++)
        result += Line(uv, pos, vec2(pos.x, pos.y + 0.45* scale) * rotate2d(float(i)*3.14 * 2./12.), .01, blur/5.); 
    return result;
}
float GearChain(vec2 uv, vec2 pos, float scale, float blur){
    float result = gearFull(uv, pos, scale, .5, blur) - Circle(uv, pos, .15 * scale, blur);
    return result;
}

float Chain(vec2 uv){
    float result = (Circle(uv, vec2(-.1,-.25) , .09, .001) - Circle(uv, vec2(-.1,-.25) , .086, .001))*
        (1.0 - Circle(uv, vec2(-.15,-.25) , .09, .001));
    result += Circle(uv, vec2(-.4, -.2) , .03, .001) - Circle(uv, vec2(-.4, -.2) , .026, .001);
    result *= (1.0 - Circle(uv, vec2(-.38, -.205) , .03, .001));
    result += Line(uv, vec2(-.1, -.162), vec2(-.4, -.173), .004, .001);
    result += Line(uv, vec2(-.12, -.335), vec2(-.4, -.23), .004, .001);
    return result;
}

float Rama(vec2 uv){
 	float result = Line(uv, vec2(-.4, -.2), vec2(-.1,-.25), .02, .001);
    result += Line(uv, vec2(-.1, .0), vec2(-.4, -.2), .02, .001);
    result += Line(uv, vec2(-.1, -.25), vec2(-.1, .05), .02, .001);
    result += Line(uv, vec2(.4, -.2), vec2(.35, .15), .02, .001);
    result += Line(uv, vec2(-.1, -.25), vec2(.35, .08), .02, .001);
    result += Line(uv, vec2(-.1, .0), vec2(.35, .08), .02, .001);
    result += Line(uv, vec2(.3, .13), vec2(.4, .18), .02, .001);
    result += Line(uv, vec2(-.15, .05), vec2(.0, .05), .02, .001);
    return result;
}

vec3 Vechicle(vec2 uv, vec2 pos, float scale, float blur){
    uv -= pos;
    uv /= scale;
    vec2 w_pos1 = vec2(-.4, -.2), w_pos2 = vec2(.4, -.2);

    float wls = Wheel((uv-w_pos1)*rotate2d(iTime), vec2(.0, .0), .38, .01) +  
        Wheel((uv-w_pos2)*rotate2d(iTime), vec2(.0, .0), .38, .01);

    float smile = Smiley(uv, vec2(.0, .3), .2, .01);

    float rama = Rama(uv);
    float g_chain1 = GearChain((uv-vec2(-.1, -.25))*rotate2d(iTime), vec2(.0, .0), .15, .01);
    float g_chain2 = GearChain((uv-vec2(-.4, -.2))*rotate2d(iTime), vec2(.0, .0), .05, .01);
    float chain = Chain(uv);

    vec3 result = vec3(1., .0, 1.) * wls*(1.0 - rama);
    result += vec3(.0, .0, 1.) * rama;
    result += vec3(1., 1., 1.) * g_chain1;
    result *= 1.0 - g_chain2;
    result += vec3(1., 1., 1.) * g_chain2;
    result *= 1.0 - chain;
    result += vec3(1., .0, .0) * chain;
    //result -= vec3(.0, .0, 1.) * (wls*rama);

    return result;
}






