Material getMaterial (Ray ray) {
	Material material;
	if (ray.object.id == 1) {
		float squareSize = 1.0;
		vec3 p = mod ( ray.position - plane_1.center - ray.epsilon, squareSize * 2.0 );
		float s = squareSize;
		float l = s*0.5;
		float h = s*0.5;
		if ( ( p.x < l && p.y < l && p.z < l ) ||
			 ( p.x > h && p.y < l && p.z > h ) ||
			 ( p.x < l && p.y > h && p.z > h ) ||
			 ( p.x > h && p.y > h && p.z < l ) ) {
			material = material_plane_1b;
		} else {
			material = material_plane_1w;
		}
	} else if (ray.object.id == 2) {
		float squareSize = 0.25;
		vec3 p = mod ( ray.position - plane_2.center - ray.epsilon, squareSize * 2.0 );
		float s = squareSize;
		float l = s;
		float h = s;
		if ( ( p.x < l && p.y < l && p.z < l ) ||
			 ( p.x > h && p.y < l && p.z > h ) ||
			 ( p.x < l && p.y > h && p.z > h ) ||
			 ( p.x > h && p.y > h && p.z < l ) ) {
			material = material_plane_2b;
		} else {
			material = material_plane_2w;
		}
	} else if (ray.object.id == 3) {
		material = material_sphere_1;
	} else if (ray.object.id == 4) {
		material = material_sphere_2;
	} else if (ray.object.id == 5) {
		material = material_sphere_3;
	} else if (ray.object.id == 6) {
		material = material_sphere_4;
	} else if (ray.object.id == 7) {
		material = material_box_1;
	} else if (ray.object.id == 8) {
		material = material_box_2;
	} else if (ray.object.id == 9) {
		material = material_box_3;
	} else if (ray.object.id == 10) {
		material = material_box_4;
	}
	return material;
}

