//Пересечение луча со сферой
float intersectSphere(Ray ray, Sphere sphere, inout vec3 normal) {
	float t0, t1, t;

	vec3 l = sphere.center - ray.origin;
	float tca = dot(l, ray.direction);
	if ( tca < 0.0 ) return ray.far;
	float d2 = dot (l, l) - (tca * tca);
	float r2 = sphere.radius*sphere.radius;
	if ( d2 > r2 ) return ray.far;
	float thc = sqrt(r2 - d2);
	t0 = tca - thc;
	t1 = tca + thc;

	if ( t0 < 0.0 ) {
		t = t1;
	} else if ( t1 < 0.0 ) {
		t = t0;
	} else {
		t = min(t0,t1);
	}
	if (t<0.0) return ray.far;
	vec3 p = ray.origin + t * ray.direction;
	float flagInside = -sign(dot(ray.direction, p - sphere.center));
	normal = normalize ( p - sphere.center ) * flagInside;
	return t;
}
//Пересечение луча с плоскостью
float intersectPlane(Ray ray, Plane plane, inout vec3 normal) {
	float det = dot ( plane.normal, ray.direction );
	if (abs(det) < ray.epsilon) return ray.far;

	float t = - ( dot ( plane.normal, ray.origin ) + length(plane.center) ) / dot ( plane.normal, ray.direction );

	if ( t < 0.0 ) return ray.far;

	float flagInside = -sign(dot(ray.direction, plane.normal));
	normal = plane.normal;
	normal *= flagInside;

	return t;	
}
//Пересечение луча с кубом
float intersectBox(Ray ray, Box box, inout vec3 normal) {

	vec3 minp = box.center - box.size;
	vec3 maxp = box.center + box.size;

	float tmin = (minp.x - ray.origin.x) / ray.direction.x;
	float tmax = (maxp.x - ray.origin.x) / ray.direction.x;
	if (tmin > tmax) { 
		float s = tmin; 
		tmin = tmax; 
		tmax = s; 
	}
	float tymin = (minp.y - ray.origin.y) / ray.direction.y;
	float tymax = (maxp.y - ray.origin.y) / ray.direction.y;
	if (tymin > tymax) { 
		float s = tymin; 
		tymin = tymax; 
		tymax = s; 
	}
	if ((tmin > tymax) || (tymin > tmax)) return ray.far;
	if (tymin > tmin) tmin = tymin;
	if (tymax < tmax) tmax = tymax;
	float tzmin = (minp.z - ray.origin.z) / ray.direction.z;
	float tzmax = (maxp.z - ray.origin.z) / ray.direction.z;
	if (tzmin > tzmax) { 
		float s = tzmin; 
		tzmin = tzmax; 
		tzmax = s; 
	}
	if ((tmin > tzmax) || (tzmin > tmax)) return ray.far;
	if (tzmin > tmin) tmin = tzmin;
	if (tzmax < tmax) tmax = tzmax;

	float t;
	if ( tmin < ray.epsilon && tmax < ray.epsilon ) return ray.far;
	if ( tmin < ray.epsilon ) {
		t = tmax;
	} else if ( tmax < ray.epsilon ) {
		t = tmin;
	} else {
		t = min(tmin, tmax);
	}

	float flagInside = 1.;
	if ( tmin < ray.epsilon && tmax > ray.epsilon ) flagInside = -1.;

	vec3 p = ray.origin + t * ray.direction;

	if ( abs(p.x - maxp.x) < ray.epsilon ) {
		normal = vec3(1,0,0) * flagInside;
	} else if ( abs(p.x - minp.x) < ray.epsilon ) {
		normal = vec3(-1,0,0) * flagInside;
	} else if ( abs(p.y - maxp.y) < ray.epsilon ) {
		normal = vec3(0,1,0) * flagInside;
	} else if ( abs(p.y - minp.y ) < ray.epsilon ) {
		normal = vec3(0,-1,0) * flagInside;
	} else if ( abs(p.z - maxp.z ) < ray.epsilon ) {
		normal = vec3(0,0,1) * flagInside;
	} else {
		normal = vec3(0,0,-1) * flagInside;
	}
	return t;
}

//---------------------------------------------------------------------------------
void rayMarch(inout Ray ray) {
	ray.origin += ray.near * ray.direction;

	ray.hit = false;
	ray.distance = Infinity;
	ray.position = ray.origin + ray.distance * ray.direction;
	ray.normal = vec3(0);
	ray.object.id = -1;

	Sphere sphere;
	Plane plane;
	Box box;

	float d;
	vec3 normal;

	//Горизонтальная поверхность
	plane = plane_1;
	d = intersectPlane ( ray, plane, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
		ray.object.id 	= 1;
	}

	//Вертикальная поверхность
	plane = plane_2;
	d = intersectPlane ( ray, plane, normal );
	if (ray.distance > d) {
		vec3 p = ray.origin + d * ray.direction;
		if (p.y>0. && p.y < 1.0 && abs(p.x) < 2.) {
			ray.hit 	 	= true;
			ray.distance 	= d;
			ray.position 	= ray.origin + ray.distance * ray.direction;
			ray.normal   	= normal;
			ray.object.id 	= 2;
		}
	}

	//Красная сфера
	sphere = sphere_1;
	sphere.center.y += sin(u_time * 0.4);
	d = intersectSphere ( ray, sphere, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
		ray.object.id 	= 3;
	}


	//Зеленая сфера
	sphere = sphere_2;
	sphere.center.y += cos(u_time * 0.5);
	d = intersectSphere ( ray, sphere, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
		ray.object.id 	= 4;
	}


	//Синяя сфера
	sphere = sphere_3;
	sphere.center.y += sin(u_time * 0.5);
	d = intersectSphere ( ray, sphere, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
		ray.object.id 	= 5;
	}
/*
	//Белая сфера
	sphere = sphere_4;
	sphere.center.y += cos(u_time * 0.4);
	d = intersectSphere ( ray, sphere, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
		ray.object.id 	= 6;
	}
*/	
	box = box_1;
	box.center.y += cos(u_time * 0.4);
	d = intersectBox ( ray, box, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
		ray.object.id 	= 7;
	}

	box = box_2;
	box.center.y += sin(u_time * 0.5);
	d = intersectBox ( ray, box, normal );
	if (ray.distance > d) {
		ray.hit 	 	= true;
		ray.distance 	= d;
		ray.position 	= ray.origin + ray.distance * ray.direction;
		ray.normal   	= normal;
