vec3 lighting(Ray ray, Light lights[2], Material material) {
	vec3 col = vec3(0);

	for (int i=0; i<2; i++) {
		Light light = lights[i];

		float lightDistance = length(light.position - ray.position);

		//Тень
		float shadowFactor = 1.0;

		Ray ray1 = ray;
		{
			ray1.origin 	= ray.position;
			ray1.direction 	= normalize(light.position - ray.position);
			ray1.near 		= 0.01;
			ray1.far		= lightDistance;
		}
		rayMarch ( ray1 );


		if ( ray1.distance < ray1.far) {
			shadowFactor = clamp(1. - (lightDistance - 3.*ray1.distance) / lightDistance, 0.2, 1.);
		}

		float distanceAttenuationPower = 0.2;
		vec3 lightDir 	= normalize(light.position - ray.position);
		vec3 eyeDir 	= - ray.direction; //normalize ( ray.origin - ray.position );
		vec3 reflected 	= reflect ( -lightDir, ray.normal );

		vec3 color = light.colorAmbient * material.colorAmbient;

		color += shadowFactor * light.colorDiffuse * material.colorDiffuse * max(dot(ray.normal,lightDir), 0.0);
		color += shadowFactor * light.colorSpecular * material.colorSpecular * pow ( max ( dot(reflected, eyeDir), 0.0) , material.shininess ) * step(1., shadowFactor) ;	
		color *= min ( 1.0/pow(length(ray.position - ray.origin), distanceAttenuationPower), 1.0);

		col += color/2.;
	}

	return col;
}

//Цвет отражений
vec3 rayReflect(in Ray ray, in Light lights[2]) {
	//Цвет отражений
	vec3 color = vec3(0);
	//Фактор отражения
	float factorReflect = 1.0;

	for (int i=0; i<4; i++) {

		rayMarch (ray);

		if (ray.hit) {
			Material material = getMaterial(ray);
			color += factorReflect * lighting(ray, lights, material);
			factorReflect *= material.reflectivity;
			if (factorReflect < 0.01) break;
			//Следующий луч
			{
				ray.origin 		= ray.position;
				ray.direction 	= reflect (ray.direction, ray.normal);
				ray.near 		= 0.01; //Отступ
			}
		} else {
			//Цвет фона
			color += vec3(0.1);
			break;
		}
	}
	return color;
}
//Цвет преломлений
vec3 rayRefract(in Ray ray, in Light lights[2]) {
	vec3 color = vec3(0);
	//Фактор отражения
	float factorRefract = 1.0;

	for (int i=0; i<4; i++) {

		rayMarch (ray);

		if (ray.hit) {
			//Достигли фигуры
			Material material = getMaterial(ray);
			factorRefract *= material.refractivity;
			if (factorRefract < 0.01) break;
			//Луч преломления сквозь фигуру
			{
				ray.origin 		= ray.position;
				ray.direction 	= refract (ray.direction, ray.normal, material.indexRefractive);
				ray.near 		= 0.01; //Отступ
			}

			//Пересечение луча с границей 2 той же фигуры 
			rayMarch (ray);

            if (! material.flagSolid) {


				if (ray.hit) {
					//Достигли границы 2 фигуры
					//Луч преломления сквозь границу 2 фигуры
					{
						ray.origin 		= ray.position;
						ray.direction 	= refract (ray.direction, ray.normal, material.indexRefractive);
						ray.near 		= 0.01; //Отступ
					}
					//Уменьшаем фактор преломления
					factorRefract *= material.refractivity;
					//Цвет за границей 2 фигуры
					color += factorRefract * rayReflect(ray, lights);

					//Луч отражения от границы 2
					{
						ray.origin 		= ray.position;
						ray.direction 	= reflect (ray.direction, ray.normal);
						ray.near 		= 0.01; //Отступ
					}
					//Отражение от границы 2
					rayMarch (ray);

					if (ray.hit) {
						//Отраженный луч достиг границы 1 фигуры
						//Луч преломления сквозь границу 1 фигуры
						{
							ray.origin 		= ray.position;
							ray.direction 	= refract (ray.direction, ray.normal, material.indexRefractive);
							ray.near 		= 0.01; //Отступ
						}
						//Уменьшаем фактор преломления
						factorRefract *= material.refractivity;
					}
				}
			}			
			color += factorRefract * rayReflect(ray, lights);
		} else {
			break;
		}
	}

	return color;
}

void mainImage( out vec4 GL_FragColor, in vec2 GL_FragCoord ) {
    u_time = iTime;
    vec2 u_canvas = iResolution.xy;
    vec3 u_mouse = iMouse.xyz;

    float aspect = u_canvas.x/u_canvas.y;
    vec2 uv = GL_FragCoord.xy / u_canvas.xy;
    uv = uv * 2.0 - 1.0;

    vec3 mouse = u_mouse / vec3(u_canvas, 1.0);
    mouse.xy -= 0.5;
    if (u_mouse.xy==vec2(0) ) mouse.xy = vec2(0);

    Camera cam;
    {
        cam.fov     = 45.;
        cam.aspect  = aspect;

        cam.origin  = vec3(0.0, 3.0, 3.0);

		if (mouse.z != 0.0) {
			rotate(cam.origin, vec3(1,1,0) * vec3(PI/3. + mouse.y*PI, mouse.x*2.*PI + u_time * 0.2, 0));
		} else {
			rotate(cam.origin, vec3(0,1,0) * u_time * 0.2);            
		}	

//        cam.origin  = vec3(0.0, 3.0, 3.0 + mouse.z);
//        rotate (cam.origin, vec3(1,1,0) * vec3(mouse.y*PI, mouse.x*PI*2. + (mouse.z==0. ? u_time/4. : 0.), 0));

        cam.target  = vec3(0,1,0);
        cam.up 		= vec3(0,1,0);
    }

    Ray ray = lookAt(uv, cam);
    {
        ray.near 	= 0.;
        ray.far  	= 1e20;
        ray.epsilon = 0.0001;
        ray.steps 	= 1;
    }	

    rotate(light_0.position, vec3(0,1,0)*u_time*0.25);
    rotate(light_1.position, vec3(0,1,1)*u_time*0.25);

    vec3 color = vec3(0);

    Light lights[2];
    lights[0] = light_0;
    lights[1] = light_1;

    color += rayReflect(ray, lights);
    color += rayRefract(ray, lights);

    //Гаммакоррекция
    //			color = pow(color, vec3(1.0/2.2));

    GL_FragColor = vec4(color,1.0);
}

const vec3 RED = vec3(1.0, 0.0, 0.0);
const vec3 GREEN = vec3(0.0, 1.0, 0.0);
const vec3 BLUE = vec3(0.0, 0.0, 1.0);

const float THICK = 50.0;

float circle(in float radius, in float thickness, float dist) {
 	return smoothstep(radius - thickness/2.0, radius, dist) 
        	- smoothstep(radius, radius + thickness/2.0, dist);   
}

float radiusFromTime(in float t, in float speedMod) {
 	return mod(t*speedMod, iResolution.y);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 CENTER = vec2(iResolution.xy/2.0);
    float dist = distance(CENTER, fragCoord);

    float radiusR = radiusFromTime(iTime, 100.0);
    float radiusG = radiusFromTime(iTime, 30.0);
    float radiusB = radiusFromTime(iTime, 25.0);

    vec3 color = vec3(0.0);
    color += RED * circle(radiusR, THICK, dist);
    color += GREEN * circle(radiusG, THICK, dist);
    color += BLUE * circle(radiusB, THICK, dist);

    fragColor = vec4(color, 1.0);
}

//https://www.iquilezles.org/www/articles/morenoise/morenoise.htm
float hash( float n ) { return fract(sin(n)*753.5453123); }

//https://www.iquilezles.org/www/articles/morenoise/morenoise.htm
//---------------------------------------------------------------
// value noise, and its analytical derivatives
//---------------------------------------------------------------

vec4 noised( in vec3 x )
{
    vec3 p = floor(x);
    vec3 w = fract(x);
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);

    float n = p.x + p.y*157.0 + 113.0*p.z;

    float a = hash(n+  0.0);
    float b = hash(n+  1.0);
    float c = hash(n+157.0);
    float d = hash(n+158.0);
    float e = hash(n+113.0);
    float f = hash(n+114.0);
    float g = hash(n+270.0);
    float h = hash(n+271.0);

    float k0 =   a;
    float k1 =   b - a;
    float k2 =   c - a;
    float k3 =   e - a;
    float k4 =   a - b - c + d;
    float k5 =   a - c - e + g;
    float k6 =   a - b - e + f;
    float k7 = - a + b + c - d + e - f - g + h;

    return vec4( k0 + k1*u.x + k2*u.y + k3*u.z + k4*u.x*u.y + k5*u.y*u.z + k6*u.z*u.x + k7*u.x*u.y*u.z, 
                 du * (vec3(k1,k2,k3) + u.yzx*vec3(k4,k5,k6) + u.zxy*vec3(k6,k4,k5) + k7*u.yzx*u.zxy ));
}

vec4 fbmd( in vec3 x )
{
    const float scale  = 1.5;

    float a = 0.0;
    float b = 0.5;
    float f = 1.0;
    vec3  d = vec3(0.0);
    for( int i=0; i<8; i++ )
    {
        vec4 n = noised(f*x*scale);
        a += b*n.x;           // accumulate values      
        d += b*n.yzw*f*scale; // accumulate derivatives
        b *= 0.5;             // amplitude decrease
        f *= 1.8;             // frequency increase
    }

    return vec4( a, d );
}


