mat2 rot(float r) { 
    return mat2(
        cos(r), sin(r),
        -sin(r), cos(r)
    );
}

float sdfSphere(vec3 p, float r)
{
    return length(p) - r;
}

float sdfBox(vec3 p, vec3 s)
{
    vec3 d = abs(p) - s;
    return length(max(d, 0.0)) + min(max(d.x, max(d.y, d.z)), 0.0);
}

float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float pokeBallTop(vec3 p)
{
    float s = sdfSphere(p, 0.5);
    float b = sdfBox(p + vec3(0.0, 0.47, 0.0), vec3(0.6, 0.5, 0.6));
    return max(s, -b);
}

float pokeBallBottom(vec3 p)
{
    float s = sdfSphere(p, 0.5);
    float b = sdfBox(p + vec3(0.0, -0.47, 0.0), vec3(0.6, 0.5, 0.6));
    return max(s, -b);
}

float pokeBallButtonCutout(vec3 p)
{
    p.z += 0.5;
    p.yz *= rot(3.14/2.0);
    float cy = sdCappedCylinder(p, vec2(0.13));
    return cy;
}

float btnDist = 0.0;
float pokeBallButton(vec3 p)
{
    vec3 p0 = p;
    p0.z += 0.47;
    p0.yz *= rot(3.14/2.0);
    float cy0 = sdCappedCylinder(p0, vec2(0.1, 0.01));

    vec3 p1 = p;
    p1.z += 0.49;
    p1.yz *= rot(3.14/2.0);
    float cy1 = sdCappedCylinder(p1, vec2(0.06, 0.01));

    float c = sdfSphere(p + vec3(0.0, 0.0, 0.585), 0.1);

    float d = min(cy0, max(cy1, -c));
    btnDist = 0.01 / (d * d);
    return d;
}


Material mixMaterial(Material a, Material b, float s)
{
    return Material(
        mix(a.albedo, b.albedo, s),
        mix(a.roughness, b.roughness, s),
        mix(a.metallic, b.metallic, s)
    );
}

Hit minHit(Hit a, Hit b)
{
    if (a.dist < b.dist) return a;
    return b;
}

Hit interHit(Hit a, Hit b)
{
    if (a.dist > b.dist) return a;
    return b;
}

