vec3 white = vec3(1.0, 1.0, 1.0); //Kolor tła
float gridwidth = 2.0; //Szerokość kratki
float gridgap = 40.0; // odległość między kratkami
float PI = 3.1415; //Liczba PI
float linewidth = 3.0; //Szerokość linii przesuwanej
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 pixel = white; //domyślne ustawienie pikseli na biało
    vec3 col2 = vec3(0.2, 0.7, 1.0);
    vec3 col3 = vec3(0.2, 1.0, 0.2);
    vec3 col4 = vec3(0.0, 0.0, 0.0);
    vec3 col5 = vec3(1.0, 0.0, 0.0);
    float time = iTime;
    vec2 zero = iResolution.xy / 2.0; //wyśrodkowanie układu współrzędnych
    //Środek układu przypisanego do linii, który przesuwa się w czasie
    vec2 floatingzero = vec2((mod(time, 4.0)-2.0<0.0)?(iResolution.x / 2.0 - mod(time*200.0, 20.0*gridgap)+5.0*gridgap):((iResolution.x - 40.0*gridgap) / 2.0 + mod(time*200.0, 20.0*gridgap)+5.0*gridgap), iResolution.y/2.0);
    //pozycja piłeczki
    vec2 ballpoz = vec2(zero.x, zero.y+((mod(time, 4.0)-2.0)<=0.0?gridgap*-5.0+mod(time, 2.0)*gridgap*5.0:gridgap*5.0-mod(time, 2.0)*gridgap*5.0));
    //Rysowanie linii poziomych
    if (mod(abs(zero.x-fragCoord.x)+gridwidth/2.0, gridgap)<gridwidth) if (zero.x - fragCoord.x < 5.0 && zero.x - fragCoord.x > -5.0) pixel = col5; else pixel = col2;
    //rysowanie linii pionowych
    if (mod(abs(zero.y-fragCoord.y)+gridwidth/2.0, gridgap)<gridwidth) if (zero.y - fragCoord.y < 5.0 && zero.y - fragCoord.y > -5.0) pixel = col5; else pixel = col2;
    //Miejscowienie piłeczki
    if (length(ballpoz - fragCoord) < 10.0) pixel = col3;
    //miejscowienie górnej połowy linii
    if (((fragCoord.x-floatingzero.x+3.0)/(fragCoord.y-floatingzero.y) <= 1.0)&& floatingzero.x-fragCoord.x >= 0.0 && floatingzero.y-fragCoord.y >= 0.0&&(fragCoord.x-floatingzero.x-3.0)/(fragCoord.y-floatingzero.y)>=1.0)
               pixel = col4;
    //miejscowienie dolnej połowy linii
    if (((fragCoord.x-floatingzero.x-3.0)/(fragCoord.y-floatingzero.y)<=1.0)&&floatingzero.x-fragCoord.x<=0.0 && floatingzero.y-fragCoord.y<=0.0&&(fragCoord.x-floatingzero.x+3.0)/(fragCoord.y-floatingzero.y)>=1.0)
            pixel = col4;
    //rysowanie
    fragColor = vec4(pixel, 1.0);
}

#define _BackgroundColor vec4(1.0,1.0,1.0,1.0)
#define _LineColor vec3(0.0,0.0,0.0)
#define _LineWidth 5.0
#define _AA 1.0
#define _CircleRadius1 45.0
#define _CircleRadius2 90.0
#define _CirclePos1 vec2(0.15,1.0)
#define _CirclePos2 vec2(0.85,1.0)
#define _CircleColor1 vec3(1.0,0.0,0.0)
#define _CircleColor2 vec3(0.0,1.0,0.0)

vec4 drawCircle(vec2 pos, vec2 center, float radius, vec3 color, float antialiasing) {
			float distance = length(pos - center) - radius;
			float threshod = smoothstep(0.0, antialiasing, distance);
			return vec4(color, 1.0 - threshod);
		}

vec4 drawLine(vec2 pos, vec2 point1, vec2 point2, float width, vec3 color, float antialiasing) {
			float k = (point1.y - point2.y) / (point1.x - point2.x);
			float b = point1.y - k * point1.x;
			float distance = abs(k*pos.x - pos.y + b) / sqrt(k*k + 1.0);
			float threshod = smoothstep(width / 2.0, width / 2.0 + antialiasing, distance);
			return vec4(color, 1.0 - threshod);
		}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos=fragCoord;
    float circleRadius1=_CircleRadius1*smoothstep(-1.0,1.0,sin(iTime));
    float circleRadius2=_CircleRadius2*smoothstep(-1.0,1.0,sin(iTime+90.0));
    vec2 circlePos1=vec2(_CirclePos1.x,_CirclePos1.y*smoothstep(-1.0,1.0,sin(iTime)));
    vec2 circlePos2=vec2(_CirclePos2.x,_CirclePos2.y*smoothstep(-1.0,1.0,sin(iTime+90.0)));

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 backgroundColor=vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec3 circleColor1=vec3(0.5+0.5*sin(iTime),uv);
    vec3 circleColor2=vec3(uv.x,0.5+0.5*sin(iTime),uv.y);

    vec4 linePic=drawLine(fragCoord,circlePos1*iResolution.xy,circlePos2*iResolution.xy,_LineWidth,_LineColor,_AA);
    vec4 circlePic1=drawCircle(fragCoord,circlePos1*iResolution.xy,circleRadius1,circleColor1,_AA);
    vec4 circlePic2=drawCircle(fragCoord,circlePos2*iResolution.xy,circleRadius2,circleColor2,_AA);
    vec4 layer1=mix(backgroundColor,linePic,linePic.a);
    vec4 layer2=mix(layer1,circlePic1,circlePic1.a);
    vec4 layer3=mix(layer2,circlePic2,circlePic2.a);
    fragColor=layer3;
}

#define PI 3.1415926535897932384626433832795

vec3 hash3( uint n ) 
{
    // integer hash copied from Hugo Elias
	n = (n << 13U) ^ n;
    n = n * (n * n * 15731U + 789221U) + 1376312589U;
    uvec3 k = n * uvec3(n,n*16807U,n*48271U);
    return vec3( k & uvec3(0x7fffffffU))/float(0x7fffffff);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = 2.0*uv-1.0;
    uv *= vec2(iResolution.x/iResolution.y,1.0);

    const uint n = 8u;
    float R = 0.5;// + 0.3*sin(0.5*iTime);
    float r = 0.13 + 0.1*sin(iTime);

    vec2 bigCircleCenter = vec2(0);

    vec3 col = vec3(1.0);
    if(dot(uv-bigCircleCenter,uv-bigCircleCenter)<R*R)
        col = vec3(0.0,0.0,1.0);
    else
    {
        float delta = 2.0*PI/float(n);
        float phi = iTime;
        for(uint i=0u;i<n;++i)
        {
            vec2 smallCircleCenter = (R+r)*
                vec2(cos(delta*float(i)+phi),
                     sin(delta*float(i)+phi));
            if(dot(uv-smallCircleCenter,uv-smallCircleCenter)<r*r)
            {
                col = hash3(i);
            }
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

const float M_PI = 3.1415926535897932384626433832795;


vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec4 taylorInvSqrt(vec4 r){return 1.79284291400159 - 0.85373472095314 * r;}

float snoise(vec3 v){ 
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //  x0 = x0 - 0. + 0.0 * C 
  vec3 x1 = x0 - i1 + 1.0 * C.xxx;
  vec3 x2 = x0 - i2 + 2.0 * C.xxx;
  vec3 x3 = x0 - 1. + 3.0 * C.xxx;

// Permutations
  i = mod(i, 289.0 ); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients
// ( N*N points uniformly over a square, mapped onto an octahedron.)
  float n_ = 1.0/7.0; // N=7
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z *ns.z);  //  mod(p,N*N)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  m = m * m;
  return 42.0 * dot( m*m, vec4( dot(p0,x0), dot(p1,x1), 
                                dot(p2,x2), dot(p3,x3) ) );
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord;
    uv -= iResolution.xy*0.5;
    uv /= min(iResolution.x, iResolution.y)*0.5;

    vec3 col = vec3(1.0);

    uv *= (sin(iTime/4.0)*0.5+0.5)*8.0 + 1.0;
    float d = length(uv);
    float angle = atan(uv.y, uv.x);
    angle += snoise(vec3(uv.x/4.0, uv.y/4.0, iTime))/d/4.0;
    if (distance(mod(angle/(2.0*M_PI)*8.0, 1.0), 0.5) < 0.2/pow(d, 1.5)) {
        col = vec3(0.0);
    }

	fragColor = vec4(col, 1.0);
}

// Quick and dirty line experiment to generate electric bolts :)

// http://extremelearning.com.au/unreasonable-effectiveness-of-quasirandom-sequences/
float R1seq(int n)
{
	return fract(float(n) * 0.618033988749894848204586834365641218413556121186522017520);
}

vec2 R2seq(int n)
{
	return fract(vec2(n) * vec2(0.754877666246692760049508896358532874940835564978799543103, 0.569840290998053265911399958119574964216147658520394151385));
}

// modified iq's segment: https://www.shadertoy.com/view/ldj3Wh
vec2 Line(vec2 a, vec2 b, vec2 p, vec2 identity, float sa, float sb)
{
    vec2 pa = p - a;
    vec2 pb = p - b;
	vec2 ba = b - a;
	float t = clamp(dot(pa,ba)/dot(ba,ba), 0.0, 1.0);    
    vec2 pp = a + ba * t;
    vec2 y = vec2(-identity.y, identity.x);
    float cutoff = max(dot(pb, identity), dot(pa, -identity));
    float s = mix(sa, sb, t);
    return vec2(max(cutoff - .005, abs(dot(y, p - pp)) - s), t);
}

float Rythm(float x)
{
    x = x * 6.28318 * 10.0 / 60.0;
	x = smoothstep(-1.0, 1.0, sin(x));
	x = smoothstep(0.0, 1.0, x);
	x = smoothstep(0.0, 1.0, x);
	x = smoothstep(0.0, 1.0, x);
	x = smoothstep(0.0, 1.0, x);
	return x;
}

vec3 Background(vec2 uv, vec2 baseDir, float time)
{
    uv = uv * vec2(.75, .75) + vec2(-.0, .0);
	vec3 result = vec3(0.91, 0.56, 0.02);

    vec2 n = vec2(-baseDir.y, baseDir.x);

    result = mix(result, vec3(1.0) - result, Rythm(time));

    float lines = texture(iChannel0, vec2(uv.x - uv.y) + vec2(time * 1.35, 0.0)).r;
    result += lines * lines * .75 + lines * lines * lines * .35;    
    result *= smoothstep(.5, .0, abs(dot(uv, n)));

    return result * 1.25;
}

vec3 Magic(float leadTime, vec3 baseColor, vec2 uv, vec2 baseDir, float time, float spread, float freq, float intensity)
{
    int frame = iFrame / 12;

    float speed = -1.5 - ((Rythm(time)) * .5 + .5) * 2.0;
    //speed *= .2;
    vec2 dir = normalize(baseDir);


    uv -= dir * mix(.1, .3, Rythm(time));

    vec2 normal = vec2(-dir.y, dir.x);

    vec2 baseOffset = dir * speed * floor(float(iFrame) / 24.0);

    vec2 p = uv;
    p += dir * speed * (float(iFrame) / 24.0);
    p -= R2seq(int(floor(float(iFrame)/3.0))) * .05;
    p += normal * sin(time * 12.0) * .05;

    float ray = 0.0;
	float glow = 0.0;

    p += (texture(iChannel1, p * .015 + leadTime * .25).xy * 2.0 - 1.0) * .1;

    float leadIntro = mix(.3, .015, smoothstep(10.0, 14.0, time));

    float leadingTime = 1.0 - smoothstep(leadTime - .5, leadTime, time);
    float distanceToLead = dot(uv - .5, dir) - leadingTime * 2.0 - leadIntro;
    float leadingMask = smoothstep(-.85, -.0, distanceToLead);

    p += leadingMask * (texture(iChannel1, vec2(time * .01 + leadTime * .35)).xy * 2.0 - 1.0) * .35;

    float sizeIntro = smoothstep(13.85, 14.15, time);
    spread *= leadingMask * (1.0 - Rythm(time) * .75) * sizeIntro;

    for(int i = -12; i < 10; i++)
    {
		float offsetA = R1seq(i+frame) * 2.0 - 1.0;
        float offsetB = R1seq(i+frame+1) * 2.0 - 1.0;

        vec2 a = baseOffset + dir * float(i) * freq + normal * offsetA * spread;
        vec2 b = baseOffset + dir * float(i+1) * freq + normal * offsetB * spread;

        float sa = mix(.05, 3.0 * intensity, R1seq(frame*7+i-1)) * .005;
        float sb = mix(.05, 3.0 * intensity, R1seq(frame*7+i)) * .005;

        vec2 l = Line(a, b, p, dir, sa, sb);
        float d = .025 * leadingMask;

        ray += smoothstep(d, d * .75 - .0001, l.x);
        glow += .5 * leadingMask * smoothstep(d * 20.0, d, l.x);
    }

    ray = clamp(ray, 0.0, 1.0);
    return baseColor * (1.0 + glow * (Rythm(time * 16.0) * .05 + .025)) + vec3(ray) * intensity * leadingMask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = -.25 + floor(iTime * 1.1 * 24.0) / 24.0;
    float intro = smoothstep(12.85, 13.15, time);
    vec2 uv = fragCoord/iResolution.xy;

    uv.y -= .075;
    uv.x -= sin(time*4.0) * .2;

    vec2 baseDir = normalize(vec2(.57, .45));

    vec3 col = Background(uv, baseDir, time) * intro;

    float spread = .35 + (sin(time * 10.0) * .5 + .5);
    float freq = .6 - (sin(time * 4.0) * .5 + .5) * .2;


    float offset = 1.0 - (smoothstep(5.0, 7.0, time) * smoothstep( 14.0, 13.0, time));

    spread *= offset;

   	col = Magic(.5, col, uv + vec2(.4, .1) * offset, baseDir, time, .2, .35, 1.0 - intro * .5);
    col = Magic(3.0, col, uv + vec2(.2, .0) * offset, baseDir, time, .05, .15, .55 + intro * .3);
	col = Magic(8.0, col, uv + vec2(.2, -.25) * offset, baseDir, time, .05, .15, .35 + intro * .3);
    col = Magic(10.0, col, uv + vec2(-.15, -.35) * offset, baseDir, time, .04, .05, .75 + intro * .3);
    col = Magic(11.0, col, uv + vec2(-.3, -.15) * offset, baseDir, time, .04, .05, .75 + intro * .3);
    col = Magic(12.0, col, uv, baseDir, time, spread * .75, freq, 1.0);

    fragColor = vec4(col,1.0);
}

