float hash21(vec2 v) {
    return fract(sin(dot(v, vec2(12.9898, 78.233))) * 43758.5453123);
}

float noise(vec2 uv) {
	vec2 f = fract(uv);
	vec2 i = floor(uv);
	f = f * f * (3. - 2. * f);
	return mix(
		mix(hash21(i), hash21(i + vec2(1,0)), f.x),
		mix(hash21(i + vec2(0,1)), hash21(i + vec2(1,1)), f.x), f.y);
}

float fbm(vec2 uv) {
	float freq = 2.;
	float amp = .5;
	float gain = .54;
	float v = 0.;
	for(int i = 0; i < 6; ++i) {
		v += amp * noise(uv);
		amp *= gain;
		uv *= freq;
	}
	return v;
}

float fbmPattern(vec2 p, out vec2 q, out vec2 r) {
	float qCoef = 2.;
	float rCoef = 3.;
	q.x = fbm(p             + .0  * iTime);
	q.y = fbm(p             - .02 * iTime + vec2(10., 7.36));
	r.x = fbm(p + qCoef * q + .1  * iTime + vec2(5., 3.));
	r.y = fbm(p + qCoef * q - .07 * iTime + vec2(10., 7.36));
	return fbm(p + rCoef * r + .1 * iTime);
}

vec3 basePalette(float t) {
	return .5 + .6 * cos(6.283185 * (-t + vec3(.0, .1, .2) - .2));
}

vec3 smokePalette(float t) {
	return vec3(.6, .5, .5) 
		+ .5 * cos(6.283185 * (-vec3(1., 1., .5) * t + vec3(.2, .15, -.1) - .2));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord/iResolution.yy;

	float scale = 5.;
	vec3 col = vec3(.1);
	float n;

	vec2 q;
	vec2 r;
	n = fbmPattern(scale * uv, q, r);
	vec3 baseCol = basePalette(r.x);
	vec3 smokeCol = smokePalette(n);

	col = mix(baseCol, smokeCol, pow(q.y, 1.3));

#ifdef DEBUG_PALETTE
    float x = fragCoord.x / iResolution.x;
	col = mix(col, basePalette(x), step(abs(uv.y - .03), .02));
	col = mix(col, smokePalette(x), step(abs(uv.y - .08), .02));
#endif

	fragColor = vec4(col, 1);
}

vec2 rot(vec2 v, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c)*v;
}


float cro(in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

float sdUnevenCapsule( in vec2 p, in vec2 pa, in vec2 pb, in float ra, in float rb )
{
    p  -= pa;
    pb -= pa;
    float h = dot(pb,pb);
    vec2  q = vec2( dot(p,vec2(pb.y,-pb.x)), dot(p,pb) )/h;

    //-----------

    q.x = abs(q.x);

    float b = ra-rb;
    vec2  c = vec2(sqrt(h-b*b),b);

    float k = cro(c,q);
    float m = dot(c,q);
    float n = dot(q,q);

         if( k < 0.0 ) return sqrt(h*(n            )) - ra;
    else if( k > c.x ) return sqrt(h*(n+1.0-2.0*q.y)) - rb;
                       return m                       - ra;
}


float TimerInOut(vec4 v)
{
    return smoothstep(v.y,v.y+v.w,v.x) - smoothstep(v.z-v.w,v.z,v.x);
}

vec2 spiral(vec2 uv,float zoom, float len, float offset)
{
    float d = length(uv) * zoom;

    float trip = TimerInOut(vec4(mod(iTime,60.0),15.0,45.0,1.0));
    float m = 0.4+sin(iTime+uv.y+uv.x)*0.5;
    d*=(1.0-(m*trip));

    d=log(d);
    uv = rot(uv,sin(d*offset)+d*5.0);
	float v = 1.0-sdUnevenCapsule(uv,vec2(0.0,0.0),vec2(0.4*len,0.0),0.0,0.4*len)*14.0;
    float l = d;   
    return vec2(v,l);	// distx , disty
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float t = fract(iTime*0.4)*6.0;

    float offset = 25.0 * TimerInOut( vec4(mod(iTime,9.6),1.0, 7.2, 0.8));

	vec2 dist = spiral(uv,1.0,t,offset);
    float v = dist.x;
    float l = dist.y;

	v = smoothstep(0.5-0.1,0.5+0.1,v);
    //vec3 col = vec3(v*1.0-l/t);

    vec3 col = mix(vec3(0.0,0.0,0.2),vec3(1.0,0.6,0.5),v*1.0-l/t);

 	vec2 q = fragCoord/iResolution.xy;
    col *= 0.3 + 0.7*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.25);    

    fragColor = vec4(col, 1.0);
}

