vec4 RaycastAtPixelCoord(vec2 pixCoord) 
{
	// Compute the ray to be casted through the pixel towards the 3D scene
    vec3 rayPos, rayDir;
    computeCameraRayFromPixel(pixCoord, rayPos, rayDir); 

    ShadeInfo infos[MAX_NB_BOUNCES];

    int nbBounces = 0;

    // Test ray-objetcs intersections and find the nearest one
    // (with its associated intersection point and normal at the object surface)
    int  objectId;
    vec3 intersecI, normalI;

    vec3 RayRefr;
    vec3 spec;

    vec3 ambiant;

    do
    {	

    	float distI = computeNearestIntersection(rayPos, rayDir, -1, objectId, intersecI, normalI);

    	// Apply the shading to the points that are on the sphere surface and seen by the camera
    	if (distI <= 0.0)
    	{

            infos[nbBounces].shadedcol = skyCol;
        	infos[nbBounces].Ks = 0.0;
            nbBounces++;
        	break;

        }

        // Get the diffuse color at the intersection point
        Material objectMat;

        infos[nbBounces].shadedcol = vec3(0);
        vec3 objectCol = getObjectColorAtPoint(objectId, intersecI, objectMat);

        //ambiant = computeAmbiant(objectMat);


        infos[nbBounces].Ks = objectMat.Ks;

        // unit-vector going from the surface point toward the camera 
       	vec3 V = -rayDir;

        vec3 I = intersecI + normalI*eps;

        for (int l=0; l< NB_LIGHTS; l++)
        {


        	// unit-vector going from the surface point toward the light
	    	vec3 L = normalize(lights[l].pos - intersecI);

        	// unit-vector of the reflection direction of the light at the surface point
    		vec3 R = 2.0 * dot(normalI, L) * normalI - L;


        	float Ldist = distance(lights[l].pos,intersecI);


        	float shadowfactor = getShadowAtPoint(I, normalI, objectMat, L, Ldist, lights[l]);

        	// Apply the Phong shading to compute the color
        	// of the surface point as seen from the camera

            infos[nbBounces].shadedcol += computePhongShading(objectCol, objectMat, normalI, L, R, V, shadowfactor, lights[l]);;

        }

        RayRefr = 2.0 * normalI * dot(normalI, V) - V;

		rayDir = RayRefr;

        rayPos = I;


        //bounce from the surface towards the reflect direction of the ray


        nbBounces++;
    }
    while (nbBounces < MAX_NB_BOUNCES);

    vec3 resCol = vec3(0);

    for (int i=0; i<=nbBounces; i++)
    {
        resCol = infos[nbBounces-i].Ks*resCol + infos[nbBounces-i].shadedcol;
    }

    //resCol = vec4(computePhongShading(objectCol, objectMat, normalI, L, R, V, shadowfactor), 1);

    return vec4(resCol, 1);

    //return vec4(computePhongShading(resCol, objectMat, normalI, L, R, V, shadowfactor), 1);; 
}


