vec3 voroni(vec2 uv)
{
    vec2 iuv = floor(uv);
    vec2 fuv = fract(uv);
    vec3 col = (fuv).xyy;
    float mdist = 1.;
    // used to render center point
    float cmdist = 1.;
    vec2 p;
    vec2 sp;
    // get neighbour tiles
    for(int y = -1; y <= 1; y++)
    {
        for(int x = -1; x <= 1; x++)
        {
            vec2 n = vec2(float(x),float(y));

            p = rand2d(iuv + n);

            // animate
            p.y = 0.5 + 0.5*sin(iTime + 5.*p.y);
            p.x = 0.5 + 0.5*cos(iTime + 5.*p.x);

            vec2 diff = n + p - fuv;
            float dist = length(diff);

            // 
            mdist = pow(mdist, ROUNDNESS);

            if(dist < mdist)
            {
            	mdist = min(mdist,dist);
                cmdist = min(cmdist,dist);
                sp = p;
            }
        }
    }
    return vec3(length(sp)) + (1.-step(0.02,cmdist));
}

