//Here goes the main function, which is applied for every pixel in our window
//It takes the pixel coordinates as an argument
//And returns the colour of the given pixel.
//Below we see how we can define our shape by using mathemaica sn code

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //////////////////////////////////////////////////////
    //Starting the common section
    //The lines above are the same for most of the examples. 
    //Basically it is a mapping between pixel coordinates and world coordinates.
    //We discuss coordinates systems soon. 
    //
    //taking pixel coordinates in the uv space, range [0,1]
	vec2 uv = fragCoord.xy / iResolution.xy;
    //map pixel coordinates to [-1,1]
    vec2 c = 2.0 * uv - 1.0;
    //adjust coordinates given an aspect ratio
    float aspect_ratio = iResolution.x/iResolution.y;
    c.x *= aspect_ratio;
    //
    //End of the common section
    ///////////////////////////////////////////////////////

    //We are going to map pixel coordinates from [-1,1] to 
    //[central point - size, central point + size]
    //
    //The size is time-dependent, so graphically we xoom around centre point.
    //
    //The central point is pre-defined in the code to be at the "edge" of the set.
    //You can also try some other ones, like 
    //(-0.745, 0.113)
    //(0.013, 0.655)
    //(-0.745, 0.186)
    //(-1.41, 0.0)

    float centre_x = -1.41;
    float centre_y = 0.0;

    //Below we define the size of our area
    //Some explanations: cos(iTime) creates time-dependent oscillations between -1 and 1
    //1.0-cos maps that wave to [0,2]
    //pow(0.003, 1.0-cos) create wave between 0.00009 (which is 0.003^2) and 1.0 (0.003^0)
    //So eventually by using continuous time variable we oscillate between 1 and 0.00009
    //
    //You can try to have something smaller than 0.003, but eventually you hit the floating point limit
    float zooming_ratio = pow(.003,1.-cos(0.2*iTime));

    //Finally we map from [-1,1] to [centre_x-zoom, centre_x+zoom]
    //Position px and py are from the given pixel
    float cx = centre_x + c.x*zooming_ratio;
    float cy = centre_y + c.y*zooming_ratio;

    //z_0 = c
    //I could use vec2 to pack complex number, but we have not discussed vectors yet.
    //Instead real part and imaginary part have their own variables
    float z_re = cx;
    float z_im = cy;

	float n = 0.; //iteration number

    vec3 colour = vec3(0.0, 0.0, 0.0); //default colour in this shader is black 

    //we have 256 iterations maximum
    for(int i = 0; i < 256; i++)
    {
        //z_n+1 = z_n^2 + c
        //recall that (a+bi)(c+di) = (ac-bd)+(ad+bc)i
        //therefore z*z = (z_re*z_re - z_im*z_im) + (z_re*z_im + z_re*z_im)i

        //z*z
        float zn_re = z_re*z_re - z_im*z_im;
        float zn_im = 2.0*z_re*z_im;

        //z*z+c
        zn_re += cx;
        zn_im += cy;

        n++;

        //if the modulus of the point greater than 2, the point is not in Mandelbrot set anymore, stop
        if(sqrt(zn_re*zn_re+zn_im*zn_im)>2.0)
        {
            //assign colour to the pixel given the iteration number
            //f denotes how many "palettes" we have
            float pn = 10.;
            //use trigonometry to have rather nice set of 
        	colour = vec3(sin(n/pn),cos(n/pn),1.0-sin(n/pn));
            break;
        }
        z_re = zn_re;
        z_im = zn_im;
    }

	fragColor = vec4(colour,1.0);


}

//fork of iq's Boxes traced
//https://www.shadertoy.com/view/4tKBWy

#define LEVELS 14
#define STEPS 3000
#define FAR 100.0

#define ZERO min(0,iFrame)

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

vec2  hash2( float n ) { return fract(sin(vec2(n,n+3.0))*vec2(43758.5453123,22578.1459123)); }
float hash2( vec2 x )  { return fract(sin(dot(x,vec2(12.353,31.71))*43758.5453123)); }

//https://www.shadertoy.com/view/XlXcW4
const uint k = 1103515245U;  // GLIB C
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}

void random(out vec3 dcen, out vec3 drad, in int id, in int level) {
    dcen = hash(uvec3(id,level,uint(iMouse.x))).xyz;
    drad = hash(uvec3(id,level,uint(iMouse.y))).xyz;
    drad = drad*0.4+0.5;
}

void generate(inout vec3 cen, inout vec3 rad, in int id, in int level) {
    vec3 dcen;
    vec3 drad;

    random(dcen,drad, id, level);

    vec3 dp = dcen*2.0-1.0;

    vec3 ncen = cen;
    vec3 nrad = rad;
    nrad = nrad*drad;
    ncen += dp*(rad-nrad);

    rad = nrad;
    cen = ncen;
}

void backgenerate(inout vec3 cen, inout vec3 rad, in int id, in int level) {
    vec3 dcen;
    vec3 drad;

    random(dcen,drad, id, level);

    vec3 dp = dcen*2.0-1.0;

    vec3 pcen = cen;
    vec3 prad = rad;
    prad = rad/drad;
    pcen -= dp*(prad-rad);

    rad = prad;
    cen = pcen;
}

vec2 boxIntersect( in vec3 ro, in vec3 rd, in vec3 ird, in vec3 cen, in vec3 rad, out vec3 nor) 
{
    vec3 n = ird*(cen-ro);
    vec3 k = abs(ird)*rad;

    vec3 t1 = n - k;
    vec3 t2 = n + k;

    float tN = max(max( t1.x, t1.y ),t1.z);
    float tF = min(min( t2.x, t2.y ),t2.z);

    if( tN > tF || tF < 0.0) return vec2(-1.0);

    nor = -sign(rd)*step(t1.yzx,t1.xyz)*step(t1.zxy,t1.xyz);

    return vec2( tN, tF );
}

bool boxIntersectAny( in vec3 ro, in vec3 rd, in vec3 ird, in vec3 cen, in vec3 rad ) 
{
    vec3 n = ird*(cen-ro);
    vec3 k = abs(ird)*rad;

    vec3 t1 = n - k;
    vec3 t2 = n + k;

    float tN = max( max( t1.x, t1.y ), t1.z );
    float tF = min( min( t2.x, t2.y ), t2.z );

    return tN<tF && tF>0.0;
}

vec3 ibox = vec3(2,1,2);

vec3 cityIntersect( in vec3 ro, in vec3 rd, in vec3 ird, inout float tmax, out int id2, out int i)
{
    vec3 res = vec3(-1.0);
    vec3 cen = vec3(0);
    vec3 rad = ibox;
    int level = 0;
    int id = 0;

    bool subdivide = true;

    for( i=ZERO; i<STEPS; i++ )
    {
        if (subdivide) {
            vec3 nor;
            vec2 tmp = boxIntersect( ro, rd, ird, cen, rad, nor);

            // intersect node
            if( tmp.y>0.0 && tmp.x < tmax)
            {
                // if leaf
                if(level == LEVELS)
                {
                    tmax = tmp.x;
                    res = nor;
                    id2 = id;

                    subdivide = false;
                }
                // else, test a child node of even id number
                else
                {

                    level++;
                    //even id number
                    id *= 2;
                    generate(cen,rad,id,level);
                }
            }
            // pop next node from the stack
            else
            {
                subdivide = false;
            }
        } else {
            if (level == 0) {
                break;
            }
            //going up nodes until there is an untested node,
            //if the id number is even id+1 is not tested,
            //if level is 0 every node is tested, and the ray hasn't hit anything
            if (id/2*2 != id) {

                backgenerate(cen,rad,id,level);

                id/=2;
                level--;
            } else {
                backgenerate(cen,rad,id,level);
                //uneven id number
                id++;
                generate(cen,rad,id,level);
                subdivide = true;
            }
        }
    }

    return res;
}

bool cityIntersectAny( in vec3 ro, in vec3 rd, in vec3 ird )
{
    bool res = false;

    // start with the root
    vec3 cen = vec3(0);
    vec3 rad = ibox;
    int level = 0;
    int id = 0;

    bool subdivide = true;

    for( int i=ZERO; i<STEPS; i++ )
    {
        // intersect node
        if (subdivide) {
        if( boxIntersectAny( ro, rd, ird, cen, rad ) )
        {
            // if leaf
            if( level == LEVELS )
            {
                res = true;
                break;
            }
            // else, generate child of even id
            else
            {
                level++;
                //even id number
                id *= 2;
                generate(cen,rad,id,level);
            }
        }
        // pop next node from the stack
        else
        {
            subdivide = false;
        }
        } else {
            if (level == 0) {
                break;
            }
            //going up nodes until there is an untested node,
            //if the id number is even id+1 is not tested,
            //if level is 0 every node is tested, and the ray hasn't hit anything
            if (id/2*2 != id) {

                backgenerate(cen,rad,id,level);

                id/=2;
                level--;
            } else {
                backgenerate(cen,rad,id,level);
                //uneven id number
                id++;
                generate(cen,rad,id,level);
                subdivide = true;
            }
        }
    }

    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    float seed = texelFetch( iChannel2, ivec2(fragCoord)&255, 0 ).x;

    float t = iTime*0.2;

    vec3 ro = vec3(-sin(t),0.5,-cos(t));
    vec3 rd = normalize(vec3(uv,1));
    rd.yz *= rot(-0.7);
    rd.xz *= rot(t);
    vec3 ird = 1.0/rd;

    float tmin = FAR;
    int id;
    int i;
    vec3 res = cityIntersect( ro, rd, ird, tmin, id,i);

    vec3 col= vec3(0);
    if (tmin < FAR) {

        vec3 pos = ro + tmin*rd;
        vec3 nor = res;

        //random color, based on box id
        col = hash(uvec3(id,ZERO,ZERO));

        //direction to light
        vec3 light = normalize(vec3(1));

        float diffuse = clamp(dot(nor,light),0.2,1.0);

        //shadow
        if (cityIntersectAny(pos+nor*0.01,light,1.0/light)) {
            diffuse = 0.2;
        }

        col *= diffuse;

    } else {
        //some random background
        col += clamp(dot(rd,normalize(vec3( 1.0, 0.5,-0.7))),0.0,1.0)*vec3(0.8,0.3,0.4);
        col += clamp(dot(rd,normalize(vec3(-1.0, 0.8,-0.1))),0.0,1.0)*vec3(0.2,0.9,0.4);
        col += clamp(dot(rd,normalize(vec3( 0.0,-0.3, 0.9))),0.0,1.0)*vec3(0.8,0.6,0.4);
        col += clamp(dot(rd,normalize(vec3( 0.0,-0.3,-0.9))),0.0,1.0)*vec3(0.8,0.3,0.8);
        col *= 0.5;
        col += 0.2;
    }
    fragColor = vec4(col, 1);

    fragColor = sqrt(fragColor);
}

