Intersect intersect(Ray ray, Sphere sphere) {
    // Check for a Negative Square Root
    vec3 oc = sphere.position - ray.origin;
    float l = dot(ray.direction, oc);
    float det = pow(l, 2.0) - dot(oc, oc) + pow(sphere.radius, 2.0);
    if (det < 0.0) return miss;

    // Find the Closer of Two Solutions
             float len = l - sqrt(det);
    if (len < 0.0) len = l + sqrt(det);
    if (len < 0.0) return miss;
    return Intersect(len, (ray.origin + len*ray.direction - sphere.position) / sphere.radius, sphere.material);
}

Intersect intersect(Ray ray, Plane plane) {
    float len = -dot(ray.origin, plane.normal) / dot(ray.direction, plane.normal);
    if (len < 0.0) return miss;
    return Intersect(len, plane.normal, plane.material);
}


Intersect trace(Ray ray, int bouncecounter) 
{
    Intersect PlaneIntersection;
    Intersect plane = intersect(ray, Plane(vec3(0, 1, 0), Material(vec3(.5, .5, .5), 1.0, 0.0, false)));
    if (plane.material.diffuse > 0.0 || plane.material.specular > 0.0) { PlaneIntersection = plane; }

    Intersect SphereIntersection;

    for (int i = 0; i < spheres.length(); i++) 
    {
         if(bouncecounter>=1) // reflecdet rays
         {
                Intersect sphere = intersect(ray, spheres[i]);
            	if (sphere.material.diffuse > 0.0 || sphere.material.specular > 0.0)
                	if(sphere.len < SphereIntersection.len || SphereIntersection.len == miss.len) //Chicking if Sphere is the Closest Sphere if nothing was Hit before it is
                    {
                        SphereIntersection = sphere;
                    }
         }
        else //normal rays
        {
            if(spheres[i].material.ghost != true) //Ignore Ghosts
            {
                Intersect sphere = intersect(ray, spheres[i]);
                if (sphere.material.diffuse > 0.0 || sphere.material.specular > 0.0)
                    if(sphere.len < SphereIntersection.len || SphereIntersection.len == miss.len)//Chicking if Sphere is the Closest Sphere if nothing was Hit before it is
                    {
                        SphereIntersection = sphere;
                    }
            }  

        }     
    }

    Intersect intersection = PlaneIntersection;

    if(SphereIntersection != miss)
    {
            if(SphereIntersection.len < PlaneIntersection.len || PlaneIntersection==miss)
            {
                intersection = SphereIntersection;
            }  
    }    
    return intersection;
}

vec3 Tracing(Ray ray) {    
    vec3 fresnel = vec3(0.0);
    vec3 mask = vec3(1.0);
    vec3 color = vec3(0.0);
    Light light = getLight();


  for (int i = 0; i <= Raybounces; ++i) 
  {      
    Intersect hit = trace(ray, i);   

    if(hit != miss)
    {
        if (hit.material.diffuse > 0.0 || hit.material.specular > 0.0)
        {
            vec3 r0 = hit.material.color.rgb * hit.material.specular;
            float hv = clamp(dot(hit.normal, -ray.direction), 0.0, 1.0);
            fresnel = r0 + (1.0 - r0) * pow(1.0 - hv, 5.0);
            mask *= fresnel;  

            //return mask;

            vec3 Hitpoint = ray.origin + hit.len * ray.direction;
            vec3 DirToLight = normalize(light.Position - Hitpoint);
            Ray Shadowray = Ray(Hitpoint + DirToLight*2.0, DirToLight);

            Intersect shadowhit = trace(Shadowray, i);        
            if (shadowhit == miss) //nichtImSchatten
            {         
                color += (clamp(dot(hit.normal, DirToLight), 0.0, 1.0) * light.color
                    * hit.material.color.rgb * hit.material.diffuse
                    * (1.0 - fresnel) * mask / fresnel);         
            }    
            vec3 reflection = reflect(ray.direction, hit.normal);
            ray = Ray(Hitpoint + epsilon * reflection, reflection);
        }

    }
    else
    {
      color += mask * (ambient); break;
    }

  } 
    return color;
}

vec3 RenderColor(in vec2 fragCoord)
{
    //Uv Space
    vec2 uv    = fragCoord.xy / iResolution.xy - vec2(0.5);
    uv.x *= iResolution.x/ iResolution.y;

    //CamMovement
    vec3 newCamPos = CamPosition;
    newCamPos.xz +=  vec2(sin(iTime*MovementSpeed),cos(iTime*MovementSpeed))* 30.;

    //NewCoordinates
    vec3 ForwardVec = normalize(LookPosition-newCamPos);
    vec3 RightVec = cross(vec3(0.,1.,0.), ForwardVec);
    vec3 UpVec = cross(ForwardVec,RightVec);
    vec3 Centerpoint = newCamPos+ ForwardVec*Zoom;

    vec3 Color;

    //TraceRays   
    vec3 ImagePoint = Centerpoint -uv.x*RightVec + uv.y*UpVec;    
    vec3 Raydirection = ImagePoint-newCamPos;
    Raydirection = normalize(Raydirection);

    Ray ray = Ray(newCamPos, Raydirection);
    Color += pow(Tracing(ray) * exposure, vec3(1.0 / gamma));

    return Color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 OversampleCol = vec3(0.);

    if (AntiAliasing)
    {
        float SamplingRange = .4;

         for (int i = 1; i <= OverSampling; ++i) 
  		 {
             float ExtraCoord = SamplingRange/float(i);
             OversampleCol += clamp(RenderColor(fragCoord + vec2(ExtraCoord,ExtraCoord)),0.,1.);
             OversampleCol += clamp(RenderColor(fragCoord + vec2(ExtraCoord,-ExtraCoord)),0.,1.);
             OversampleCol += clamp(RenderColor(fragCoord + vec2(-ExtraCoord,ExtraCoord)),0.,1.);
             OversampleCol += clamp(RenderColor(fragCoord + vec2(-ExtraCoord,-ExtraCoord)),0.,1.);
         }       
        OversampleCol /= float(OverSampling)*4.;
    }
    else
    {
        OversampleCol += RenderColor(fragCoord);
    }

    fragColor = vec4(OversampleCol, 1.0);
}

#define PI 3.141592654

vec2 ToPolar(vec2 pos)
{
   	float r = length(pos);
    // a (0, 2*PI)
    float a = atan(-pos.y,-pos.x)+PI;
    return vec2(r,a);
}

vec2 Rotate(vec2 pos, float angle)
{
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle))*pos;
}

// ====================== Drawing Shapes ===========================
float Circle(vec2 center, vec2 pos, float radius)
{
    vec2 dir = pos - center;
    return step(dot(dir,dir),pow(radius,2.0));
}

float Triangle(vec2 center, vec2 pos, float radius)
{
    vec2 dir = pos - center;

    //return step(-radius,dir.y)*
    //    step(dir.y, sqrt(3.0)*dir.x+2.0*radius)*
    //    step(dir.y, -sqrt(3.0)*dir.x+2.0*radius);

    return step(
        0.0,
        min(dir.y + radius,
            min(- dir.y + sqrt(3.0)*dir.x+2.0*radius,
                - dir.y - sqrt(3.0)*dir.x+2.0*radius)));
}

float Hexagram(vec2 center, vec2 pos, float radius)
{
	float t1 = Triangle(center, pos, radius);

    pos = center + Rotate(pos-center,PI);

    float t2 = Triangle(center, pos, radius);
    return max(t1,t2);
}

float Skeleton(vec2 center, vec2 pos, float radius)
{
	vec2 polar = ToPolar(pos - center);

    // vertical skeleton
    float width = 0.13*radius;
    vec2 dir = Rotate(pos-center, round((polar.y-PI/2.0)/(PI/3.0))*(PI/3.0));
    float ver = mix(0.0,1.0,1.0-clamp(abs(dir.x)/width,0.0,1.0));
    ver = ver - mod(ver,0.25);
    ver *= step(polar.x,radius);

    // horizontal skeleton
    dir.x = abs(dir.x);
    dir = Rotate(dir,-PI/6.0);
    float hor = clamp(dir.y/radius,0.0,1.0);
    hor = fract(hor*4.0);
    hor = 1.0 - abs(hor*2.0 - 1.0);
    hor = hor - mod(hor,0.25);

    return max(ver, hor);
}

float DrawShape(vec2 center, vec2 pos, float radius)
{
    float shapeRatio = sin(iTime*0.7)*0.5+0.5;
    return
		mix(Skeleton(center, pos, radius),
            Hexagram(center, pos, radius),
            shapeRatio);
}

// ================================================================

float Koch(vec2 pos)
{
    const int n = 4;

    float radius = 100.0;
    vec2 center = vec2(0.0);

    float c = DrawShape(center,pos,radius);

	for(int i = 0;i<n;i++)
    {
        vec2 localPos = pos - center;
        float polarAngle = atan(-localPos.y,-localPos.x) + PI;
        float index = floor(polarAngle/(PI/3.0))+0.5;
        center += (radius*4.0/3.0)*vec2(cos(index*PI/3.0),sin(index*PI/3.0));
        radius /= 3.0;
        c += DrawShape(center,pos,radius);
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 pos = 350.0*(2.0*fragCoord - iResolution.xy)/iResolution.y;

    float t = iTime;
    // rotate & scale
    pos = Rotate(pos,iTime*0.4)*(1.0+0.2*sin(t));
    // alpha
   	float c = Koch(pos)*(sin(iTime)*0.2+0.35);

    // color it
    vec3 col = 
        vec3(0.1,0.15,0.2)*pow(1.5-length(uv-vec2(0.5)),2.0) + 
        vec3(0.4,0.8,0.88)*c;

