}

const float pi = 3.141592653589;
float mmod(float a, float b) { return a - floor(a/b); }
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	vec2 c1 = vec2(0.5,0.5);
    float n = 3.0*mmod(iTime,1.0);
    float a = cos(length(uv - c1) * 6.0 * n * 2.0 * pi) * sin(length(uv - c1) * 4.0 * (n+1.0) * 2.0 * pi);
    vec3 col = vec3(0,1,0) * a;

    fragColor = vec4(col,1.0);
}

vec2 rot(vec2 v, float angle)
{
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, -s, s, c)*v;
}
float cro(in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

float sdUnevenCapsule( in vec2 p, in vec2 pa, in vec2 pb, in float ra, in float rb )
{
    p  -= pa;
    pb -= pa;
    float h = dot(pb,pb);
    vec2  q = vec2( dot(p,vec2(pb.y,-pb.x)), dot(p,pb) )/h;

    //-----------

    q.x = abs(q.x);

    float b = ra-rb;
    vec2  c = vec2(sqrt(h-b*b),b);

    float k = cro(c,q);
    float m = dot(c,q);
    float n = dot(q,q);

         if( k < 0.0 ) return sqrt(h*(n            )) - ra;
    else if( k > c.x ) return sqrt(h*(n+1.0-2.0*q.y)) - rb;
                       return m                       - ra;
}


vec2 spiral(vec2 uv,float zoom, float len)
{
    float d = length(uv) * zoom;
    d=log(d);
    uv = rot(uv,d*5.0);
	float v = 1.0-sdUnevenCapsule(uv,vec2(0.0,0.0),vec2(0.4*len,0.0),0.0,0.4*len)*14.0;
    float l = d;   
    return vec2(v,l);	// distx , disty
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    float t = fract(iTime*0.4)*6.0;

	vec2 dist = spiral(uv,1.0,t);
    float v = dist.x;
    float l = dist.y;

	v = smoothstep(0.5-0.1,0.5+0.1,v);
    //vec3 col = vec3(v*1.0-l/t);

    vec3 col = mix(vec3(0.0,0.0,0.2),vec3(1.0,0.6,0.5),v*1.0-l/t);

 	vec2 q = fragCoord/iResolution.xy;
    col *= 0.3 + 0.7*pow(16.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.25);    

    fragColor = vec4(col, 1.0);
}

// setup
/////////////////////////////////////////////////////////////////
const vec3 CAMERA_POS = vec3(0, 0, 2.0);
const vec3 FORWARD = vec3(0.0, 0.0, -1.0);
const vec3 RIGHT = normalize(cross(vec3(0.0, 1.0, 0.0), FORWARD));
const vec3 UP = normalize(cross(FORWARD, RIGHT));
const mat3 CAMERA_MATRIX = mat3(RIGHT, UP, FORWARD);
const float FOV = 5.5;

const float LIGHT_INTENSITY = 1.0;
const float SHADOW_FACTOR = 0.3;
const float BIAS = 0.05;
const float LIGHT_MOVE_FACTOR = 0.2;

struct Material
{
    vec3 color;
};

struct Sphere
{
    vec3 position;
    float radius;
    Material mat;
};

const Material RED = Material(vec3(1.0, 0.0, 0.0));
const Material GREEN = Material(vec3(0.0, 1.0, 0.0));
const Material BLUE = Material(vec3(0.0, 0.0, 1.0));
const Material PURPLE = Material(vec3(1.0, 0.0, 1.0));
const Material WHITE = Material(vec3(1.0, 1.0, 1.0));

Sphere SPHERES[] = Sphere[]
(
    Sphere(vec3(-1.0, 0.0, -5.0), 0.5, RED),
    Sphere(vec3(0.2, 0.0, -5.0), 0.4, GREEN),
    Sphere(vec3(0.8, 0.0, -5.0), 0.3, BLUE),
    Sphere(vec3(1.5, 0.0, -5.0), 0.2, PURPLE),
    Sphere(vec3(0.0, 0.0, -7.0), 1.2, WHITE)
);

struct IResult
{
    bool iIntersected;
    vec3 iPoint;
    vec3 iNormal;
};
/////////////////////////////////////////////////////////////////

// get a direction ray in world-space
vec3 rayDir(vec2 coord)
{
    return normalize(CAMERA_MATRIX * vec3(coord, FOV));
}

// test ray-sphere intersection
IResult sphereIntersect(vec3 start, vec3 dir, Sphere s)
{
    vec3 m = start - s.position;
    float b = dot(m, dir); 
	float c = dot(m, m) - s.radius * s.radius;

    if (c > 0.0f && b > 0.0f)
        return IResult(false, vec3(0.0), vec3(0.0));

	float discr = b * b - c;

    if (discr < 0.0f)
		return IResult(false, vec3(0.0), vec3(0.0));

    float t = -b - sqrt(discr);
    if (t < 0.0)
        t = 0.0;

    vec3 ipoint = start + t * dir;

    return IResult(true, ipoint, normalize(ipoint - s.position));
}

// test for rs-intersection, run reflection test, run shadow test
vec3 rayTrace(const vec3 dir, vec3 light_pos)
{
    for (int i = 0; i < SPHERES.length(); ++i)
    {
        IResult result = sphereIntersect(CAMERA_POS, dir, SPHERES[i]);

        if (result.iIntersected)
        {
            vec3 indirect = vec3(0.0);
            float shadow = 1.0;
            vec3 ldir = -normalize(light_pos);
            vec3 from_light = normalize(result.iPoint - light_pos);

            for (int j = 0; j < SPHERES.length(); ++j)
            {   
            	IResult result2 = sphereIntersect(result.iPoint + result.iNormal * BIAS, result.iNormal, SPHERES[j]);

                if (result2.iIntersected)
                    indirect += LIGHT_INTENSITY * max(dot(ldir, result2.iNormal), 0.0) * SPHERES[j].mat.color;
            }

            for (int j = 0; j < SPHERES.length(); ++j)
            {
            	IResult result2 = sphereIntersect(result.iPoint + from_light * BIAS, from_light, SPHERES[j]);

                if (result2.iIntersected)
                    shadow -= SHADOW_FACTOR;
            }

            if (shadow < 0.0)
                shadow = 0.0;

            return (indirect + SPHERES[i].mat.color) * shadow * LIGHT_INTENSITY * max(dot(ldir, result.iNormal), 0.0);
        }
    }

    return vec3(0.0);
}

// move spheres up and down
void animateSpherePositions()
{
    for (int i = 0; i < SPHERES.length() - 1; ++i)
    {
     	SPHERES[i].position.y = cos(sin(SPHERES[i].position.x)) + sin(iTime * (float(i) + 0.2)) - 0.8;   
    }
}

// move light around the scene
vec3 animateLight()
{
    float factor = iTime * LIGHT_MOVE_FACTOR;
    float circleX = sin(factor);
    float circleZ = cos(factor);
 	return 50.0 * vec3(circleX, 0.0, circleZ);
}

// output pixel
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    vec3 dir = rayDir(coord);

    animateSpherePositions();

    const vec3 gamma = vec3(0.454545);
    fragColor = vec4(pow(rayTrace(dir, animateLight()), gamma), 1.0);
}

