// here some values to play for you ...

#define MAP_OCTAVE 7
#define BACK_COLOR vec3(0.4, 0.05, 0.05)
#define INNER_COLOR vec3(0.2, 0.2, 0.6)
#define GLAS_COLOR vec3(0.5, 0.5, 0.9)








//---------------------------------------------------------
//Play with the detail level, change it!
#define DETAIL 5

//also play with this one. Fixed it so that the marble doesn't go dark when this is small.
#define INNER_DEPTH 64

//play with these too you can try changes in the range of 1s, 10s or 100s
//but keep a decimal point on it or it won't compile
#define COLOR_CONTRAST 45.0
#define MILKY_LIGHT 80.0

//not exactly opacity.. in this model the "subtractive" colors are actually adding light, but light can 
//have negative colors... Yeah it's not a realistic model. I did it by mistake, but it looks cool. 

//0 is color is additive
//1 is color is kind of subtractive
#define OPACITY_OF_COLOR 1.0

float zoom=1.;


vec2 cmul( vec2 a, vec2 b )  { return vec2( a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x ); }
vec2 csqr( vec2 a )  { return vec2( a.x*a.x - a.y*a.y, 2.*a.x*a.y  ); }


mat2 rot(float a) {
	return mat2(cos(a),sin(a),-sin(a),cos(a));	
}

vec2 iSphere( in vec3 ro, in vec3 rd, in vec4 sph )//from iq
{
	vec3 oc = ro - sph.xyz;
	float b = dot( oc, rd );
	float c = dot( oc, oc ) - sph.w*sph.w;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0);
	h = sqrt(h);
	return vec2(-b-h, -b+h );
}
//---------------------------------------------------------
vec3 map(in vec3 p) {

	float res = 0.;

    vec3 c = p;
	for (int i = 0; i < DETAIL; ++i) {
        p =.7*abs(p)/dot(p,p) -.7;
        p.yz= csqr(p.yz);
        p=p.zxy;
        res += exp(-19. * abs(dot(p,c)))+.02;

	}
	return res*COLOR_CONTRAST*0.013*(normalize(p)+(1.0-OPACITY_OF_COLOR)*vec3(1.0));
}
//---------------------------------------------------------
vec3 raymarch( in vec3 ro, vec3 rd, vec2 tminmax )
{
    float t = tminmax.x;
    float dt = .02;
    if (animate_pattern)
      dt = 0.02 + 0.01*cos(time*0.5);

    vec3 col= vec3(0.);
    vec3 c = vec3( 0.);
    for( int i=0; i<INNER_DEPTH; i++ )
	{
        t+=dt*exp(-2.*length(c));
        if(t>tminmax.y)break;
        //vec3 pos = ro+t*rd;
        vec3 pos = refract( ro, (ro+t*rd)/.7,-.012);
        c = map(pos);//map(ro+t*rd);               

        float gr= MILKY_LIGHT*0.02/float(INNER_DEPTH);//.01*(2.0-float(i)/64.0);

        col = .99*col+ .08*c+vec3(gr);//vec3(c*c, c, c*c*c);//green	
        //col = .99*col+ .08*vec3(c*c*c, c*c, c);//blue
    }    
    return col;
}

//---------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    keyInput();

    vec2 uv = 2.0*(fragCoord.xy / resolution.xy) - 1.0;
    uv.x *= resolution.x / resolution.y;
    vec2 m = vec2(-0.5);
//    if( mouse.z > 0.0 )   // mouse pressed?
	m = mouse.xy / resolution.xy * 3.14;
//    m += mouse.xy * 3.14;    // use on glslsandbox.com

    float eye = 0.1;
    float radius = 1.4;

    if (cross_eye_view)     // stereo view ?
    {
      radius = 1.0;
      if (uv.x > 0.)
      {
        uv.x -= 0.8;
        eye = -0.1;
      } else {
        uv.x += 0.8;
        eye = +0.1;
      }
    }

    // camera
    vec3 ro = vec3(6,0,0);   // origin
    ro.y += eye;
    ro.yz *= rot(m.y);
    ro.xz *= rot(m.x);
    if (rotation_scene)
      ro.xz *= rot(0.07*time);

    vec3 ta = vec3( 0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    vec3 rd = normalize( uv.x*uu + uv.y*vv + 4.0*ww );

    vec2 tmm = iSphere( ro, rd, vec4(0.,0.,0.,radius) );

    // sound animation
//	float fft  = texture( iChannel0, vec2(p.x+22.,0.25) ).x;
//  float wave = texture( iChannel0, vec2(p.x,0.75) ).x;
//  tmm += 0.6*wave + fft;

    // raymarch
    vec3 col = raymarch(ro,rd,tmm);  // get sphere Color

    const float taa = 6.3;     // antialiasing limit
    float aa = (2.2*(tmm.x - taa));
    if ((tmm.x < 0.0) || (tmm.x > taa))
    {
        vec3 bgCol = BACK_COLOR * map(rd);   // background color
        if (tmm.x > taa)
    	{
	        //bgCol = vec3(9.0, 5.0, 0.0);   // for testing antialiasing
            col = mix( col, bgCol, aa);      // do antialasing
        }
        else col = bgCol;        // set pure background color
    }

    if (tmm.x > 0.0)             // add sphere reflection ?
    {
        vec3 nor = reflect(rd, (ro+tmm.x*rd) * 0.5);  // sphere normal
        float fre = pow(0.3+ clamp(dot(nor,rd), 0.0, 1.0), 3. )*1.3;
        // add reflecting color
        col += GLAS_COLOR * fre * 0.1 *(1.0-aa);   // add some solid glas
        if (show_reflections)
	      col += BACK_COLOR * map(nor) * fre * 0.3 *(1.0-aa);
    }

    // shade
    col = 0.5 *(log(1.0 + col));
    col = clamp(col, 0., 1.0);
    fragColor = vec4( col, 1.0 );
}

#define MAX_STEPS 1000
#define MAX_DIST 100.
#define SURF_DIST 0.0001
#define ROT_SPEED 0.


struct Sphere{
    vec4 s;
    vec4 color;
    float reflectiveness;
};

struct Plane{
 	vec4 p;
    vec4 color;
    float reflectiveness;
};

struct Torus{
 	vec3 t;
    vec2 r;
    vec3 d;
    vec4 color;
    float reflectiveness;
};   

struct Cube{
    vec4 c;
    vec3 d;
    vec4 color;
    float reflectiveness;
};
Sphere sp = Sphere(vec4(0., 1., -6., 1), vec4(1., 0., 0., 1.), 0.);
Plane pl = Plane(normalize(vec4(0., 1., 0., 0.)), vec4(1., 1., 0., 0.), 0.);
Torus tr = Torus(vec3(0., 1., -6.), vec2(1., .4), vec3(0., 1., 0.), vec4(0., 1., 1., 1.), 0.);
Cube cb = Cube(vec4(3, 1, -6, 1), vec3(0, 1, 0), vec4(0, 1, 0, 1), 0.);

float sdSphere(vec3 p, Sphere _sp){
 	return (length(p-_sp.s.xyz)-_sp.s.w);   
}

float sdPlane(vec3 p, Plane _pl){
    float d = dot(p, _pl.p.xyz)-_pl.p.w;

    return d;
}

float sdTorus(vec3 p, Torus _tr){
    float dxz = length(p.xz-_tr.t.xz)-_tr.r.x;
    float dxyz = length(vec2(dxz, p.y-_tr.t.y))-_tr.r.y;
    return dxyz;

}

float sdCube(vec3 p, Cube _cb){
    vec3 dif = abs(_cb.c.xyz-p);
    return length(max(vec3(0), dif-_cb.c.w));
}

float sdIntersect(float d1, float d2) {
    return max(d1, d2);
}

float sdUnion(float d1, float d2){
    return min(d1, d2);
}

float sdDifference(float d1, float d2){
    return max(d1, -d2);
}
float getDist(vec3 p){
	float sD = sdSphere(p, sp);
    float pD = sdPlane(p, pl);
    float tD = sdTorus(p, tr);
    float cD = sdCube(p, cb);
    float _min = sD;
    _min = sdDifference(sD, tD);
    _min = sdUnion(_min, pD);
    _min = min(_min, cD);
    return _min;
}

float rayMarch(vec3 d, vec3 o){
 	vec3 p = o;
    float dO = 0.;
    float dist;
    for(int i = 0; i < MAX_STEPS; i++){
        dist = getDist(p);
    	dO += dist; 
        p = o + d*dO;
        if(dO > MAX_DIST || dist < SURF_DIST){
            break;
        }
    }
	return dO;
}

vec3 getNormal(vec3 p){

    vec2 dd = vec2(SURF_DIST, 0);
    float d = getDist(p);
    float dx = getDist(p-dd.xyy);
    float dy = getDist(p-dd.yxy);
    float dz = getDist(p-dd.yyx);
    return normalize(d-vec3(dx, dy, dz));


}

vec4 getCol(vec3 p){
    float sd = abs(sdSphere(p, sp));
    float pd = abs(sdPlane(p, pl));
    float td = abs(sdTorus(p, tr));
    float cd = sdCube(p, cb);
    float _min = min(sd, pd);
    _min = min(_min, td);
    _min = min(_min, cd);
    if(_min == sd) return sp.color;
    if(_min == pd) return pl.color;
    if(_min == td) return tr.color;
    if(_min == cd) return cb.color;



    return vec4(1, 1, 0, 1);
}

vec4 getLight(vec3 p){
    vec3 norm = getNormal(p);
    p += norm*SURF_DIST*2.;
    vec3 lightPos = vec3(10.*sin(iTime), 7, 10.*cos(iTime));
    float distToLight = length(p-lightPos);
    vec3 dirToLight = normalize(lightPos-p);
    float dist = rayMarch(dirToLight, p);
    float diff = mix(.1, 1., clamp(dot(dirToLight, norm), 0., 1.));

    vec4 col = getCol(p);

    if(dist < distToLight){
        return col*0.1;
    }else{
        return col*diff;
    }

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y/1.;
    vec3 camDir = vec3(sin(iTime*ROT_SPEED), 0., -cos(iTime*ROT_SPEED));
    vec3 camPos = vec3(0, 2, 0.);

    vec3 px = vec3(-camDir.z, 0, camDir.x)*uv.x;
    vec3 py = vec3(0, 1, 0)*uv.y;



    vec3 rD = normalize(camDir+px+py);
    vec3 rO = camPos;

    float dist = rayMarch(rD, rO);
    vec3 p = rO + rD*dist;
    vec4 col = vec4(0);
    if(dist < MAX_DIST){
        col = vec4(getLight(p));
    }
	//col = vec4(vec3(sdCube(p, vec4(-5, 1, -6, 1))/20.), 1.);
    fragColor = vec4(col);
}

float boundary = 7.0;	// Defines the boundary beyond which a point is considered to have escaped; almost completely irrelvant but artifacts occur less than ~5.0
int iterations = 500;	// The maximum number of iterations to perform on a given point
float zoomspeed = 0.5;	// Initial zoom level at t = 0.0
vec2 zoomcenter = vec2(-0.7335185, 0.197);	// The point to zoom in on

// The set of colors that define the gradient
vec3[] colors = vec3[](vec3(1.0,0.0,0.0), vec3(1.0,1.0,0.0), vec3(0.0,1.0,0.0), vec3(0.0,1.0,1.0), vec3(0.0,0.0,1.0), vec3(1.0, 0.0, 1.0));

// Multiply two vec2's like they're complex numbers
vec2 cpx_mul(vec2 a, vec2 b) {
    return vec2(a.x*b.x-a.y*b.y, a.x*b.y+a.y*b.x);
}

// Sample a gradient
vec3 color_ramp(float fac, vec3[6] colors, bool repeat) {
    if (repeat) fac = mod(fac, 6.0);
    else fac = clamp(fac, 0.0, 5.0);
    return mix(colors[int(fac)], colors[(int(fac) + 1) % 6], fract(fac));
}

vec3 fractal(vec2 c) {
    // Zoom mechanics
    float zoomlevel = exp(-iTime * zoomspeed);
    c *= zoomlevel;
    c += zoomcenter;
    int maxiter = int(iTime / zoomspeed) + iterations;

    // Algorithm iteration with early exit for efficiency
    int i = 0;
    vec2 f = vec2(0.0);
    while (i < maxiter && length(f) < boundary) {
        i++;
        f = cpx_mul(f, f) + c;
    }

    // Color according to escape time
    if (length(f) < boundary)
        // Point is still bounded
        return vec3(0.0);
    else {
        // Point has diverged beyond the specified boundary
    	float smoothfac = float(i + 1) - log(log(length(f))) / log(2.0);
        return color_ramp(smoothfac - iTime, colors, true);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv1 = fragCoord;
    vec2 uv2 = fragCoord + vec2(0.5, 0.0);	// 4xAA
    vec2 uv3 = fragCoord + vec2(0.0, 0.5);
    vec2 uv4 = fragCoord + vec2(0.5);
    vec2 c1 = uv1 / iResolution.yy - vec2(1.0, 0.5);	// Centers the set in the window
    vec2 c2 = uv2 / iResolution.yy - vec2(1.0, 0.5);
    vec2 c3 = uv3 / iResolution.yy - vec2(1.0, 0.5);
    vec2 c4 = uv4 / iResolution.yy - vec2(1.0, 0.5);

    fragColor.rgb = fractal(c1);
    fragColor.rgb += fractal(c2);
    fragColor.rgb += fractal(c3);
    fragColor.rgb += fractal(c4);
    fragColor.rgb /= 4.0;
}

