#define PI 3.14159265359
// Props to FabriceNeyret2 for this antialiasing function :)
#define S(v) smoothstep(0.,1.5*fwidth(v), v )

// keyToggle Stolen from https://www.shadertoy.com/view/Xsl3zX :b
// Don't know how it works, might have to look into it later.
bool keyToggle(int ascii) 
{
	return (texture(iChannel0,vec2((.5+float(ascii))/256.,0.75)).x > 0.);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    // Output to screen
    vec3 color = vec3(0.0);

    // To move the cross we move the space

    // Add the shape on the foreground
    // Add  0.1 + sin(u_time) * 0.2) to move displacement
    // Add u_time to displament sin for x movement
    float yDisplacement = sin(st.x * PI * 2. + PI* iTime *2.)*(0.1 + sin(iTime*2.+PI) * 0.05) ;
    st.y -= yDisplacement;
    float freq = 19.;
    // front wave
    vec3 frontWave = vec3(S(sin(st.y * PI * freq) * 0.5+0.5 -0.5));
    color = frontWave;
    // Initially this sin function is a screen long. And to move into place, we just move it by the frequency 
    // PI * 1./14. Allways multiplied by PI because that's what sin uses
    float lines = min(freq, 5.);
    // Hidden has to be before the reset because we need it with the displacement
    float mask = S(sin(st.y * PI + PI * 1./freq * lines)*0.5+0.5-0.5);
    // Multiply by mask to eliminate everything outside of the white part of the mask
    color *= mask;
    // Reset y displacement
    // So it doesnt affect the backWave
    st.y += yDisplacement;

    //  Fisrt part of mult is for the wave itself.
    // Second part is for how strong the wave is.
    float xDisplacement = sin(st.y * PI * 2. + iTime * PI *2.)*(0.1 + sin(iTime*2.) *0.);
    // xDisplacement = 600.5;
    st.x -= xDisplacement;
    // u_time here moves x axis. But dont need it because we have xDisplacement
	vec3 backWave = vec3(S(sin(st.x * PI * freq) * 0.5+0.5- 0.5));
    // Multiply by the reverse mask to only add that part of the wave
	color += backWave * (1. - mask);
    // Keyboard controls
    // Look at the different parts of this shader
    if (keyToggle(49)) { 
        color = frontWave * smoothstep(-0.05,0.05,sin(iTime* 1.5)) + backWave * smoothstep(-0.05,0.05,sin(iTime *1.5 + PI));
    }
	if (keyToggle(50)) color = vec3(mask);

    fragColor = vec4(color,1.0);
}

float rand(vec2 value)
{
	return fract(sin(dot(value, vec2(23.1245,87.2980))) * 5387.58135649);
}

vec2 rand2d(vec2 value)
{
	return fract(sin(vec2(dot(value, vec2(23.1245,87.2980)), dot(value, vec2(127.5123,39.183)))) * 5387.58135649);
}

vec3 voroni(vec2 uv)
{
    vec2 iuv = floor(uv);
    vec2 fuv = fract(uv);
    vec3 col = (fuv).xyy;
    float mdist = 1.;
    vec2 p;
    vec2 sp;
    // get neighbour tiles
    for(int y = -1; y <= 1; y++)
    {
        for(int x = -1; x <= 1; x++)
        {
            vec2 n = vec2(float(x),float(y));

            p = rand2d(iuv + n);

            // animate
            p.y = 0.5 + 0.5*sin(iTime + 5.*p.y);
            p.x = 0.5 + 0.5*cos(iTime + 5.*p.x);

            vec2 diff = n + p - fuv;
            float dist = length(diff);

            // 

            if(dist < mdist)
            {
            	mdist = min(mdist,dist);
                sp = p;
            }
        }
    }
    return vec3(round(mdist/mod(fuv.y,0.01)));
}

