// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// Method 1 :
// 1) For each segment, check if the point is on the right or left side.
// 2) If the point is on the same side for all the segments, the point is inside the triangle.
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
vec3 edgeSide(in vec3 A, in vec3 B, in vec3 C, in vec3 I, inout bool isIn)
{
    vec3 col;
    isIn = false;

    vec3 N = normalize(cross(B-A, C-A));

    // Check if the point is in the triangle /////////////////////////////////////////
    vec3 N_AB = cross(B-A, N); // Normal to AB vector
    vec3 N_BC = cross(C-B, N); // Normal to BC vector
    vec3 N_CA = cross(A-C, N); // Normal to CA vector

    float dir_N_AB = dot(I-A, N_AB);
    float dir_N_BC = dot(I-B, N_BC);
    float dir_N_CA = dot(I-C, N_CA);

    bool all_positive = (dir_N_AB >= 0.0) && (dir_N_BC >= 0.0) && (dir_N_CA >= 0.0);
    bool all_negative = (dir_N_AB <= 0.0) && (dir_N_BC <= 0.0) && (dir_N_CA <= 0.0);
    isIn = all_positive || all_negative;

    // Color interpolation ///////////////////////////////////////////////////////////
    if (isIn)
    {
        float a = dot(I-B, N_BC); // Orthogonal distance from I to BC
        float b = dot(I-C, N_CA); // Orthogonal distance from I to CA
        float c = dot(I-A, N_AB); // Orthogonal distance from I to AB

        float sum = a+b+c;
        a /= sum;
        b /= sum;
        c /= sum;

     	col = vec3(a,b,c);
    }

	return col;
}

// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// Method 2 :
// 1) Form 4 triangles from your point I : AIB, BIC, CIA and ABC.
// 2) Compute the areas. [formula: area(XYZ)=0.5*length(cross(Y-X,Z-X))]
// 2) If area(AIB)+area(BIC)+area(CIA)=area(ABC), the point is inside the triangle.
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
vec3 areas(in vec3 A, in vec3 B, in vec3 C, in vec3 I, inout bool isIn)
{
    vec3 col;
    isIn = false;

    vec3 N = normalize(cross(B-A, C-A));

    // Check if the point is in the triangle /////////////////////////////////////////
    float areaAIB = 0.5*length(cross(I-A, B-A));
    float areaBIC = 0.5*length(cross(I-B, C-B));
    float areaCIA = 0.5*length(cross(I-C, A-C));
    float areaABC = 0.5*length(cross(B-A, C-A));

    isIn = abs((areaAIB+areaBIC+areaCIA)-areaABC) < 0.001;

    // Color interpolation ///////////////////////////////////////////////////////////
    if (isIn)
    {
        float a = areaBIC/areaABC;
        float b = areaCIA/areaABC;
        float c = areaAIB/areaABC;

     	col = vec3(a,b,c);
    }

	return col;
}

// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// Method 3 :
// 1) Form 3 triangles from your point I : AIB, BIC and CIA.
// 2) Compute the signed areas. [formula: signedArea(XYZ)=dot(0.5*(cross(Y-X,Z-X)),N), with N the triangle's normal]
// 2) If the signs are all the same, the point is inside the triangle.
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
vec3 areasSigned(in vec3 A, in vec3 B, in vec3 C, in vec3 I, inout bool isIn)
{
    vec3 col;
    isIn = false;

    vec3 N = normalize(cross(B-A, C-A));

    // Check if the point is in the triangle /////////////////////////////////////////
    float signedAreaAIB = dot(0.5*(cross(I-A, B-A)),N);
    float signedAreaBIC = dot(0.5*(cross(I-B, C-B)),N);
    float signedAreaCIA = dot(0.5*(cross(I-C, A-C)),N);

    bool all_positive = (signedAreaAIB >= 0.0) && (signedAreaBIC >= 0.0) && (signedAreaCIA >= 0.0);
    bool all_negative = (signedAreaAIB <= 0.0) && (signedAreaBIC <= 0.0) && (signedAreaCIA <= 0.0);
    isIn = all_positive || all_negative;

    // Color interpolation ///////////////////////////////////////////////////////////
    if (isIn)
    {
        float areaABC = 0.5*length(cross(B-A, C-A));

        float a = abs(signedAreaBIC)/areaABC;
        float b = abs(signedAreaCIA)/areaABC;
        float c = abs(signedAreaAIB)/areaABC;

     	col = vec3(a,b,c);
    }

	return col;
}

// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// Method 4 :
// 1) Rewrite the point, I, as (I-A)=u*(B-A)+v*(C-A). [which is also I=(1-u-v)*A+u*B+v*C, barycentric coordinates]
// 2) Compute u and v. [to get u, first cross product the equation with (C-A), then project along the face normal N; then similarly to get v]
// 2) If u and v are in [0,1] and u+v<=1, the point is inside the triangle.
// //////////////////////////////////////////////////////////////////////////////////////////////////////
// //////////////////////////////////////////////////////////////////////////////////////////////////////
vec3 uvCoord(in vec3 A, in vec3 B, in vec3 C, in vec3 I, inout bool isIn)
{
    vec3 col;
    isIn = false;

    vec3 N = normalize(cross(B-A, C-A));

    // Check if the point is in the triangle /////////////////////////////////////////
    float u = dot(cross(I-A,C-A),N)/dot(cross(B-A,C-A),N);
    float v = dot(cross(I-A,B-A),N)/dot(cross(C-A,B-A),N);

    isIn = (u >= 0.0) && (v >= 0.0) && (u+v <= 1.0);

    // Color interpolation ///////////////////////////////////////////////////////////
    if (isIn)
    {
        float a = 1.0-u-v;
        float b = u;
        float c = v;

     	col = vec3(a,b,c);
    }

	return col;
}









