// Draw a point
vec3 drawPoint(in vec3 X, in vec3 I, in float radius, in vec3 color)
{
    if (length(I-X) <= radius)
    {
        if (int(floor(length(I-X)/radius*3.0))%2 == 0)
        {
        	return color;
        }
    }
	return vec3(0.0);
