void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
	vec3 I = vec3((2.0*fragCoord.xy-iResolution.xy)/iResolution.y*1.5, 1.0);

    // Triangle definition
    float m = 0.5;
	vec3 A = vec3(cos(iTime*m + vec2(0.0,2.00) + 0.0), 1.0);
    vec3 B = vec3(cos(iTime*m + vec2(0.0,1.50) + 1.5), 1.0);
	vec3 C = vec3(cos(iTime*m + vec2(0.0,3.00) + 4.0), 1.0);

    // Mouse manipulation
    if (iMouse.z>=1.0)
    {
        vec3 Click = vec3((2.0*iMouse.xy-iResolution.xy)/iResolution.y*1.5, 1.0);
        A = Click;
    }

    // Interpolation in the triangle, according to a given method
    float method = mod(floor(iTime/5.0), 4.0)+1.0;
    bool isIn;
    vec3 col;
    if (method==1.0)
    {
    	col = edgeSide(A,B,C,I,isIn);
        col = max(col,string_Method1(fragCoord.xy*0.02));
    }
    else if (method==2.0)
    {
    	col = areas(A,B,C,I,isIn);
        col = max(col,string_Method2(fragCoord.xy*0.02));
    }
    else if (method==3.0)
    {
    	col = areasSigned(A,B,C,I,isIn);
        col = max(col,string_Method3(fragCoord.xy*0.02));
    }
    else if (method==4.0)
    {
    	col = uvCoord(A,B,C,I,isIn);
        col = max(col,string_Method4(fragCoord.xy*0.02));
    }

    // Draw the vertices
    if (!isIn)
    {
        col = max(col,drawPoint(A,I,0.07,vec3(1.0,0.0,0.0)));
    	col = max(col,drawPoint(B,I,0.07,vec3(0.0,1.0,0.0)));
    	col = max(col,drawPoint(C,I,0.07,vec3(0.0,0.0,1.0)));
    }

    // Draw the labels
    vec3 pA = A+0.2*normalize(normalize(A-B)+normalize(A-C));
    vec3 pB = B+0.2*normalize(normalize(B-C)+normalize(B-A));
    vec3 pC = C+0.2*normalize(normalize(C-A)+normalize(C-B));

    col = max(col,vec3(string_A((I.xy-pA.xy)*5.0+0.5),0.0,0.0));
    col = max(col,vec3(0.0,string_B((I.xy-pB.xy)*5.0+0.5),0.0));
    col = max(col,vec3(0.0,0.0,string_C((I.xy-pC.xy)*5.0+0.5)));



	fragColor = vec4(col,1.0);
}

