float sdSphere(vec3 pos, vec3 center, float radius)
{
    return length(center - pos) - radius;
}

float sdPlane( vec3 p, vec4 n )
{
  return dot(p,n.xyz) + n.w;
}

//Ray-sphere intersection
bool raycastSphere(vec3 ro, vec3 rd, out vec3 p0, out vec3 p1, vec3 center, float r)
{
    float A = 1.0; //dot(rd, rd);
    float B = 2.0 * (rd.x * (ro.x - center.x) + rd.y * (ro.y - center.y) + rd.z * (ro.z - center.z));
    float C = dot(ro - center, ro - center) - (r * r);

    float D = B * B - 4.0 * A * C;
    if (D < 0.0)
    {
        return false;
    }
    else
    {
        float t0 = (-B - D)/(2.0 * A);
        float t1 = (-B + D)/(2.0 * A);
        p0 = ro + rd * t0;
        p1 = ro + rd * t1;
        return true;
    }
}

//X-axis rotation matrix
vec3 rotateX(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(1.0, 0.0, 0.0,
        			0.0, cosTheta, -sinTheta,
    			    0.0, sinTheta, cosTheta);

    return rot * p;
}

//Y-axis rotation matrix
vec3 rotateY(vec3 p, float t)
{
    float cosTheta = cos(t);
    float sinTheta = sin(t);
    mat3 rot = mat3(cosTheta, 0.0, sinTheta,
        			0.0, 1.0, 0.0,
    			    -sinTheta, 0.0, cosTheta);

    return rot * p;
}

//iq's gradient noise
vec3 hash( vec3 p )
{
	p = vec3( dot(p,vec3(127.1,311.7, 74.7)),
			  dot(p,vec3(269.5,183.3,246.1)),
			  dot(p,vec3(113.5,271.9,124.6)));

	return -1.0 + 2.0*fract(sin(p)*43758.5453123);
}

float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}

//Fractal (pink) noise function S(f) ∝ 1/f^α, {0 <= α <=2}
float fbm(vec3 seed, int octaves, float freq, float lac)
{
    float val;
    for (int i = 1; i < octaves; i++)
    {
        val += noise(seed * freq * float(i)) / (pow(float(i), lac));
    }

    return val;
}

float map(vec3 pos)
{   
    float mX = iMouse.x/iResolution.x;
    float thickness = mix(0.5, 0.9999, mX);
    float mY = iMouse.y/iResolution.y;
    float freq = mix(8.0, 1.0, mY);
    float d1 = sdSphere(pos, _Center, _Radius);
    float d2 = -sdSphere(pos, _Center, _Radius * thickness);
    float df = max(d1, d2);
    df = max(df, abs(noise(pos*freq)));
    //df += cos(pos.y*16.0)/48.0;
    //df += cos(pos.x*16.0)/48.0;
    //df += cos(pos.z*16.0)/48.0;

    return df;
}

vec3 raymarch(vec3 pos, vec3 dir)
{
    for(int i = 0; i < MAX_STEPS; i++)
    {
        float dist = map(pos);

        if (dist <= MIN_DIST)
        {
            return pos;
        }

        pos += dir * dist * _Epsilon;
    }

    return vec3(0.0);
}

//Approximate surface normal using central difference
vec3 calculateNormal(vec3 p)
{
    const float eps = 0.01;

    return normalize(vec3(map(p + vec3(eps, 0, 0)) - map(p - vec3(eps, 0, 0)),
                          map(p + vec3(0, eps, 0)) - map(p - vec3(0, eps, 0)),
                          map(p + vec3(0, 0, eps)) - map(p - vec3(0, 0, eps))));
}

float softShadow(vec3 ro, vec3 rd, float mint, float maxt, float k )
{
    float res = 1.0;
    int i = 0;
    float t = mint;
    for(int i = 0; i < 64; i++)
    {
        if (t >= maxt)
            break;

        float h = map(ro + rd*t);
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

//Tri-planar texture mapping
vec3 triPlanar(vec3 p, vec3 n, float k)
{
    vec2 xUV = p.zy/_TextureScale;
    vec2 yUV = p.xz/_TextureScale;
    vec2 zUV = p.xy/_TextureScale;

    vec3 colX = texture(iChannel0, xUV).rgb;
    vec3 colY = texture(iChannel0, yUV).rgb;
    vec3 colZ = texture(iChannel0, zUV).rgb;

    vec3 blend = pow(abs(n),vec3(k));
    blend = blend/(blend.x + blend.y + blend.z);

    return colX * blend.x + colY * blend.y + colZ * blend.z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec3 rayOrigin = vec3(0.0, 0.0, -5.0);

    //Compute eye vector from field of view
    vec2 uv = fragCoord/iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    float d = ar/tan(radians(_FoV/2.0));    
    vec3 rayDir = normalize(vec3((-1.0 + 2.0 * uv) * vec2(ar, 1.0), d));

    rayDir = rotateY(rayDir, iTime * 0.2);
    rayOrigin = rotateY(rayOrigin, iTime * 0.2);
    //rayDir = rotateX(rayDir, iTime * 0.1);

    vec3 col = vec3(0.0);

    vec3 start, end;

    if(raycastSphere(rayOrigin, rayDir, start, end, _Center, _Radius))
    {
        vec3 sp = raymarch(start, rayDir);

        vec3 n = calculateNormal(sp);

        vec3 r = rayOrigin - sp;
        float atten = 1.0/dot(r,r);

        float nl = dot(n, -rayDir);

        vec3 h = (n-rayDir)/length(n-rayDir);     
        float spec = pow(max(dot(n, h), 0.0), _SpecularExp) * max(0.0, nl);

        col = triPlanar(sp, n, 2.0); 

        float d = length(r);
       	float shadow = softShadow(sp, -rayDir, 0.0001, d, 16.0);

        //Lighting
        //float flicker = ((1.0 + sin(iTime*2.0)/2.0) + (1.0 + sin(iTime*10.0)/4.0) + (1.0+sin(iTime*25.0)/8.0))*0.5;
        col = vec3(0.1, 0.1,0.2) + col * (nl + spec) * atten * shadow * 15.0;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
