}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse = mouse * 2.0 - 1.0;
    mouse.x *= iResolution.x / iResolution.y;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv *= 2.0;
    uv -= vec2(1.0);
    uv.x *= iResolution.x / iResolution.y;
    uv *= 1.4;

    // Define color sets
    vec3 fillColors[3] = vec3[3](
        vec3(0.886, 0.969, 0.925), // #E2F7EC
        vec3(0.761, 0.902, 0.965), // #C2E6F6
        vec3(0.635, 0.855, 0.416)  // #A2DA6A
    );
    vec3 shadowColors[3] = vec3[3](
        vec3(0.635, 0.855, 0.416), // #A2DA6A
        vec3(0.184, 0.463, 1.000), // #2f76ff (alpha ignored)
        vec3(0.129, 0.769, 0.424)  // #21C46C
    );

    // Calculate color cycle
    float cycleTime = mod(iTime, 9.0); // 9 seconds for a full cycle (3 colors * 5 seconds each)
    int colorIndex = int(cycleTime / 3.0);
    float colorTransition = fract(cycleTime / 3.0);

    // Initial desired shape dimensions in pixels
    float initialWidth = 860.0;
    float initialHeight = 460.0;

    // Maximum number of shapes
    int maxShapes = 8; // Adjust this number to add more or fewer shapes

    // Pulsing effect
    float pulseSpeed = 2.0;
    float pulse = 0.5 + 0.5 * sin(iTime * pulseSpeed);

    for (int i = 0; i < maxShapes; i++) {         
        // Calculate parallax factor (1.0 for innermost shape, 0.0 for outermost)
        float parallaxFactor = 1.0 - float(i) / float(maxShapes - 1);

        // Calculate center with parallax effect
        vec2 center = mouse * parallaxFactor;

        // Calculate scaling factors for the current shape
        float scaleX = initialWidth / iResolution.x;
        float scaleY = initialHeight / iResolution.y;

        // Apply pulsing effect to shape size
        float pulseFactor = 1.0 + 0.1 * pulse * (1.0 - parallaxFactor);
        scaleX *= pulseFactor;
        scaleY *= pulseFactor;

        // Scale UV coordinates
        vec2 scaledUV = uv - center;
        scaledUV.x /= scaleX;
        scaledUV.y /= scaleY;

        // Check if the point is within the current shape
        if (inPath(scaledUV)) {
            // Calculate distance from the edge for shadow effect
            float distFromEdge = 1.2 - length(scaledUV);
            float shadowStrength = smoothstep(0.0, 1.0, distFromEdge);

            // Calculate color transition for this shape
            float shapeTransition = clamp(colorTransition * float(maxShapes) - float(i), 0.0, 1.0);
            int nextColorIndex = (colorIndex + 1) % 3;

            vec3 currentFillColor = mix(fillColors[colorIndex], fillColors[nextColorIndex], shapeTransition);
            vec3 currentShadowColor = mix(shadowColors[colorIndex], shadowColors[nextColorIndex], shapeTransition);

            // Apply pulsing effect to colors
            currentFillColor = mix(currentFillColor, vec3(1.0), 0.2 * pulse);
            currentShadowColor = mix(currentShadowColor, vec3(0.0), 0.2 * pulse);

            // Mix fill color with shadow color
            vec3 finalColor = mix(currentShadowColor, currentFillColor, shadowStrength);

            // Apply overall pulsing effect
            finalColor *= 0.8 + 0.2 * pulse;

            fragColor = vec4(finalColor, 1.0);
            return;
        }

        // Scale up for the next shape
        initialWidth *= 1.4;
        initialHeight *= 1.4;
    }

    // Background color
    fragColor = vec4(vec3(0.0), 1.0);
}

#define pi 3.14159

float gamma(float brightness) {
    float result = 1.055 * pow(brightness, 5.0/12.0) - 0.055;
    if (brightness < 0.0031308) {
        result = 12.92 * brightness;
    }
    if (brightness < 0.0) {
        result = 0.0;
    }
    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Sphere settings
    float sphereRadius = 1.5;
    vec3 sphereCenter = vec3(0.0, 0.0, 3.0);
    float roughness = 2.0*iMouse.x/iResolution.x;

    //Lighting settings
    //vec3 lightDirection = normalize(vec3(cos(iTime), 0.0, sin(iTime)));
    vec3 lightDirection = vec3(1.0, 0.0, 0.0);

    //Calculate direction vector
    float m = iResolution.y;
    vec2 uv = vec2(fragCoord.x-.5*(iResolution.x - m), fragCoord.y)/m;
    vec2 xy = 2.0 * uv - 1.0;

    vec3 D = normalize(vec3(xy, 1.0));

    //Sphere intersection
    float tca = dot(D, sphereCenter);
    float d = sqrt(dot(sphereCenter, sphereCenter) - tca * tca);
    float thc = sqrt(sphereRadius * sphereRadius - d * d);
    float dist = tca - thc;

    //Normal vector
    vec3 normal = normalize(D * dist - sphereCenter);

    //Calculate angles
    float roughSquare = roughness*roughness;

    vec3 tangentLight = normalize(cross(normal, cross(lightDirection, normal)));
    vec3 tangentView = normalize(cross(normal, cross(-D, normal)));
    float cosDiff = dot(tangentLight, tangentView);

    float cosThetaLight = dot(normal, lightDirection);
    float cosThetaView = dot(normal, -D);

    float thetaLight = acos(abs(cosThetaLight));
    float thetaView = acos(abs(cosThetaView));

    float alpha = max(thetaLight, thetaView);
    float beta = min(thetaLight, thetaView);

    float c1 = 1.0 - 0.5 * roughness*roughness/(roughness*roughness + 0.33);
    float c2Mult = sin(alpha);
    if (cosDiff < 0.0) {
        c2Mult -= pow(2.0*beta/pi,3.0);
    }
    float c2 = 0.45 * roughSquare/(roughSquare + 0.09) * c2Mult;
    float c3 = 0.125 * roughSquare * pow(4.0*thetaLight*thetaView/(pi*pi),2.0)/(roughSquare + 0.09);

    float l1 = 1.0 * cosThetaLight * (c1 + c2*cosDiff*tan(beta) + c3*(1.0-abs(cosDiff))*tan(0.5*(alpha+beta)));
    float l2 = 0.17 * cosThetaLight * roughSquare * (1.0 - cosDiff*pow(2.0*beta/pi,2.0))/(roughSquare+0.13);

    //Lighting calculations
    //float lighting = dot(normal, lightDirection); //Lambertian diffuse
    float lighting = l1 + l2; //Oren-Nayar reflection model
    //float lighting = cosThetaLight/(cosThetaLight + cosThetaView); //Lommel-Seeliger law? idk

    if (dot(normal, lightDirection) < 0.0) {
        lighting = 0.0;
    }

    //Aesthetics
    lighting = mix(lighting, 1.0, 0.003);

    // Output to screen
    fragColor = vec4(vec3(gamma(lighting)), 1.0);
    if (tca < 0.0) {
        fragColor = vec4(0.0);
    }
    if (d > sphereRadius) {
        fragColor = vec4(0.0);
    }
}

vec3 palette( float t ) {
    vec3 a = vec3(0.5, 0.5, 0.5);
    vec3 b = vec3(0.5, 0.5, 0.5);
    vec3 c = vec3(1.0, 1.0, 1.0);
    vec3 d = vec3(0.263,0.416,0.557);

    return a + b*cos( 6.28318*(c*t+d) );
}



