void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;


	uv.y*=iResolution.y/iResolution.x;
    vec3 dir=vec3(uv*zoom,1.);
    vec3 from=vec3(1.,.5,0.5);
    float s=0.1,fade=1.;
	vec3 v=vec3(0.);
	for (int r=0; r<volsteps; r++) {
		vec3 p=from+s*dir*.5;
		p = abs(vec3(tile)-mod(p,vec3(tile*2.))); // tiling fold
		float pa,a=pa=0.;
		for (int i=0; i<iterations; i++) { 
			p=abs(p)/dot(p,p)-formuparam;
            p.xy*= rotate(0.1 * time);// the magic formula
			a+=abs(length(p)-pa); // absolute sum of average change
			pa=length(p);
		}
		float dm=max(0.,darkmatter-a*a*.001); //dark matter
		a*=a*a; // add contrast
		if (r>6) fade*=1.2-dm; // dark matter, don't render near
		//v+=vec3(dm,dm*.5,0.);
		v+=fade;
		v+=vec3(s,s*s,s*s*s*s)*a*brightness*fade; // coloring based on distance
		fade*=distfading; // distance fading
		s+=stepsize;
	}
	v=mix(vec3(length(v)),v,saturation); //color adjust
    	vec4 fragColor2 = vec4(v*.03,1.);	
    vec3 finalColor = vec3(0.0);
    vec2 p = uv;

	float d = 9999.0;
	float size = 0.1;

	for (int i = 0; i < 17; i++) { 
		p *= rotate(0.1 * time);
		p = foldX(p);
		p = foldY(p);
		p = translate(p, vec2(0.2, 0.2));
		d = min(d, box(p * rotate(3.1 * time * float(i)), vec2(size)));
		size *= 0.75;
	}




	vec3 c = vec3(1.0) * smoothstep(0.0, 0.01, abs(d));

    for (float i = 0.0; i < 4.0; i++) {
      uv*= rotate(0.5 * time);
      uv*= translate(p, uv*c.xy);
        uv = fract(uv+p * 1.5) - 0.5;

        float d = length(uv) * exp(-length(uv0));

        vec3 col = palette(length(uv0) + i*.4 + iTime*.4);

        d = sin(d*8. + iTime)/8.;
        d = abs(d);

        d = pow(0.01 / d, 1.2);

        finalColor += col * d*c*fragColor2.xyz;
    }

    fragColor = vec4(finalColor, 1.0);
}

