    // Position vector
    vec3 o;

    // Current time
    float time = iTime;
vec4  finalImage = O;
vec2 texCoords = C;
    // Spiral pattern control variables
    float spiralFactor = 0.1;
    float scale = 3.0;
    float brightness = 2.0;
    float maxExponent = 25.0;
    float angleOffset = 0.314;
    float modulationFactor = 0.1;
    float fractOffset = 0.5;
