#define R iResolution
#define M iMouse
#define T iTime

#define PI  3.141592653
#define PI2 6.283185307

#define MIN_DIST 1e-3
#define MAX_DIST 40.

mat2 rot(float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float lsp(float b, float e, float t) { return clamp((t - b) / (e - b), 0., 1.); }
float eoc(float t) { return t = --t * t * t + 1.; }

float box(vec3 p, vec3 b){
    vec3 d = abs(p)-b;
    return length(max(d,0.))+min(max(d.x,max(d.y,d.z)),.0);
}

//globals
vec3 s_hit, g_hit;
vec2 s_id, g_id;
mat2 mx, my;

//scales for all things
const float scale = 1./.5;
const float scale_h = scale*.5;
const vec2 s = vec2(scale)*2.;

//positions and rotations
const vec2 pos = vec2(.5,-.5);
const vec2[4] ps4 = vec2[4](pos.yx,pos.xx,pos.xy,pos.yy);

// block map -v4 tap technique from @Shane
vec2 map(vec3 q3){

    vec2 res = vec2(1e5,0.);
    vec2 p,ip,id = vec2(0),ct = vec2(0);

    float t=1e5, y=1e5, m=1.;

    for(int i =0; i<4; i++){

        ct = ps4[i]/2. -  ps4[0]/2.;
        p = q3.xz - ct*s;
        ip = floor(p/s) + .5;
        p -= (ip)*s;
        vec2 idi = (ip + ct)*s;

        float rnd = mod(idi.x,10.); 
              rnd+=(mod(idi.y,10.)*10.)*.15;
        float tmod = mod(T, 24.); 

        //movement
        float t3 = lsp(rnd, rnd+1., tmod);
        t3 = eoc(t3); t3 = t3*t3*t3;
        float t4 = lsp(rnd+1., rnd+2., tmod);
        t4 = eoc(t4); t4 = t4*t4*t4;
        float t5 = lsp(rnd+2., rnd+3., tmod);
        t5 = eoc(t5); t5 = t5*t5*t5;
        float t6 = lsp(rnd+3., rnd+4., tmod);
        t6 = eoc(t6); t6 = t6*t6*t6;

        float xx = scale*(t3-t5);  
        float yy = scale*(t4-t6); 

        vec3 q = vec3(p.x,q3.y-yy-xx,p.y);
        mat2 rmx=rot((t3-t5)*PI);
        mat2 rmy=rot((t4-t6)*PI);

        q.xy*=rmx;
        q.zy*=rmy;

        float b = box(q,vec3(scale*.385))-.065;
        if(b<t ) {
            t = b;
            m = mod(float(i),2.)<1.? 5.:4.;
            s_id = idi;
            s_hit = q+(idi.xyx*vec3(.25)+float(i));
            mx=rmx;
            my=rmy;
        }
    }

	if(t<res.x) res = vec2(t,m);

    float f = q3.y+1.5;
    if (f<res.x) {
        res = vec2(f,2.);
        s_hit = q3;
    }

    return res;
}

vec3 normal(vec3 p, float t)
{
    float e = MIN_DIST*t;
    vec2 h =vec2(1,-1)*.5773;
    vec3 n = h.xyy * map(p+h.xyy*e).x+
             h.yyx * map(p+h.yyx*e).x+
             h.yxy * map(p+h.yxy*e).x+
             h.xxx * map(p+h.xxx*e).x;
    return normalize(n);
}

vec4 FC = vec4(.17,.14,.11,0.);

vec4 render(inout vec3 ro, inout vec3 rd, inout vec3 ref, bool last, inout float d) {

    vec3 C = vec3(0);
    float m = 0.;
    vec3 p = ro;
    // marcher
    for(int i=0;i<132;i++) {
        p = ro + rd * d;
        vec2 ray = map(p);
        if(ray.x<MIN_DIST*d||d>MAX_DIST)break;
        d += i<32? ray.x*.25: ray.x*.75;
        m  = ray.y;
    } 
    g_hit = s_hit;
    g_id = s_id;

    float alpha = 0.;
    if(d<MAX_DIST)
    {
        vec3 n = normal(p,d);
        vec3 lpos =  vec3(-5,8,0);
        vec3 l = normalize(lpos);

        float diff = clamp(dot(n,l),0.,1.);

        int face;
        vec3 cuv = s_hit;

        if(m>2.) {
            // quick find the correct face with normal
            vec3 tn = n;
            tn.xy*=mx;
            tn.zy*=my;
            vec3 aN = abs(tn);
            ivec3 idF = ivec3(tn.x<-.25? 0 : 5, tn.y<-.25? 1 : 4, tn.z<-.25? 2 : 3);
            face = aN.x>.5? idF.x : aN.y>.5? idF.y : idF.z;

            if(face==0||face==5) cuv = s_hit.yzx;
            if(face==1||face==4) cuv = s_hit.xzy+vec3(.5);
            if(face==2||face==3) cuv = s_hit.xyz-vec3(.5);
        }

        vec3 h = vec3(.15);

        if( m==5.) {
            h = texture(iChannel0,cuv.xy).rgb*vec3(.9,.4,.1);
            ref = h;
        }
        if( m==4.) {
            h = texture(iChannel1,cuv.xy*.25).rgb;
            ref = h;
        }

        if( m==2.) {
            vec2 uv = fract(g_hit.xz*3.)-.5;
            if(uv.x*uv.y>0.) h = vec3(.4);
            ref=h;
        }

        float shdw = 1.;
        for( float t=.01; t < 10.; ) {
            float h = map(p + l*t).x;
            if( h<MIN_DIST ) { shdw = 0.; break; }
            shdw = min(shdw, 18.*h/t);
            t += h;
            if( shdw<MIN_DIST || t>18. ) break;
        }
        diff = mix(diff,diff*shdw,.65);

        vec3 ret = reflect(normalize(lpos), n);
        float spec = .5 * pow(max(dot(normalize(p-ro), ret), 0.), 14.);

        C = h*diff+min(spec,shdw);
        if(last) C = mix(FC.rgb,C,  exp(-.0025*d*d*d));

        ro = p+n*1e-3;
        rd = reflect(rd,n);

    }else{
        C = FC.rgb;
    } 
    return vec4(C,alpha);
}

void mainImage( out vec4 O, in vec2 F )
{

    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);

    //ray origin - direction
    vec3 ro = vec3(0,0,12);
    vec3 rd = normalize(vec3(uv, -1));

    // mouse //
    float x = M.xy==vec2(0)||M.z<0. ? .1+.08*sin(T*.35) : -(M.y/R.y*.25-.125)*PI;
    float y = M.xy==vec2(0)||M.z<0. ? .1*cos(T*.15) : -(M.x/R.x*2.-1.)*PI;

    mat2 rx =rot(-.92+x);
    mat2 ry =rot(.78+y);

    ro.zy*=rx;rd.zy*=rx;
    ro.xz*=ry;rd.xz*=ry;

    ro.z+=T;

    vec3 C = vec3(0),ref=vec3(0),fil=vec3(1);

    float d = 0.,a = 0.;

    float numBounces = 2.;//3 ok on 4090 - slower on mac m3

    for(float i=0.; i<numBounces; i++) {
        d =0.;
        vec4 pass = render(ro, rd, ref, i==numBounces-1., d);
        C += pass.rgb*fil;
        fil*=ref;
        if(i==0.) a = d;
    }

    C = mix(FC.rgb,C,  exp(-.0002*a*a*a));
    O = vec4(pow(C, vec3(.4545)),1);
}

const float pi = 3.14159;

// makes a triangle function which basicly just goes from -1 to 1 and then back to -1 forever in a loop.
float TriangleWaveFunction(float x)
    {
        return (acos(cos(x*pi))/pi -0.5)*2.0;
    }

// a sine wave modified to have the same peaks as the triangle wave.
float SineWave(float x)
    {
        return sin(x * pi + (pi/2.0)*3.0);
    }

float dist(vec2 Ball, vec2 PixelCord) 
    {
        return sqrt(pow(Ball.x - PixelCord.x, 2.0) + pow(Ball.y - PixelCord.y, 2.0));
    }

// this sdf was made by iq, not me.
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    //pixel coordinates
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv*= 2.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 BallPos = vec2(TriangleWaveFunction(iTime)*1.4, TriangleWaveFunction(iTime)*0.6*-1.0);
    float BallFace = step(dist(BallPos, uv),0.1);

    float RacketOffsetX = 1.6;
    vec2 RacketPosY = vec2(SineWave(iTime), 0.5);
    float RacketWidth = 0.005;
    float RacketHeight = 0.25;

    float RacketFaceLeft = sdBox(vec2(uv.x+RacketOffsetX, uv.y +SineWave(iTime)*0.6), vec2(RacketWidth, RacketHeight));
    float RacketFaceRight = sdBox(vec2(uv.x-RacketOffsetX, uv.y +SineWave(iTime*2.0)*0.6*-1.0), vec2(RacketWidth, RacketHeight));

    float RacketFace = max(step(RacketFaceLeft, 0.1),step(RacketFaceRight, 0.1));
    // Time varying pixel color
    vec3 ColVaraity = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    vec3 col = vec3(step(0.0, uv.x)*(uv.x+0.2), 0.0, step(uv.x, 0.0)*(abs(uv.x)+0.2));

    col += ColVaraity*0.5;
    float GlobalFaces = max(BallFace, RacketFace);

    col *= GlobalFaces;

    // Output to screen
    fragColor = vec4(col,1.0);
}

