const float T = 6.28318531;

float ts = 0.0;

float hash11(float p) {
    uint u = floatBitsToUint(p * 1231.1031) * 2597334677u;
    return float(u * u * 2597334677u) / float(~0u);
}

float noise(float p) {
	float i = floor(p);
	float f = fract(p);
    f *= f * (3.0 - 2.0 * f);
	return mix(hash11(i), hash11(i + 1.0), f);
}

float A(float t) {
    float F = 90.0;
    float F2 = F / 3.0;
    float M = 1.0 / (1.0 + ts * ts);

    float vb = 0.0;
    float tFT = t * F * T - (M - 1.0) * 15.0;
    float tF2T = t * F2 * T - (M - 1.0) * 45.0;
    float tF2 = t * F2 + (M - 1.0) * 45.0;
    vb += cos(tFT * 1.0) * cos(tF2T * 1.0 + noise(tF2 - 321.85652) * 3.0 * M) * 0.1;
    vb += cos(tFT * 3.0) * cos(tF2T * 3.0 + noise(tF2 - 311.54742) * 3.0 * M) * 0.5;
    vb -= cos(tFT * 5.0) * cos(tF2T * 5.0 + noise(tF2 - 222.84654) * 3.0 * M) * 0.5;
    vb += cos(tFT * 6.0) * cos(tF2T * 6.0 + noise(tF2 - 111.63453) * 3.0 * M);
    vb -= cos(tFT * 8.0) * cos(tF2T * 8.0 + noise(tF2 - 845.86532) * 3.0 * M);
    vb -= cos(tFT * 9.0) * cos(tF2T * 9.0 + noise(tF2 - 655.11123) * 3.0 * M) * 0.3;
    vb += cos(tFT * 12.0) * cos(tF2T * 12.0 + noise(tF2 - 531.63329) * 3.0 * M) * 0.5;

    return vb;
}

vec2 mainSound(int samp, float t) {
    ts = fract(abs(0.5 - fract(t * 2.0)) - abs(0.5 - fract(t * 0.5)) * 3.0);
    if (t < 4.0) ts = 0.0;
    float s = A(t - 0.0251262) - A(t + 0.0365754) + A(t - 0.07462323) - A(t + 0.0726861);
    if (t < 4.0) ts = 0.99;
    s *= ts * smoothstep(1.0, 0.99, ts);
    return vec2(s) * 0.1;
}

