#define MAX_STEPS 30
#define MAX_DIST 40.
#define SURF_DIST .001

float sdSphere(vec3 p, float s) {
    return length(p) - s;
}

vec3 rotateY(vec3 p, float a) {
    float c = cos(a), s = sin(a);
    return vec3(p.x * c - p.z * s, p.y, p.x * s + p.z * c);
}

float getDistance(vec3 p) {
    float d = MAX_DIST;
    vec3 z = p;
    float scale = 0.8;

    for(int i=0; i<10; i++) {
        z = rotateY(z, iTime * 0.1 + iMouse.x * 0.001); // Aadir interaccin con el ratn
        z = abs(z) - vec3(5.0, 0.5, 1.0) * scale;

        if((z.x) < z.y) z.xy = z.yx;
        if(z.x < z.z) z.xzy = z.xzy;
        if(z.y < z.z) z.yxz = z.xzy;

        z = z * 2.80 - vec3(5.0, 3.0, -0.5) * scale;
        scale *= 1.0200; // Aumentar el factor de escala para incrementar la deformacin

        float sphereDist = sdSphere(z, 1.50 * scale);
        d = min(d, sphereDist / scale);
    }

    return d * 0.5;
}

vec3 getNormal(vec3 p) {
    float d = getDistance(p);
    vec2 e = vec2(.00001, 0);
    vec3 n = d - vec3(
        getDistance(p-e.xyy),
        getDistance(p-e.yxy),
        getDistance(p-e.yyx));
    return normalize(n);
}

float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.5;
    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd * dO;
        float dS = getDistance(p);
        dO += dS;
        if(dO > MAX_DIST || dS < SURF_DIST) break;
    }
    return dO;
}

vec3 getColor(vec3 p) {
    vec3 col = vec3(0.5) + 0.5 * sin(iTime * 0.1 + p.xyx * 0.2 + vec3(0,2,4));
    return mix(vec3(30.8, 10.5, 4.22), vec3(0.2, 0.5, 0.08), col) * .3333333; // Hacer colores ms translcidos
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.09*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0);

    vec3 ro = vec3(1, 0, -4);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    ro = rotateY(ro, iTime * 0.1 + iMouse.x * 0.001); // Aadir interaccin con el ratn
    rd = rotateY(rd, iTime * 0.1 + iMouse.x * 0.001); // Aadir interaccin con el ratn

    float d = rayMarch(ro, rd);

    if(d < MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = getNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = getColor(p);

        col *= dif;
        col += pow(max(dot(r, normalize(vec3(1,2,3))), 0.70), 8.0); // specular
        col *= refract(rd, n, 5.0 / 4.533); // Aadir refraccin para simular la ptica de las burbujas de jabn
    }

    col = pow(col, vec3(.4545));  // gamma correction

    fragColor = vec4(col,0.0000001); // Ajustar la opacidad a 0.1 para hacer los objetos casi transparentes
}

#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)cos((h)*6.3+vec3(0,23,21))*.5+.5



#define PI acos(-1.)
#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(.05,fwidth(val-thre)))

#define TRYPOPHOBIA 0

