float sdCylinder( vec3 p, float r, float h ) {
	vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(r, h);
	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0));
}

float sdCube(vec3 p, float b) {
	vec3 d = abs(p) - b;
	return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

float sdSphere(vec3 p, float r) {
    return length(p) - r;
}

float sdTet(vec3 p, float r) {
    const vec3 c = normalize(vec3(1.,-1.,1.));
    return max(max(max(dot(p, c.xxx) - r, dot(p, c.yyx) - r), dot(p, c.xyy) - r), dot(p, c.yxy) - r);
}

vec3 opRepeat(vec3 p, vec3 spacing) {
    return mod(p, spacing) - 0.5 * spacing;
}

float opSub(float d1, float d2) {
    return max(d1, -d2);
}

float opAdd(float d1, float d2) {
    return min(d1, d2);
}

float opInt(float d1, float d2) {
    return max(d1, d2);
}

vec3 rX(vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    q.y = c * p.y - s * p.z;
    q.z = s * p.y + c * p.z;

    return q;
}

vec3 rY(vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    q.x = c * p.x + s * p.z;
    q.z = -s * p.x + c * p.z;

    return q;
}

vec3 rZ(vec3 p, float a) {
    vec3 q = p;
    float c = cos(a);
    float s = sin(a);
    q.x = c * p.x - s * p.y;
    q.y = s * p.x + c * p.y;

    return q;
}

// assumes normalized axis
mat3 makeRotation(vec3 axis,float angle) {

    float c = cos(angle), s = sin(angle);
    float mc = 1. - c;
    float sz = s * axis.z;
    float sy = s * axis.y;
    float sx = s * axis.x;
    float mx = mc * axis.x;
    float my = mc * axis.y;
    float mz = mc * axis.z;

    return mat3(c + mx * axis.x,
                mx * axis.y - sz,
                mx * axis.z + sy,
                mx * axis.y + sz,
                c + my * axis.y,
                my * axis.z - sx,
                mx * axis.z - sy,
                my * axis.z + sx,
                c + mz * axis.z);
}

